INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12459, 12514, 12523, 12511, 5, "", 6072, "「カミツレ」とも呼ばれるハーブの一種。りんごのような香りがあり、お茶としてよく好まれる", 3, 5, 2, 5, "これ、何と言う？", 6, "カモミール", 5, 12540, 5, "カモミール");
