INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12469, 12531, 12454, 12471, 5, "", 6080, "両生類の一種。防衛のために出す粘液の匂いが、和食に使うとあるスパイスに似ているのが由来", 3, 5, 2, 5, "これ、何と言う？", 6, "サンショウ", 5, 12519, 5, "サンショウウオ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12501, 12461, 12454, 12494, 5, "", 6081, "「蕗（フキ）」が大きく成長する前の、つぼみの部分。春の山菜として、天ぷらなどで好まれる", 3, 5, 2, 5, "これ、何と言う？", 6, "フキノトウ", 5, 12488, 5, "フキノトウ");
