INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12489, 12521, 12464, 12511, 5, "", 6085, "ゴリラが手で胸をドコドコと叩いて音をたてる行動", 2, 5, 2, 5, "これ、何と言う？", 6, "ドラミング", 5, 12531, 5, "ドラミング");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12495, 12512, 12522, 12473, 5, "", 6086, "太ももの裏にある、大きな筋肉の集まり。筋トレでも重要視されることが多い", 3, 5, 2, 5, "これ、何と言う？", 6, "ハムストリ", 5, 12488, 5, "ハムストリング");
