INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12487, 12517, 12531, 12521, 5, "", 6176, "伝承に登場する、首の無い騎士", 2, 5, 2, 5, "これ、何と言う？", 6, "デュラハン", 5, 12495, 5, "デュラハン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12458, 12483, 12452, 12489, 5, "", 6177, "両目でそれぞれ瞳の色が異なること。いろいろな動物に見られるが、特に猫に多い", 2, 5, 2, 5, "これ、何と言う？", 6, "オッドアイ", 5, 12450, 5, "オッドアイ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 29483, 12376, 12375, 12419, 4, "", 6178, "「エノコログサ」という草の通称。猫の目の前で振ると興味を示し、よくじゃれつくことから", 1, 4, 2, 4, "これ、何と言う？", 6, "猫じゃらし", 6, 12425, 4, "猫じゃらし");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12489, 12521, 12521, 12461, 5, "", 6179, "非常によく知られた怪物。獲物の血を吸うのが特徴で、十字架やニンニクが弱点とされる", 1, 5, 2, 5, "これ、何と言う？", 6, "ドラキュラ", 5, 12517, 5, "ドラキュラ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12531, 12467, 12471, 12525, 5, "", 6180, "動物のフンをボール状にして、コロコロ転がすという変わった習性を持つ昆虫", 1, 5, 2, 5, "これ、何と言う？", 6, "ンコロガシ", 5, 12460, 5, "フンコロガシ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12521, 12464, 12523, 12489, 5, "", 6181, "「ぬいぐるみ」という意味を持つ、猫の種類。抱っこが好きで、毛並みがフサフサなことから", 3, 5, 2, 5, "これ、何と言う？", 6, "ラグドール", 5, 12540, 5, "ラグドール");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12456, 12461, 12463, 12478, 5, "エキゾチック・ショートヘア", 6182, "猫の種類の一つ。鼻が低く、ムスッとしたような愛嬌のある顔立ちが特徴的", 3, 5, 2, 5, "これ、何と言う？", 6, "エキゾチク", 5, 12481, 5, "エキゾチック・ショートヘア");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12435, 12375, 12392, 12423, 4, "", 6183, "ハトの帰巣本能を使って遠くに手紙を届けること。通信技術が発達する前は、広く活用された", 2, 4, 2, 4, "これ、何と言う？", 6, "んしょばと", 4, 12400, 4, "でんしょばと");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12507, 12507, 12519, 12465, 5, "ホーホケキョ", 6184, "「ウグイス」の鳴き声を文字に起こした表現。春の訪れの象徴としてもなじみ深い", 2, 5, 2, 5, "これ、何と言う？", 6, "ホホケキョ", 5, 12461, 5, "ホーホケキョ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12507, 12488, 12473, 12488, 5, "", 6185, "武将の性格を比喩した「鳴かぬなら～～」の俳句に使われている鳥の名前は？", 1, 5, 2, 5, "これ、何と言う？", 6, "ホトトギス", 5, 12462, 5, "ホトトギス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12504, 12499, 12468, 12452, 5, "", 6186, "「ヘビがいそうな場所に生える」ことに由来する、イチゴに似た植物。食用には向かない", 2, 5, 2, 5, "これ、何と言う？", 6, "ヘビイチゴ", 5, 12481, 5, "ヘビイチゴ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 26494, 12412, 12426, 12387, 4, "", 6187, "松かさとも呼ばれる、概略すると松の木の果実の部分。クリスマスツリーの飾りにもよく使われる", 2, 4, 2, 4, "これ、何と言う？", 6, "松ぼっくり", 6, 12367, 4, "松ぼっくり");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6188, 12450, 12478, 12489, 12540, 5, "", 6188, "", 1, 5, 1, 5, "このキャラの名前は？", 6, "アゾールド", 5, 12523, 5, "アゾールド");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6189, 12474, 12490, 12416, 12385, 4, "", 6189, "", 3, 4, 1, 5, "左のキャラは右のキャラを何と呼ぶ？", 6, "ズナちょむ", 5, 12423, 4, "ズナちょむ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12501, 12457, 12489, 12540, 5, "", 6190, "猫の種類の一つ。垂れた耳が特徴的", 2, 5, 2, 5, "これ、何と言う？", 6, "フォールド", 5, 12523, 5, "スコティッシュフォールド");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12468, 12540, 12531, 12523, 5, "", 6191, "非常にポピュラーな種の犬で、フサフサとした黄色系の体毛と、大きな体が特徴", 1, 5, 2, 5, "これ、何と言う？", 6, "ゴールデン", 5, 12487, 5, "ゴールデンレトリーバー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12489, 12483, 12531, 12464, 5, "", 6192, "家庭犬がリードをつけずに自由に走り回れるように設けられた、フェンスで囲われた広いスペース", 2, 5, 2, 5, "これ、何と言う？", 6, "ドッグラン", 5, 12521, 5, "ドッグラン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12521, 12502, 12523, 12521, 5, "ラブラドールレトリーバー", 6193, "レトリーバー種としてゴールデンレトリーバーに並び有名な犬。盲導犬・警察犬としても活躍する", 1, 5, 2, 5, "これ、何と言う？", 6, "ラブラドル", 5, 12489, 5, "ラブラドールレトリーバー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12510, 12540, 12464, 12461, 5, "", 6194, "動物が自分の体臭や尿などを残すことで、そこが自分のなわばりであるとアピールする習性", 1, 5, 2, 5, "これ、何と言う？", 6, "マーキング", 5, 12531, 5, "マーキング");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12383, 12356, 12385, 12371, 6, "", 6195, "しきりに偉い人のご機嫌をとるような人間を、ある楽器の名前を使ってなんと呼ぶ？", 2, 4, 2, 4, "これ、何と言う？", 6, "たいこ持ち", 4, 25345, 4, "たいこ持ち");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12461, 12522, 12473, 12462, 5, "", 6196, "有名な童話の中で、「アリ」と対比して、なまけ者として描かれている昆虫", 1, 5, 2, 5, "これ、何と言う？", 6, "キリギリス", 5, 12522, 5, "キリギリス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12463, 12523, 12499, 12510, 5, "", 6197, "エビの一種。身体にクッキリとシマシマがあり、丸まった姿が車のホイールに見える", 1, 5, 2, 5, "これ、何と言う？", 6, "クルマエビ", 5, 12456, 5, "クルマエビ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12480, 12452, 12454, 12471, 5, "", 6198, "ヘビの一種で、身体は青緑色。由来は諸説あるが「ヘビの中でも特に大物の風格がある」ことなど", 3, 5, 2, 5, "これ、何と言う？", 6, "ダイショウ", 5, 12519, 5, "アオダイショウ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12375, 12381, 12358, 12385, 4, "", 6199, "太古の時代に存在した鳥で、その名の通り、鳥類の始まりとなった鳥とされている", 2, 4, 2, 4, "これ、何と言う？", 6, "しそちょう", 4, 12423, 4, "しそちょう");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12454, 12521, 12473, 5, "", 6200, "「最古の人類」とされている種類の猿人", 2, 5, 2, 5, "これ、何と言う？", 6, "アウストラ", 5, 12488, 5, "アウストラロピテクス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12461, 12517, 12531, 12454, 5, "", 6201, "ペットとしても親しまれる手乗りサイズの鳥で、人の声をマネして鳴くのが上手い", 1, 5, 2, 5, "これ、何と言う？", 6, "キュウカン", 5, 12459, 5, "キュウカンチョウ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12458, 12483, 12452, 12488, 5, "", 6202, "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", 2, 5, 2, 5, "これ、何と言う？", 6, "オットセイ", 5, 12475, 5, "オットセイ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12472, 12454, 12450, 5, "", 6203, "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", 1, 5, 2, 5, "これ、何と言う？", 6, "アジアゾウ", 5, 12478, 5, "アジアゾウ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12505, 12491, 12511, 12471, 5, "", 6204, "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", 3, 5, 2, 5, "これ、何と言う？", 6, "ベニシジミ", 5, 12472, 5, "ベニシジミ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12472, 12517, 12531, 12454, 5, "", 6205, "テントウムシの一種。身体に13個の点々模様がある", 2, 5, 2, 5, "これ、何と言う？", 6, "ジュウサン", 5, 12469, 5, "ジュウサンホシテントウ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12491, 12472, 12516, 12517, 5, "", 6206, "テントウムシの一種。身体に28個の点々模様がある", 3, 5, 2, 5, "これ、何と言う？", 6, "ニジュウヤ", 5, 12454, 5, "ニジュウヤホシテントウ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12491, 12473, 12469, 5, "", 6207, "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", 2, 5, 2, 5, "これ、何と言う？", 6, "アニサキス", 5, 12461, 5, "アニサキス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12431, 12383, 12426, 12426, 4, "", 6208, "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", 1, 4, 2, 4, "これ、何と言う？", 6, "わたりどり", 4, 12393, 4, "わたりどり");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12501, 12531, 12488, 12508, 5, "", 6209, "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", 3, 5, 2, 5, "これ、何と言う？", 6, "フンボルト", 5, 12523, 5, "フンボルトペンギン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12456, 12508, 12522, 12471, 5, "", 6210, "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", 3, 5, 2, 5, "これ、何と言う？", 6, "エボシドリ", 5, 12489, 5, "エボシドリ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12493, 12470, 12489, 12521, 5, "ネザーランドドワーフ", 6211, "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", 3, 5, 2, 5, "これ、何と言う？", 6, "ネザランド", 5, 12531, 5, "ネザーランドドワーフ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12507, 12540, 12489, 12521, 5, "", 6212, "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", 3, 5, 2, 5, "これ、何と言う？", 6, "ホーランド", 5, 12531, 5, "ホーランドロップ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12501, 12455, 12488, 12524, 5, "", 6213, "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", 2, 5, 2, 5, "これ、何と言う？", 6, "フェレット", 5, 12483, 5, "フェレット");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12510, 12531, 12498, 12488, 5, "", 6214, "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", 2, 5, 2, 5, "これ、何と言う？", 6, "マントヒヒ", 5, 12498, 5, "マントヒヒ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12471, 12525, 12473, 12490, 5, "", 6215, "最も大きい動物としてよく知られる種類のクジラ", 2, 5, 2, 5, "これ、何と言う？", 6, "シロナガス", 5, 12460, 5, "シロナガスクジラ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12471, 12525, 12459, 12452, 5, "", 6216, "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", 3, 5, 2, 5, "これ、何と言う？", 6, "シロイルカ", 5, 12523, 5, "シロイルカ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12375, 12429, 12366, 12358, 4, "", 6217, "神話に登場する、有名な動物", 2, 4, 2, 4, "これ、何と言う？", 6, "しろうさぎ", 4, 12373, 4, "いなばのしろうさぎ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12473, 12467, 12531, 12500, 5, "さそり＝スコーピオン", 6218, "カタカナでの呼び方は？", 1, 5, 2, 5, "これ、何と言う？", 6, "スコピオン", 5, 12458, 5, "さそり＝スコーピオン");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12385, 12363, 12406, 12425, 4, "", 6219, "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", 2, 4, 2, 4, "これ、何と言う？", 6, "ちからこぶ", 4, 12371, 4, "ちからこぶ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12373, 12427, 21512, 12363, 4, "", 6220, "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", 2, 4, 2, 4, "これ、何と言う？", 6, "さるかに合", 4, 12395, 6, "さるかに合戦");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12481, 12531, 12472, 12497, 5, "", 6221, "よく親しまれた替え歌の歌詞。ある動物の名前が入る", 1, 5, 2, 5, "これ、何と言う？", 6, "チンパンジ", 5, 12531, 5, "♪サル、ゴリラ、チンパンジー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12458, 12513, 12452, 5, "", 6222, "釣り人には釣り餌として非常におなじみの、ミミズのような虫", 3, 5, 2, 5, "これ、何と言う？", 6, "アオイソメ", 5, 12477, 5, "アオイソメ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 37628, 12513, 12523, 12531, 5, "", 6223, "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", 3, 5, 2, 5, "これ、何と言う？", 6, "鋼メンタル", 6, 12479, 5, "鋼メンタル");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6224, 12510, 12478, 12488, 12498, 5, "", 6224, "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", 3, 5, 1, 5, "これ、何と言う？", 6, "マゾヒスト", 5, 12473, 5, "マゾヒスト");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12467, 12450, 12473, 12488, 5, "", 6225, "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", 3, 5, 2, 5, "これ、何と言う？", 6, "コアトルス", 5, 12523, 5, "ケツァルコアトルス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12490, 12513, 12484, 5, "", 6226, "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", 3, 5, 2, 5, "これ、何と言う？", 6, "アナツバメ", 5, 12496, 5, "アナツバメ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12479, 12531, 12454, 12481, 5, "", 6227, "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", 3, 5, 2, 5, "これ、何と言う？", 6, "タンチョウ", 5, 12519, 5, "タンチョウヅル");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12523, 12522, 12495, 12479, 5, "", 6228, "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", 3, 5, 2, 5, "これ、何と言う？", 6, "ルリタテハ", 5, 12486, 5, "ルリタテハ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6229, 12503, 12522, 12473, 12531, 5, "", 6229, "特定の相手の能力\nを高める権能を持\nった存在", 1, 5, 1, 5, "これ、何と言う？", 6, "プリンセス", 5, 12475, 5, "プリンセスナイト");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6230, 12493, 12467, 12417, 12416, 4, "", 6230, "", 3, 4, 1, 5, "このパネルの「プリコネ読み」は？", 6, "ネコむすめ", 5, 12377, 4, "ネコむすめ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 32118, 28357, 31278, 21361, 6, "", 6231, "数が減少し、絶滅の危機に晒されている生物", 2, 6, 2, 6, "これ、何と言う？", 6, "絶滅危惧種", 6, 24807, 6, "絶滅危惧種");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12502, 12511, 12521, 5, "", 6232, "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", 1, 5, 2, 5, "これ、何と言う？", 6, "アブラゼミ", 5, 12476, 5, "アブラゼミ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12461, 33137, 12524, 5, "", 6233, "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", 1, 5, 2, 5, "これ、何と言う？", 6, "アキレス腱", 5, 12473, 6, "アキレス腱");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6234, 12490, 12523, 12488, 12471, 5, "", 6234, "自己愛が非常に強く、自分に陶酔した人、英語にすると？", 1, 5, 1, 5, "これ、何と言う？", 6, "ナルシスト", 5, 12473, 5, "ナルシスト");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6235, 12384, 12435, 12358, 12385, 4, "", 6235, "", 3, 4, 1, 4, "左のキャラは右のキャラを何と呼ぶ？", 6, "だんちょう", 4, 12423, 4, "だんちょう");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12356, 12356, 12369, 12394, 4, "", 6236, "若いうちから結婚が約束された相手のこと", 2, 4, 2, 4, "これ、何と読む？", 6, "いいなずけ", 4, 12378, 4, "許嫁＝いいなずけ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12420, 12383, 12377, 12364, 4, "", 6237, "「古事記」などに登場する、三本足の神聖なカラス", 3, 4, 2, 4, "これ、何と言う？", 6, "やたがらす", 4, 12425, 4, "やたがらす");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12373, 12372, 12358, 12376, 4, "", 6238, "「西遊記」で孫悟空と行動を共にする、河童の姿のキャラクター", 2, 4, 2, 4, "これ、何と言う？", 6, "さごじょう", 4, 12423, 4, "さごじょう");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12487, 12451, 12540, 12540, 5, "", 6239, "カジノで、ルーレットを回したりカードを配ったりとゲームの進行を行う役目", 2, 5, 2, 5, "これ、何と言う？", 6, "ディーラー", 5, 12521, 5, "ディーラー");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12456, 12531, 12473, 12503, 5, "", 6240, "「皇帝」は「エンペラー」。では「女帝」は？", 3, 5, 2, 5, "これ、何と言う？", 6, "エンプレス", 5, 12524, 5, "エンプレス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12460, 12531, 12473, 12473, 5, "", 6241, "銃のメンテナンスを職業として行う人のこと", 3, 5, 2, 5, "これ、何と言う？", 6, "ガンスミス", 5, 12511, 5, "ガンスミス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12495, 12452, 12459, 12499, 5, "", 6242, "熱帯の地域を象徴する花で、カオリの故郷でも親しまれる。赤や黄色の鮮やかな花びらをつける", 1, 5, 1, 5, "これ、何と言う？", 6, "ハイビスカ", 5, 12473, 5, "ハイビスカス");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12458, 12454, 12503, 12451, 5, "ウィル・オ・ウィスプ", 6243, "「ひとだま」と呼ばれる超常現象、英語で言うと？", 3, 5, 2, 5, "これ、何と言う？", 6, "オウィスプ", 5, 12473, 5, "ウィル・オ・ウィスプ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12491, 12471, 12452, 12461, 5, "", 6244, "魚の「鯉」の中でも観賞用として好まれる種で、高級な織物の柄によく似ていることが由来", 2, 5, 2, 5, "これ、何と言う？", 6, "ニシキゴイ", 5, 12468, 5, "ニシキゴイ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12524, 12469, 12480, 12497, 5, "レッサーパンダ", 6245, "「小さなパンダ」が由来の、動物園の人気者", 1, 5, 2, 5, "これ、何と言う？", 6, "レサパンダ", 5, 12531, 5, "レッサーパンダ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12450, 12490, 12480, 12467, 5, "", 6246, "ヘビの一種で、大きなものでは数メートルもある。映画で恐ろしいヘビとして描かれることが多い", 1, 5, 2, 5, "これ、何と言う？", 6, "アナコンダ", 5, 12531, 5, "アナコンダ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12479, 12521, 12517, 12531, 5, "", 6247, "「毒グモの代名詞」としてよく知られる大型のクモ。しかし、ペットとして好む人も多い", 1, 5, 2, 5, "これ、何と言う？", 6, "タランチュ", 5, 12481, 5, "タランチュラ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12473, 12452, 12454, 12462, 5, "", 6248, "「牛」の一種で、地域によって家畜としてもなじみ深い動物。その名の通り、水辺を好む", 1, 5, 2, 5, "これ、何と言う？", 6, "スイギュウ", 5, 12517, 5, "スイギュウ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12495, 12512, 12540, 12473, 5, "", 6249, "ネズミの仲間にあたる動物で、ヒマワリの種などを口いっぱいに頬張る姿が象徴的", 1, 5, 2, 5, "これ、何と言う？", 6, "ハムスター", 5, 12479, 5, "ハムスター");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12495, 12522, 12511, 12493, 5, "", 6250, "身を守るためのトゲトゲの体毛が非常に特徴的な動物。愛好家も多く、英名は「ヘッジホッグ」", 1, 5, 2, 5, "これ、何と言う？", 6, "ハリネズミ", 5, 12474, 5, "ハリネズミ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (0, 12498, 12519, 12531, 12454, 5, "", 6251, "猛毒を持つタコ。蛍光色のヒョウ柄の模様が、恐ろしさを助長している", 3, 5, 2, 5, "これ、何と言う？", 6, "ヒョウモン", 5, 12514, 5, "ヒョウモンダコ");
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES (6252, 12459, 12513, 12531, 12524, 5, "", 6252, "ネネカといつも一緒にいる動物", 3, 5, 1, 5, "これ、何と言う？", 6, "カメレオン", 5, 12458, 5, "カメレオン");
