INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("けさがため", "これ、何と言う？", "", 1209, 12364, 4, 12417, 12383, 2, 4, 0, 1, 4, 12373, 4, "柔道の固め技の一つ。お坊さんが身につける、とある衣装から名付けられた", 2, "けさがため", 12369, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("掛かり稽古", "これ、何と言う？", "", 1210, 12426, 6, 21476, 31293, 2, 4, 0, 1, 4, 12363, 6, "剣道の練習法。ふた手に分かれて、一方がもう一方に対し、休憩をせずに繰り返しかかっていく", 3, "掛かり稽古", 25499, 6);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("クレー射撃", "これ、何と言う？", "", 1211, 12540, 5, 25731, 23556, 2, 5, 0, 1, 5, 12524, 6, "射撃競技の一つ。高速で空中に射出された皿を、ショットガンで撃ち壊す精度を競う", 3, "クレー射撃", 12463, 6);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("イエロカド", "これ、何と言う？", "イエローカード", 1212, 12525, 5, 12489, 12459, 2, 5, 0, 1, 5, 12456, 5, "サッカーで悪質な反則に対して示される警告。これが2回出ると、さらに重いレッドカードとなる", 1, "イエローカード", 12452, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フットサル", "これ、何と言う？", "", 1213, 12488, 5, 12523, 12469, 2, 5, 0, 1, 5, 12483, 5, "サッカーから派生したスポーツで、5人対5人形式のサッカー。室内でプレイされることも。", 1, "フットサル", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("せおいなげ", "これ、何と言う？", "", 1214, 12356, 4, 12370, 12394, 2, 4, 0, 1, 4, 12362, 4, "柔道の花形の技としてよく知られる投げ技。相手を背負うような形で、肩越しに投げる", 1, "せおいなげ", 12379, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ウルトラC", "これ、何と言う？", "", 1215, 12488, 5, 67, 12521, 2, 5, 0, 1, 5, 12523, 2, "最近では「秘密兵器」という意味でも使われるが、元々は体操競技で非常に難しい技を指す言葉", 3, "ウルトラC", 12454, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("カタパルト", "これ、何と言う？", "", 1216, 12497, 5, 12488, 12523, 2, 5, 0, 1, 5, 12479, 5, "かつて使われた攻城兵器。てこの原理を利用して、遠くまで石を射出する", 2, "カタパルト", 12459, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("イージス艦", "これ、何と言う？", "", 1217, 12472, 5, 33382, 12473, 2, 5, 0, 1, 5, 12540, 6, "高度な対空射撃設備を搭載した軍艦。神話の中でゼウスが娘に与えた盾の名前が由来", 2, "イージス艦", 12452, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ツマンセル", "これ、何と言う？", "ツーマンセル", 1218, 12531, 5, 12523, 12475, 2, 5, 0, 1, 5, 12510, 5, "軍隊や警察において使われる言葉で、「二人一組」のこと", 3, "ツーマンセル", 12484, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ホロサイト", "これ、何と言う？", "", 1219, 12469, 5, 12488, 12452, 2, 5, 0, 1, 5, 12525, 5, "銃で狙いを定めるための「照準器」の種類の一つ。いわゆる「ホログラム」の技術を使っている", 2, "ホロサイト", 12507, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("サイレンサ", "これ、何と言う？", "", 1220, 12524, 5, 12469, 12531, 2, 5, 0, 1, 5, 12452, 5, "スパイ映画でもよく見る、銃の発砲音を抑えるために銃口につける装置。「サプレッサー」とも", 2, "サイレンサー", 12469, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("点バースト", "これ、何と言う？", "", 1221, 12540, 6, 12488, 12473, 2, 5, 0, 1, 5, 12496, 5, "ライフルなどの仕組みで、一度引き金を引くたびに、銃弾が3発ずつ発射されるもの", 3, "3点バースト", 28857, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ホルスター", "これ、何と言う？", "", 1222, 12473, 5, 12540, 12479, 2, 5, 0, 1, 5, 12523, 5, "拳銃を収めるために、主に腰に装備するケース。西部劇での決闘シーンなどでもよく目立つ", 2, "ホルスター", 12507, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ハロンぼう", "これ、何と言う？", "", 1223, 12531, 5, 12358, 12412, 2, 5, 0, 1, 5, 12525, 4, "競馬で、ゴールから一定距離ごとに目印として置かれる棒。赤と白のシマシマ柄をしている", 3, "ハロンぼう", 12495, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ステイヤー", "これ、何と言う？", "", 1224, 12452, 5, 12540, 12516, 2, 5, 0, 1, 5, 12486, 5, "競馬で、スタミナが多く長距離レースを得意とする馬のこと", 3, "ステイヤー", 12473, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("テンペスト", "これ、何と言う？", "", 1225, 12506, 5, 12488, 12473, 1, 5, 1225, 1, 5, 12531, 5, "『ジータ』のユニオンバーストの名前", 2, "テンペストブレード", 12486, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フィジーク", "これ、何と言う？", "", 1226, 12472, 5, 12463, 12540, 2, 5, 0, 1, 5, 12451, 5, "肉体美を競うコンテスト。よくボディビルと比較されるが、評価の観点や、対象の部位が異なる", 3, "フィジーク", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("レスリング", "これ、何と言う？", "", 1227, 12522, 5, 12464, 12531, 2, 5, 0, 1, 5, 12473, 5, "「プロレス」の「レス」、何という言葉の略？", 1, "レスリング", 12524, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ストレート", "これ、何と言う？", "", 1228, 12524, 5, 12488, 12540, 2, 5, 0, 1, 5, 12488, 5, "格闘技のパンチの種類の一つ。「ジャブ」と並んで重要となる、最も威力の出やすいもの", 1, "ストレート", 12473, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ノクアウト", "これ、何と言う？", "ノックアウト", 1229, 12450, 5, 12488, 12454, 2, 5, 0, 1, 5, 12463, 5, "格闘技での勝敗判定の一つ。相手をダウンさせたまま、既定の時間が過ぎたとき", 1, "ノックアウト", 12494, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("テクニカル", "これ、何と言う？", "", 1230, 12491, 5, 12523, 12459, 2, 5, 0, 1, 5, 12463, 5, "格闘技で、ただのノックアウトとは異なった特殊な判定である「TKO」。この「T」は何の略？", 2, "テクニカル", 12486, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ウェルター", "これ、何と言う？", "", 1231, 12523, 5, 12540, 12479, 2, 5, 0, 1, 5, 12455, 5, "ボクシングの階級で「ライト」などのつくものを省いて考えた場合、「ミドル級とライト級の間」は何？", 3, "ウェルター級", 12454, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ブーメラン", "これ、何と言う？", "", 1232, 12513, 5, 12531, 12521, 2, 5, 0, 1, 5, 12540, 5, "狩猟のために生まれた武器。「くの字型」をしており、投げた後Uターンして手元に戻ってくる", 1, "ブーメラン", 12502, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ネクロマン", "これ、何と言う？", "", 1233, 12525, 5, 12531, 12510, 2, 5, 0, 1, 5, 12463, 5, "「死霊使い」とも呼ばれる、ファンタジーでもおなじみの存在。死者を蘇らせ操る術を持つ", 2, "ネクロマンサー", 12493, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("グラディウ", "これ、何と言う？", "", 1234, 12487, 5, 12454, 12451, 2, 5, 0, 1, 5, 12521, 5, "かつての剣闘士（グラディエーター）が用いた、幅広の剣", 2, "グラディウス", 12464, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("コロッセオ", "これ、何と言う？", "", 1235, 12483, 5, 12458, 12475, 2, 5, 0, 1, 5, 12525, 5, "「コロシアム」「コロセウム」とも表記される、非常によく知られた円形闘技場", 1, "コロッセオ", 12467, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ノールック", "これ、何と言う？", "", 1236, 12523, 5, 12463, 12483, 2, 5, 0, 1, 5, 12540, 5, "球技スポーツで、そちらに目線を向けることなくパスをして、相手を出し抜くテクニックのこと", 2, "ノールックパス", 12494, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("スタンガン", "これ、何と言う？", "", 1237, 12531, 5, 12531, 12460, 2, 5, 0, 1, 5, 12479, 5, "護身用や警官が持つ装備として知られた、手持ちサイズの武器。高圧の電撃で相手を無力化する", 1, "スタンガン", 12473, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("にょいぼう", "これ、何と言う？", "", 1238, 12356, 4, 12358, 12412, 2, 4, 0, 1, 4, 12423, 4, "西遊記の「孫悟空」が武器に使った、自由自在に拡大・縮小できる棒", 1, "にょいぼう", 12395, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("デスサイズ", "これ、何と言う？", "", 1239, 12469, 5, 12474, 12452, 2, 5, 0, 1, 5, 12473, 5, "「死神」が持つと言われる大きな鎌のこと", 3, "デスサイズ", 12487, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("りゅうがん", "これ、何と言う？", "", 1240, 12358, 4, 12435, 12364, 1, 4, 1240, 1, 4, 12421, 4, "『ホマレ』が持つ能力で、発動中は遠く離れたものも見ることができる", 2, "りゅうがん", 12426, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ドーピング", "これ、何と言う？", "", 1241, 12500, 5, 12464, 12531, 2, 5, 0, 1, 5, 12540, 5, "スポーツにおいて、薬物などの禁止された手段を用いて、不当に能力を高めること", 1, "ドーピング", 12489, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("スライダー", "これ、何と言う？", "", 1242, 12452, 5, 12540, 12480, 2, 5, 0, 1, 5, 12521, 5, "野球の投球における球種の一つ。文字通り、スルリと滑るような変化をすることから", 1, "スライダー", 12473, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ツーシーム", "これ、何と言う？", "", 1243, 12471, 5, 12512, 12540, 2, 5, 0, 1, 5, 12540, 5, "野球の投球における球種の一つ。球が1回転する間に、縫い目が合計で2回通過することから", 3, "ツーシーム", 12484, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アンパイア", "これ、何と言う？", "", 1244, 12497, 5, 12450, 12452, 2, 5, 0, 1, 5, 12531, 5, "広義にはスポーツ全般の審判員を指すが、一般には特に野球の審判員を指すことが多い", 2, "アンパイア", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("デイゲーム", "これ、何と言う？", "", 1245, 12466, 5, 12512, 12540, 2, 5, 0, 1, 5, 12452, 5, "野球の試合の、開催形式の分類。時間帯によって、2つに分かれる", 2, "ナイター⇔デイゲーム", 12487, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ロスタイム", "これ、何と言う？", "", 1246, 12479, 5, 12512, 12452, 2, 5, 0, 1, 5, 12473, 5, "サッカーで「アディショナルタイム」と呼ばれるルールの、かつての呼び方", 1, "ロスタイム", 12525, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("センタリン", "これ、何と言う？", "", 1247, 12479, 5, 12531, 12522, 2, 5, 0, 1, 5, 12531, 5, "サッカーで、サイドから中央のゴール付近の味方へ大きなパスを出すこと", 2, "センタリング", 12475, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("オウンゴル", "これ、何と言う？", "オウンゴール", 1248, 12531, 5, 12523, 12468, 2, 5, 0, 1, 5, 12454, 5, "サッカーで、間違って自陣側のゴールにボールを入れてしまい、相手に得点が入ること", 1, "オウンゴール", 12458, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フェイント", "これ、何と言う？", "", 1249, 12452, 5, 12488, 12531, 2, 5, 0, 1, 5, 12455, 5, "多くのスポーツで、相手の意表を突くためにわざと行う「ふり動作」のこと。「フェイク」とも", 1, "フェイント", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ごっつぁん", "これ、何と言う？", "", 1250, 12388, 4, 12435, 12353, 2, 4, 0, 1, 4, 12387, 4, "力士の間で、お礼を言うときに広く使われるあいさつ。昨今は相撲以外の一般層にも浸透している", 3, "ごっつぁんです", 12372, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("聖火リレー", "これ、何と言う？", "", 1251, 12522, 6, 12540, 12524, 2, 6, 0, 1, 5, 28779, 5, "オリンピックで、トーチに灯した火をランナーが次々と受け継いで、会場までリレーをする催し", 1, "聖火リレー", 32854, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ダイビング", "これ、何と言う？", "", 1252, 12499, 5, 12464, 12531, 2, 5, 0, 1, 5, 12452, 5, "野球の野手が、大きく頭から飛び込んで打球をキャッチするプレー。成功すると非常に盛り上がる", 2, "ダイビングキャッチ", 12480, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フレミング", "これ、何と言う？", "フレーミング", 1253, 12511, 5, 12464, 12531, 2, 5, 0, 1, 5, 12524, 5, "野球の捕手が、判定の際どいボールを、自分がうまく動くことで審判にストライクと判定させる技術", 3, "フレーミング", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ストライク", "これ、何と言う？", "", 1255, 12521, 5, 12463, 12452, 1, 5, 1255, 1, 5, 12488, 5, "『ペコリーヌ』のユニオンバースト", 1, "プリンセスストライク", 12473, 5);
