INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ルーティン", "これ、何と言う？", "", 5151, 12486, 5, 12531, 12451, 2, 5, 0, 5, 5, 12540, 5, "毎日の夜に行う、自分ならではの習慣のこと。近年ではタレントが動画に撮って発信するのが人気", 2, "ナイトルーティン", 12523, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("バンバンジ", "これ、何と読む？", "", 5152, 12496, 5, 12472, 12531, 2, 5, 0, 5, 5, 12531, 5, "蒸した鶏肉をごまだれで味付けた中華料理。キュウリを添えることが多い", 3, "棒棒鶏＝バンバンジー", 12496, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("いそべあげ", "これ、何と言う？", "", 5153, 12409, 4, 12370, 12354, 2, 4, 0, 5, 4, 12381, 4, "天ぷらの一種。「青のり」を混ぜた衣で、ちくわなどの練り物を揚げた食べ物", 2, "いそべあげ", 12356, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("おにぎりざ", "このパネルの「プリコネ読み」は？", "", 5154, 12366, 4, 12374, 12426, 1, 4, 5154, 5, 4, 12395, 4, "", 3, "おにぎりざ", 12362, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ダージリン", "これ、何と言う？", "", 5155, 12472, 5, 12531, 12522, 2, 5, 0, 5, 5, 12540, 5, "非常にポピュラーな紅茶の種類。「ウバ」「キーマン」に並び、「世界三大銘茶」と評される", 1, "ダージリン", 12480, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アルグレイ", "これ、何と言う？", "アールグレイ", 5156, 12464, 5, 12452, 12524, 2, 5, 0, 5, 5, 12523, 5, "ポピュラーな紅茶の一種で、お菓子作りの材料にも好まれる。伯爵という意味の言葉が含まれる", 2, "アールグレイ", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ジンジャー", "これ、何と言う？", "", 5157, 12472, 5, 12540, 12515, 2, 5, 0, 5, 5, 12531, 5, "非常にポピュラーな、炭酸飲料の一種。ショウガの風味が特徴的", 1, "ジンジャーエール", 12472, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("エンゲル係", "これ、何と言う？", "", 5158, 12466, 5, 20418, 12523, 2, 5, 0, 5, 5, 12531, 6, "家庭の支出額のうち、「食費」が占める割合のこと", 2, "エンゲル係数", 12456, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("プリンたい", "これ、何と言う？", "", 5159, 12531, 5, 12356, 12383, 2, 5, 0, 5, 5, 12522, 4, "一般にビールやレバー、エビ、牡蠣などに多く含まれる成分。あまりとり過ぎると痛風の原因に", 2, "プリンたい", 12503, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("きぬどうふ", "これ、何と言う？", "", 5160, 12393, 4, 12405, 12358, 2, 4, 0, 5, 4, 12396, 4, "豆腐の種類として特によく食べられる2種類。1つは「木綿豆腐」で、もう一つは？", 1, "きぬどうふ", 12365, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("インフォム", "これ、何と言う？", "インフォームド・コンセント", 5161, 12501, 5, 12512, 12457, 2, 5, 0, 5, 5, 12531, 5, "患者が医師から十分な説明を受け、合意のうえで医療行為を受けること", 3, "インフォームド・コンセント", 12452, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("だんしゃり", "これ、何と言う？", "", 5162, 12375, 4, 12426, 12419, 2, 4, 0, 5, 4, 12435, 4, "元は「ヨガ」から生まれた習慣で、不要なものを捨て、物に執着せずに生きること", 2, "だんしゃり", 12384, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("スギかふん", "これ、何と言う？", "", 5163, 12363, 5, 12435, 12405, 2, 5, 0, 5, 4, 12462, 4, "花粉症の原因として最も多いのは、どんな種類の花粉？", 2, "スギかふん", 12473, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フレックス", "これ、何と言う？", "", 5164, 12483, 5, 12473, 12463, 2, 5, 0, 5, 5, 12524, 5, "固定の時間帯で働く従来の働き方とは違い、始業と終業の時間を自由に決められる労働制度", 2, "フレックスタイム制", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("きゅうかん", "これ、何と言う？", "", 5165, 12358, 4, 12435, 12363, 2, 4, 0, 5, 4, 12421, 4, "アルコールをあまり毎日飲み過ぎず、肝臓を休める日を適度に設けることを推奨する言葉", 2, "きゅうかん日", 12365, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("マヨネーズ", "これ、何と言う？", "", 5166, 12493, 5, 12474, 12540, 2, 5, 0, 5, 5, 12520, 5, "卵・酢・油などから作られ、サラダなどにかけることが多い、定番の調味料", 1, "マヨネーズ", 12510, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ケチャップ", "これ、何と言う？", "", 5167, 12515, 5, 12503, 12483, 2, 5, 0, 5, 5, 12481, 5, "主にトマトなどから作られる、定番の赤い調味料。オムライスやフライドポテトなどによく合う", 1, "ケチャップ", 12465, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("マーガリン", "これ、何と言う？", "", 5168, 12460, 5, 12531, 12522, 2, 5, 0, 5, 5, 12540, 5, "トーストなどに塗るのが定番の加工食品。バターの代替品として使われることも", 1, "マーガリン", 12510, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("砂糖塩酢醤", "これ、何と言う？", "砂糖・塩・酢・醤油・味噌", 5169, 22633, 6, 37284, 37218, 2, 6, 0, 5, 6, 31958, 6, "料理の「さしすせそ」、順番に何と言う？", 3, "砂糖・塩・酢・醤油・味噌", 30722, 6);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ごこくまい", "これ、何と言う？", "", 5170, 12367, 4, 12356, 12414, 2, 4, 0, 5, 4, 12371, 4, "健康食品として知られるお米。米のほかに麦や粟など、合計5種類の穀物が合わさっている", 3, "ごこくまい", 12372, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ビュッフェ", "これ、何と言う？", "", 5171, 12483, 5, 12455, 12501, 2, 5, 0, 5, 5, 12517, 5, "ホテルなどで多い、セルフサービス形式での食事。「バイキング」と混同されることが少なくない", 3, "ビュッフェ", 12499, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("たかのつめ", "これ、何と言う？", "", 5172, 12398, 4, 12417, 12388, 2, 4, 0, 5, 4, 12363, 4, "トウガラシの代表的な品種。名前は、とある鳥のかぎづめに似ていることから", 2, "たかのつめ", 12383, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("タラバガニ", "これ、何と言う？", "", 5173, 12496, 5, 12491, 12460, 2, 5, 0, 5, 5, 12521, 5, "高級食材としてよく知られるカニ。魚のタラが獲れる場所に生息していることが由来", 1, "タラバガニ", 12479, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("サクラエビ", "これ、何と言う？", "", 5174, 12521, 5, 12499, 12456, 2, 5, 0, 5, 5, 12463, 5, "食用として、かき揚げなどで好まれる小さなエビ。透き通ったピンク色の身体をしている", 2, "サクラエビ", 12469, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("さんきゅぱ", "これ、何と言う？", "さんきゅっぱ", 5175, 12365, 4, 12401, 12421, 2, 4, 0, 5, 4, 12435, 4, "売り物によく設定される「398円」という値段を読みやすくした読み方", 1, "さんきゅっぱ", 12373, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("かいばしら", "これ、何と言う？", "", 5176, 12400, 4, 12425, 12375, 2, 4, 0, 5, 4, 12356, 4, "ホタテなどの二枚貝で、円柱の形をした大きな可食部の通称", 1, "かいばしら", 12363, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ファースト", "これ、何と言う？", "", 5177, 12540, 5, 12488, 12473, 2, 5, 0, 5, 5, 12449, 5, "結婚式で、ケーキ入刀のあとに新郎新婦がお互いにケーキを食べさせあう演出", 2, "ファーストバイト", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("いろなおし", "これ、何と言う？", "", 5178, 12394, 4, 12375, 12362, 2, 4, 0, 5, 4, 12429, 4, "結婚披露宴の途中で新郎新婦が離席し、衣装を変えること", 2, "おいろなおし", 12356, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("剪定ばさみ", "これ、何と言う？", "", 5179, 12400, 6, 12415, 12373, 2, 6, 0, 5, 4, 23450, 4, "庭木を切って整えるためのはさみで、普通のはさみなら切れない太い枝なども切ることができる", 3, "剪定ばさみ", 21098, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("クーリング", "これ、何と言う？", "", 5180, 12522, 5, 12464, 12531, 2, 5, 0, 5, 5, 12540, 5, "訪問販売などで考える暇なく購入してしまったものを、一定期間中ならキャンセルできる制度", 2, "クーリング・オフ", 12463, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ねこまんま", "これ、何と言う？", "", 5181, 12414, 4, 12414, 12435, 2, 4, 0, 5, 4, 12371, 4, "「猫にあげるような飯」のこと。削り節をかけるか、味噌汁をかけるか、定義は地域で分かれる", 2, "ねこまんま", 12397, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("おくション", "これ、何と言う？", "", 5182, 12471, 4, 12531, 12519, 2, 4, 0, 5, 5, 12367, 5, "分譲マンションで、価格が1億円を超えるような特に高級なものを指す俗語", 3, "おくション", 12362, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("パーキング", "これ、何と言う？", "", 5183, 12461, 5, 12464, 12531, 2, 5, 0, 5, 5, 12540, 5, "高速道路で、「SA」は「サービスエリア」。では「PA」は？", 1, "パーキングエリア", 12497, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("細マッチョ", "これ、何と言う？", "", 5184, 12483, 6, 12519, 12481, 2, 5, 0, 5, 5, 12510, 5, "「ほどよく筋肉があり、脂肪は少なく引き締まった体型」を指す俗語。対義語は「ゴリマッチョ」", 2, "細マッチョ", 32048, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ハンバーグ", "これ、何と言う？", "", 5185, 12496, 5, 12464, 12540, 2, 5, 0, 5, 5, 12531, 5, "定番の肉料理。ひき肉をメインに、卵やパン粉、野菜などと一緒にこねて焼いたもの", 1, "ハンバーグ", 12495, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ウェルカム", "これ、何と言う？", "", 5186, 12523, 5, 12512, 12459, 2, 5, 0, 5, 5, 12455, 5, "結婚式やパーティなどで、ゲストを迎え入れるために入り口に置かれる案内板のこと", 2, "ウェルカムボード", 12454, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("てっかまき", "これ、何と言う？", "", 5187, 12363, 4, 12365, 12414, 2, 4, 0, 5, 4, 12387, 4, "のり巻き寿司の一つで、マグロの赤身とわさびが巻かれたもの", 1, "てっかまき", 12390, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("かっぱまき", "これ、何と言う？", "", 5188, 12401, 4, 12365, 12414, 2, 4, 0, 5, 4, 12387, 4, "のり巻き寿司の一つで、きゅうりが巻かれたもの", 1, "かっぱまき", 12363, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("おやこどん", "これ、何と言う？", "", 5189, 12371, 4, 12435, 12393, 2, 4, 0, 5, 4, 12420, 4, "どんぶり物の料理の一つ。割下で甘く煮た鶏肉を、たまごでとじてご飯に乗せたもの", 1, "おやこどん", 12362, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ちらしずし", "これ、何と言う？", "", 5190, 12375, 4, 12375, 12378, 2, 4, 0, 5, 4, 12425, 4, "寿司の一種で、握ったり巻いたりしたものではなく、具材をご飯の上に散らすように盛るのが特徴", 1, "ちらしずし", 12385, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("にはちそば", "これ、何と言う？", "", 5191, 12385, 4, 12400, 12381, 2, 4, 0, 5, 4, 12399, 4, "「そば」の分類の一つで、そば粉とうどん粉を２：８の配分で打ったそばのこと", 3, "にはちそば", 12395, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ストレミン", "これ、何と言う？", "", 5192, 12524, 5, 12531, 12511, 2, 5, 0, 5, 5, 12488, 5, "「世界一臭い缶詰め」として、良くも悪くも有名な食べ物。塩漬けのニシンを発酵させて作る", 3, "シュールストレミング", 12473, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("子丑寅卯辰", "これ、何と言う？", "子・丑・寅・卯・辰・巳・午・未・申……", 5193, 23493, 6, 36784, 21359, 2, 6, 0, 5, 6, 19985, 6, "「干支」を順番に並べると、最初にくる5つは何？", 3, "子・丑・寅・卯・辰・巳・午・未・申……", 23376, 6);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ふねんごみ", "これ、何と言う？", "", 5194, 12435, 4, 12415, 12372, 2, 4, 0, 5, 4, 12397, 4, "", 1, "可燃ごみ⇔ふねんごみ", 12405, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ベドタウン", "これ、何と言う？", "ベッドタウン", 5195, 12479, 5, 12531, 12454, 2, 5, 0, 5, 5, 12489, 5, "日中は大都市へ通勤し、夜だけ帰ってくるための場所として、その近郊に発展した街のこと", 2, "ベッドタウン", 12505, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("カーシェア", "これ、何と言う？", "", 5196, 12471, 5, 12450, 12455, 2, 5, 0, 5, 5, 12540, 5, "レンタカーに並び、近年急速に普及した貸し車サービス。短時間でも気軽に使えるのが長所", 2, "カーシェア", 12459, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("シュウマイ", "これ、何と読む？", "", 5197, 12454, 5, 12452, 12510, 2, 5, 0, 5, 5, 12517, 5, "豚のひき肉を皮で包んで蒸した、中華料理の一つ", 2, "焼売＝シュウマイ", 12471, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("チャーハン", "この料理、何と読む？", "", 5198, 12540, 5, 12531, 12495, 2, 5, 0, 5, 5, 12515, 5, "", 1, "炒飯＝チャーハン", 12481, 5);
