INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("Tレックス", "これ、何と言う？", "", 6049, 12483, 2, 12473, 12463, 2, 5, 0, 6, 5, 12524, 5, "『ティラノサウルス』の別の言い方", 2, "Tレックス", 84, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ケラトプス", "これ、何と言う？", "", 6050, 12488, 5, 12473, 12503, 2, 5, 0, 6, 5, 12521, 5, "3本の立派なツノが特徴の、有名な恐竜。名前の読み方は、「トリ」で区切るのが実は正しい", 1, "トリケラトプス", 12465, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ロケション", "これ、何と言う？", "エコーロケーション", 6051, 12471, 5, 12531, 12519, 2, 5, 0, 6, 5, 12465, 5, "イルカやコウモリなどが持つ能力。超音波を発し、その反響によって周囲の様子を探る", 2, "エコーロケーション", 12525, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("マングロブ", "これ、何と言う？", "マングローブ", 6052, 12464, 5, 12502, 12525, 2, 5, 0, 6, 5, 12531, 5, "熱帯や亜熱帯の一部の水辺に生育する植物の総称で、非常にユニークな形の根っこを形成する", 1, "マングローブ", 12510, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("天然記念物", "これ、何と言う？", "", 6053, 35352, 6, 29289, 24565, 2, 6, 0, 6, 6, 28982, 6, "学術的に貴重なものとして保護される、天然の動物・植物・地質・鉱物。アホウドリやトキなど", 1, "天然記念物", 22825, 6);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("つちふまず", "これ、何と言う？", "", 6054, 12405, 4, 12378, 12414, 2, 4, 0, 6, 4, 12385, 4, "人間の足の裏の、地面と接しないくぼんだ部分。人によっては無い場合もある", 1, "つちふまず", 12388, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ファントム", "これは何？", "", 6055, 12531, 5, 12512, 12488, 1, 5, 5036, 6, 5, 12449, 5, "『タマキ』の裏の名前にして、恵まれない人たちを助ける、謎に包まれた義賊", 2, "ファントムキャッツ", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ワンアイド", "これ、何と読む？", "", 6056, 12450, 5, 12489, 12452, 1, 5, 6056, 6, 5, 12531, 5, "裏社会で恐れられる『ミツキ』の二つ名。彼女の隻眼からは逃れられない", 3, "隻眼の赤い悪魔\n＝ワンアイドデビル", 12527, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("コレクター", "これ、何と読む？", "", 6057, 12463, 5, 12540, 12479, 1, 5, 6057, 6, 5, 12524, 5, "『ナナカ』の二つ名。さまざまな魔法を探し集めていることに由来する", 3, "蒐集家（＝コレクター）", 12467, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("レグナント", "これ、何と読む？", "", 6058, 12490, 5, 12488, 12531, 1, 5, 6058, 6, 5, 12464, 5, "『ネネカ』の、『七冠』としての二つ名。あらゆるものに姿を変化させられることから", 3, "変貌大妃（＝メタモルレグナント）", 12524, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ケンカバカ", "これ、何と言う？", "", 6059, 12459, 5, 12459, 12496, 1, 5, 6059, 6, 5, 12531, 5, "『イノリ』が『カヤ』に対して、ムキになったり、おちょくったりするときの呼び方", 2, "脳筋ケンカバカ", 12465, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("サラブレド", "これ、何と言う？", "サラブレッド", 6060, 12502, 5, 12489, 12524, 2, 5, 0, 6, 5, 12521, 5, "レースのために、長い年月をかけて改良された類の馬。家柄の良い人のたとえ表現でもおなじみ", 1, "サラブレッド", 12469, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("スナギツネ", "これ、何と言う？", "", 6061, 12462, 5, 12493, 12484, 2, 5, 0, 6, 5, 12490, 5, "キツネの一種。冷ややかで悟りを開いたような表情に見えることで近年人気を博している", 3, "チベットスナギツネ", 12473, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("シラカンス", "これ、何と言う？", "シーラカンス", 6062, 12459, 5, 12473, 12531, 2, 5, 0, 6, 5, 12521, 5, "数億年も前から変わらない姿のまま現存しているため、「生きた化石」と呼ばれる深海魚", 2, "シーラカンス", 12471, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ヘラクレス", "これ、何と言う？", "", 6063, 12463, 5, 12473, 12524, 2, 5, 0, 6, 5, 12521, 5, "カブトムシの中で最大のサイズを誇る種として、子どもたちからも絶大な人気を誇る昆虫", 1, "ヘラクレスオオカブト", 12504, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アブラムシ", "これ、何と言う？", "", 6064, 12521, 5, 12471, 12512, 2, 5, 0, 6, 5, 12502, 5, "昔の子どもたちがこの虫の体液を髪に塗り、テカテカさせて遊んでいたことに由来する虫の名前", 1, "アブラムシ", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("泣きぼくろ", "これ、何と言う？", "", 6065, 12412, 6, 12429, 12367, 2, 4, 0, 6, 4, 12365, 4, "目のすぐ下のあたりにあるほくろの通称", 1, "泣きぼくろ", 27875, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("シルババク", "これ、何と言う？", "シルバーバック", 6066, 12496, 5, 12463, 12496, 2, 5, 0, 6, 5, 12523, 5, "年を重ねたオスのゴリラの背中の毛が白くなる現象。群れの中のボス個体によく見られる", 3, "シルバーバック", 12471, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("カメレオン", "これ、何と言う？", "", 6067, 12524, 5, 12531, 12458, 2, 5, 0, 6, 5, 12513, 5, "身体の色を自在に変化させることでおなじみの爬虫類。演技の幅が広い俳優や声優のたとえにも", 1, "カメレオン", 12459, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("オニヤンマ", "これ、何と言う？", "", 6068, 12516, 5, 12510, 12531, 2, 5, 0, 6, 5, 12491, 5, "非常に大きなトンボの一種。「鬼」の服装とよく似た、黒と黄色のシマシマ模様が特徴的", 1, "オニヤンマ", 12458, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("グルミング", "これ、何と言う？", "グルーミング", 6069, 12511, 5, 12464, 12531, 2, 5, 0, 6, 5, 12523, 5, "猫などの動物の習性として知られる「毛づくろい」。英語でなんと言う？", 3, "グルーミング", 12464, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("マングース", "これ、何と言う？", "", 6070, 12464, 5, 12473, 12540, 2, 5, 0, 6, 5, 12531, 5, "一般に「ハブ」の天敵というイメージを持たれている、体長数十センチほどの哺乳類", 1, "マングース", 12510, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ギンヤンマ", "これ、何と言う？", "", 6071, 12516, 5, 12510, 12531, 2, 5, 0, 6, 5, 12531, 5, "トンボの一種。身体は黄緑色だが、よく見るとキレイな銀色の模様があることからこう呼ばれる", 2, "ギンヤンマ", 12462, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("カモミール", "これ、何と言う？", "", 6072, 12511, 5, 12523, 12540, 2, 5, 0, 6, 5, 12514, 5, "「カミツレ」とも呼ばれるハーブの一種。りんごのような香りがあり、お茶としてよく好まれる", 3, "カモミール", 12459, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ホタルイカ", "これ、何と言う？", "", 6073, 12523, 5, 12459, 12452, 2, 5, 0, 6, 5, 12479, 5, "指先ほどのサイズの小さなイカ。青白く発光する性質があり、また食用としても好まれる", 2, "ホタルイカ", 12507, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("キンメダイ", "これ、何と言う？", "", 6074, 12513, 5, 12452, 12480, 2, 5, 0, 6, 5, 12531, 5, "高級魚や縁起物としておなじみのタイの一種。名前は、目が金色に輝くことから", 1, "キンメダイ", 12461, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("グソクムシ", "これ、何と言う？", "", 6075, 12463, 5, 12471, 12512, 2, 5, 0, 6, 5, 12477, 5, "深海に生息する甲殻類。姿はダンゴムシに似ていて、大きなものは体長50cmを超える", 3, "ダイオウグソクムシ", 12464, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("チンアナゴ", "これ、何と言う？", "", 6076, 12450, 5, 12468, 12490, 2, 5, 0, 6, 5, 12531, 5, "水族館でも人気なアナゴの一種。砂底から身体を出し、群れで同じ方向をじっと向く姿が特徴的", 2, "チンアナゴ", 12481, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ラフレシア", "これ、何と言う？", "", 6077, 12524, 5, 12450, 12471, 2, 5, 0, 6, 5, 12501, 5, "「世界最大の花」としておなじみの、熱帯地域に生息する花。強い悪臭を放つことでも有名", 1, "ラフレシア", 12521, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ベニテング", "これ、何と言う？", "", 6078, 12486, 5, 12464, 12531, 2, 5, 0, 6, 5, 12491, 5, "真っ赤なカサに白い点のある、キノコの一種。毒があり、食べると幻覚・腹痛などの症状が出る", 3, "ベニテングタケ", 12505, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("コノハズク", "これ、何と言う？", "", 6079, 12495, 5, 12463, 12474, 2, 5, 0, 6, 5, 12494, 5, "「フクロウ」の一種。身の危険を感じたときに身体を細く縮めて、木の枝に擬態する習性がある", 3, "コノハズク", 12467, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("サンショウ", "これ、何と言う？", "", 6080, 12471, 5, 12454, 12519, 2, 5, 0, 6, 5, 12531, 5, "両生類の一種。防衛のために出す粘液の匂いが、和食に使うとあるスパイスに似ているのが由来", 3, "サンショウウオ", 12469, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フキノトウ", "これ、何と言う？", "", 6081, 12494, 5, 12454, 12488, 2, 5, 0, 6, 5, 12461, 5, "「蕗（フキ）」が大きく成長する前の、つぼみの部分。春の山菜として、天ぷらなどで好まれる", 3, "フキノトウ", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("サトウキビ", "これ、何と言う？", "", 6082, 12454, 5, 12499, 12461, 2, 5, 0, 6, 5, 12488, 5, "砂糖の原料となる、イネ科の植物", 1, "サトウキビ", 12469, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ふしちょう", "これ、何と言う？", "", 6083, 12385, 4, 12358, 12423, 2, 4, 0, 6, 4, 12375, 4, "フェニックスとも呼ばれる、死しても再びよみがえるとされる伝説の鳥", 1, "ふしちょう", 12405, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("きゅうかく", "これ、何と言う？", "", 6084, 12358, 4, 12367, 12363, 2, 4, 0, 6, 4, 12421, 4, "「五感」と呼ばれる動物の機能、視覚・聴覚・触覚・味覚と、あと一つは？", 1, "きゅうかく", 12365, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ドラミング", "これ、何と言う？", "", 6085, 12511, 5, 12464, 12531, 2, 5, 0, 6, 5, 12521, 5, "ゴリラが手で胸をドコドコと叩いて音をたてる行動", 2, "ドラミング", 12489, 5);
