INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("つくもがみ", "これ、何と言う？", "", 6092, 12418, 4, 12415, 12364, 2, 4, 0, 6, 4, 12367, 4, "漢字では「付喪神」と書く、長い年月が経った道具などに宿るとされる神様", 2, "つくもがみ", 12388, 4);
