INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("んしょばと", "これ、何と言う？", "", 6183, 12423, 4, 12392, 12400, 2, 4, 0, 6, 4, 12375, 4, "ハトの帰巣本能を使って遠くに手紙を届けること。通信技術が発達する前は、広く活用された", 2, "でんしょばと", 12435, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ホホケキョ", "これ、何と言う？", "ホーホケキョ", 6184, 12465, 5, 12519, 12461, 2, 5, 0, 6, 5, 12507, 5, "「ウグイス」の鳴き声を文字に起こした表現。春の訪れの象徴としてもなじみ深い", 2, "ホーホケキョ", 12507, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ホトトギス", "これ、何と言う？", "", 6185, 12488, 5, 12473, 12462, 2, 5, 0, 6, 5, 12488, 5, "武将の性格を比喩した「鳴かぬなら～～」の俳句に使われている鳥の名前は？", 1, "ホトトギス", 12507, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ヘビイチゴ", "これ、何と言う？", "", 6186, 12452, 5, 12468, 12481, 2, 5, 0, 6, 5, 12499, 5, "「ヘビがいそうな場所に生える」ことに由来する、イチゴに似た植物。食用には向かない", 2, "ヘビイチゴ", 12504, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("松ぼっくり", "これ、何と言う？", "", 6187, 12387, 6, 12426, 12367, 2, 4, 0, 6, 4, 12412, 4, "松かさとも呼ばれる、概略すると松の木の果実の部分。クリスマスツリーの飾りにもよく使われる", 2, "松ぼっくり", 26494, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アゾールド", "このキャラの名前は？", "", 6188, 12540, 5, 12489, 12523, 1, 5, 6188, 6, 5, 12478, 5, "", 1, "アゾールド", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ズナちょむ", "左のキャラは右のキャラを何と呼ぶ？", "", 6189, 12385, 5, 12416, 12423, 1, 5, 6189, 6, 4, 12490, 4, "", 3, "ズナちょむ", 12474, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フォールド", "これ、何と言う？", "", 6190, 12540, 5, 12489, 12523, 2, 5, 0, 6, 5, 12457, 5, "猫の種類の一つ。垂れた耳が特徴的", 2, "スコティッシュフォールド", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ゴールデン", "これ、何と言う？", "", 6191, 12523, 5, 12531, 12487, 2, 5, 0, 6, 5, 12540, 5, "非常にポピュラーな種の犬で、フサフサとした黄色系の体毛と、大きな体が特徴", 1, "ゴールデンレトリーバー", 12468, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ドッグラン", "これ、何と言う？", "", 6192, 12464, 5, 12531, 12521, 2, 5, 0, 6, 5, 12483, 5, "家庭犬がリードをつけずに自由に走り回れるように設けられた、フェンスで囲われた広いスペース", 2, "ドッグラン", 12489, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ラブラドル", "これ、何と言う？", "ラブラドールレトリーバー", 6193, 12521, 5, 12523, 12489, 2, 5, 0, 6, 5, 12502, 5, "レトリーバー種としてゴールデンレトリーバーに並び有名な犬。盲導犬・警察犬としても活躍する", 1, "ラブラドールレトリーバー", 12521, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("マーキング", "これ、何と言う？", "", 6194, 12461, 5, 12464, 12531, 2, 5, 0, 6, 5, 12540, 5, "動物が自分の体臭や尿などを残すことで、そこが自分のなわばりであるとアピールする習性", 1, "マーキング", 12510, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("たいこ持ち", "これ、何と言う？", "", 6195, 12371, 4, 12385, 25345, 2, 4, 0, 6, 4, 12356, 4, "しきりに偉い人のご機嫌をとるような人間を、ある楽器の名前を使ってなんと呼ぶ？", 2, "たいこ持ち", 12383, 6);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("キリギリス", "これ、何と言う？", "", 6196, 12462, 5, 12473, 12522, 2, 5, 0, 6, 5, 12522, 5, "有名な童話の中で、「アリ」と対比して、なまけ者として描かれている昆虫", 1, "キリギリス", 12461, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("クルマエビ", "これ、何と言う？", "", 6197, 12510, 5, 12499, 12456, 2, 5, 0, 6, 5, 12523, 5, "エビの一種。身体にクッキリとシマシマがあり、丸まった姿が車のホイールに見える", 1, "クルマエビ", 12463, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ダイショウ", "これ、何と言う？", "", 6198, 12471, 5, 12454, 12519, 2, 5, 0, 6, 5, 12452, 5, "ヘビの一種で、身体は青緑色。由来は諸説あるが「ヘビの中でも特に大物の風格がある」ことなど", 3, "アオダイショウ", 12480, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("しそちょう", "これ、何と言う？", "", 6199, 12385, 4, 12358, 12423, 2, 4, 0, 6, 4, 12381, 4, "太古の時代に存在した鳥で、その名の通り、鳥類の始まりとなった鳥とされている", 2, "しそちょう", 12375, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アウストラ", "これ、何と言う？", "", 6200, 12473, 5, 12521, 12488, 2, 5, 0, 6, 5, 12454, 5, "「最古の人類」とされている種類の猿人", 2, "アウストラロピテクス", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("キュウカン", "これ、何と言う？", "", 6201, 12454, 5, 12531, 12459, 2, 5, 0, 6, 5, 12517, 5, "ペットとしても親しまれる手乗りサイズの鳥で、人の声をマネして鳴くのが上手い", 1, "キュウカンチョウ", 12461, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("オットセイ", "これ、何と言う？", "", 6202, 12488, 5, 12452, 12475, 2, 5, 0, 6, 5, 12483, 5, "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", 2, "オットセイ", 12458, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アジアゾウ", "これ、何と言う？", "", 6203, 12450, 5, 12454, 12478, 2, 5, 0, 6, 5, 12472, 5, "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", 1, "アジアゾウ", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ベニシジミ", "これ、何と言う？", "", 6204, 12471, 5, 12511, 12472, 2, 5, 0, 6, 5, 12491, 5, "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", 3, "ベニシジミ", 12505, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ジュウサン", "これ、何と言う？", "", 6205, 12454, 5, 12531, 12469, 2, 5, 0, 6, 5, 12517, 5, "テントウムシの一種。身体に13個の点々模様がある", 2, "ジュウサンホシテントウ", 12472, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ニジュウヤ", "これ、何と言う？", "", 6206, 12517, 5, 12516, 12454, 2, 5, 0, 6, 5, 12472, 5, "テントウムシの一種。身体に28個の点々模様がある", 3, "ニジュウヤホシテントウ", 12491, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アニサキス", "これ、何と言う？", "", 6207, 12469, 5, 12473, 12461, 2, 5, 0, 6, 5, 12491, 5, "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", 2, "アニサキス", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("わたりどり", "これ、何と言う？", "", 6208, 12426, 4, 12426, 12393, 2, 4, 0, 6, 4, 12383, 4, "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", 1, "わたりどり", 12431, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フンボルト", "これ、何と言う？", "", 6209, 12508, 5, 12488, 12523, 2, 5, 0, 6, 5, 12531, 5, "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", 3, "フンボルトペンギン", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("エボシドリ", "これ、何と言う？", "", 6210, 12471, 5, 12522, 12489, 2, 5, 0, 6, 5, 12508, 5, "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", 3, "エボシドリ", 12456, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ネザランド", "これ、何と言う？", "ネザーランドドワーフ", 6211, 12521, 5, 12489, 12531, 2, 5, 0, 6, 5, 12470, 5, "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", 3, "ネザーランドドワーフ", 12493, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ホーランド", "これ、何と言う？", "", 6212, 12521, 5, 12489, 12531, 2, 5, 0, 6, 5, 12540, 5, "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", 3, "ホーランドロップ", 12507, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("フェレット", "これ、何と言う？", "", 6213, 12524, 5, 12488, 12483, 2, 5, 0, 6, 5, 12455, 5, "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", 2, "フェレット", 12501, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("マントヒヒ", "これ、何と言う？", "", 6214, 12488, 5, 12498, 12498, 2, 5, 0, 6, 5, 12531, 5, "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", 2, "マントヒヒ", 12510, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("シロナガス", "これ、何と言う？", "", 6215, 12490, 5, 12473, 12460, 2, 5, 0, 6, 5, 12525, 5, "最も大きい動物としてよく知られる種類のクジラ", 2, "シロナガスクジラ", 12471, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("シロイルカ", "これ、何と言う？", "", 6216, 12452, 5, 12459, 12523, 2, 5, 0, 6, 5, 12525, 5, "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", 3, "シロイルカ", 12471, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("しろうさぎ", "これ、何と言う？", "", 6217, 12358, 4, 12366, 12373, 2, 4, 0, 6, 4, 12429, 4, "神話に登場する、有名な動物", 2, "いなばのしろうさぎ", 12375, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("スコピオン", "これ、何と言う？", "さそり＝スコーピオン", 6218, 12500, 5, 12531, 12458, 2, 5, 0, 6, 5, 12467, 5, "カタカナでの呼び方は？", 1, "さそり＝スコーピオン", 12473, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ちからこぶ", "これ、何と言う？", "", 6219, 12425, 4, 12406, 12371, 2, 4, 0, 6, 4, 12363, 4, "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", 2, "ちからこぶ", 12385, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("さるかに合", "これ、何と言う？", "", 6220, 12363, 4, 21512, 12395, 2, 4, 0, 6, 4, 12427, 6, "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", 2, "さるかに合戦", 12373, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("チンパンジ", "これ、何と言う？", "", 6221, 12497, 5, 12472, 12531, 2, 5, 0, 6, 5, 12531, 5, "よく親しまれた替え歌の歌詞。ある動物の名前が入る", 1, "♪サル、ゴリラ、チンパンジー", 12481, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アオイソメ", "これ、何と言う？", "", 6222, 12452, 5, 12513, 12477, 2, 5, 0, 6, 5, 12458, 5, "釣り人には釣り餌として非常におなじみの、ミミズのような虫", 3, "アオイソメ", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("鋼メンタル", "これ、何と言う？", "", 6223, 12531, 6, 12523, 12479, 2, 5, 0, 6, 5, 12513, 5, "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", 3, "鋼メンタル", 37628, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("マゾヒスト", "これ、何と言う？", "", 6224, 12498, 5, 12488, 12473, 1, 5, 6224, 6, 5, 12478, 5, "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", 3, "マゾヒスト", 12510, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("コアトルス", "これ、何と言う？", "", 6225, 12488, 5, 12473, 12523, 2, 5, 0, 6, 5, 12450, 5, "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", 3, "ケツァルコアトルス", 12467, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アナツバメ", "これ、何と言う？", "", 6226, 12484, 5, 12513, 12496, 2, 5, 0, 6, 5, 12490, 5, "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", 3, "アナツバメ", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("タンチョウ", "これ、何と言う？", "", 6227, 12481, 5, 12454, 12519, 2, 5, 0, 6, 5, 12531, 5, "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", 3, "タンチョウヅル", 12479, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ルリタテハ", "これ、何と言う？", "", 6228, 12479, 5, 12495, 12486, 2, 5, 0, 6, 5, 12522, 5, "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", 3, "ルリタテハ", 12523, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("プリンセス", "これ、何と言う？", "", 6229, 12531, 5, 12473, 12475, 1, 5, 6229, 6, 5, 12522, 5, "特定の相手の能力\nを高める権能を持\nった存在", 1, "プリンセスナイト", 12503, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("ネコむすめ", "このパネルの「プリコネ読み」は？", "", 6230, 12416, 5, 12417, 12377, 1, 5, 6230, 6, 4, 12467, 4, "", 3, "ネコむすめ", 12493, 4);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("絶滅危惧種", "これ、何と言う？", "", 6231, 21361, 6, 31278, 24807, 2, 6, 0, 6, 6, 28357, 6, "数が減少し、絶滅の危機に晒されている生物", 2, "絶滅危惧種", 32118, 6);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アブラゼミ", "これ、何と言う？", "", 6232, 12521, 5, 12511, 12476, 2, 5, 0, 6, 5, 12502, 5, "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", 1, "アブラゼミ", 12450, 5);
INSERT INTO `v1_487f587ae920d8f6a306e6b0b69bf6b3c64b3f11867b156c2727e6be162ffe2c` VALUES ("アキレス腱", "これ、何と言う？", "", 6233, 12524, 5, 33137, 12473, 2, 5, 0, 6, 5, 12461, 6, "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", 1, "アキレス腱", 12450, 5);
