INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1067, "エスケープ", 0, 12503, 12473, 1, 12540, 12465, "これ、何と言う？", 5, 12456, "エスケープルート", "", "登山において、予定を変更して下山するためのルートのこと", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1068, "クランポン", 0, 12531, 12521, 1, 12509, 12531, "これ、何と言う？", 5, 12463, "クランポン", "", "登山において、雪や氷の上を歩く際に靴に装備する金属の爪", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1070, "ハイキング", 0, 12464, 12452, 1, 12531, 12461, "これ、何と言う？", 5, 12495, "ハイキング", "", "軽微な装備で、自然や景観を楽しみながら歩くアクティビティ", 5, 2, 5, 1);
