INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1075, "バタフライ", 0, 12452, 12479, 1, 12521, 12501, "これ、何と言う？", 5, 12496, "バタフライ", "", "水泳で両腕を同時に前後に、両足を同時に上下に動かす泳ぎ方。動きがとある生き物に似ている", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1076, "カーリング", 0, 12464, 12540, 1, 12531, 12522, "これ、何と言う？", 5, 12459, "カーリング", "", "戦略性の高さから「氷上のチェス」とも呼ばれるウィンタースポーツ", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1077, "デカスロン", 0, 12531, 12459, 1, 12525, 12473, "これ、何と言う？", 5, 12487, "デカスロン", "", "「10種」の種目を行い、その合計点を競う陸上競技。「10」を意味する言葉が含まれる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 1078, "ともえなげ", 0, 12370, 12418, 1, 12394, 12360, "これ、何と言う？", 4, 12392, "ともえなげ", "", "柔道で、相手の下にもぐりこみ、後方にけり投げる技。その姿勢が、「とある漢字」に似ている", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1079, "ストライク", 0, 12463, 12488, 1, 12452, 12521, "これ、何と言う？", 5, 12473, "ストライク", "", "野球の判定の一つ。これが3回宣言されると三振となる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1080, "オフサイド", 0, 12489, 12501, 1, 12452, 12469, "これ、何と言う？", 5, 12458, "オフサイド", "", "サッカーで相手陣内での待ち伏せを禁止するために設けられたルール", 5, 2, 5, 1);
