INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1116, "ムンサルト", 0, 12488, 12531, 1, 12523, 12469, "これ、何と言う？", 5, 12512, "ムーンサルトプレス", "ムーンサルトプレス", "コーナー上からバック転でボディプレスをするプロレス技。軌道が月のような弧を描くことから", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1117, "ブルズアイ", 0, 12452, 12523, 1, 12450, 12474, "これ、何と言う？", 5, 12502, "ブルズアイ", "", "ダーツの的の真ん中にある二重丸のうち、内側の方。「インナーブル」や「ダブルブル」とも", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1120, "ドライバー", 0, 12540, 12521, 1, 12496, 12452, "これ、何と言う？", 5, 12489, "ドライバー", "", "ゴルフの第1打目に使われるクラブの一つ。大きな飛距離を出す際に、もっともよく選ばれる", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1121, "コルドゲム", 0, 12512, 12523, 1, 12466, 12489, "これ、何と言う？", 5, 12467, "コールドゲーム", "コールドゲーム", "野球の試合が、悪天候や、大きな点差がついたなどの事情により途中で打ち切り終了となること", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1122, "トルネード", 0, 12489, 12523, 1, 12540, 12493, "これ、何と言う？", 5, 12488, "トルネード投法", "", "野球の投球方法の一つ。大きく身体をひねって投げる動きが、まるで竜巻を思わせる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1123, "ムンウォク", 0, 12463, 12531, 1, 12457, 12454, "これ、何と言う？", 5, 12512, "ムーンウォーク", "ムーンウォーク", "ダンスのステップの一つ。前に歩いているように見せながら後ろに下がっていく不思議な動き", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 1127, "シールド戦", 0, 25126, 12540, 1, 12489, 12523, "これ、何と言う？", 5, 12471, "シールド戦", "", "対戦カードゲームで人気のルールの一つ。その場で開封したパックで即興デッキを作り勝負する", 5, 2, 5, 3);
