INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 1131, "うっちゃり", 0, 12426, 12387, 1, 12419, 12385, "これ、何と言う？", 4, 12358, "うっちゃり", "", "漢字では「打っ棄り」「打っ遣り」と書く、相撲の技・決まり手", 4, 2, 4, 3);
