INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1179, "ボルダリン", 0, 12531, 12523, 1, 12522, 12480, "これ、何と言う？", 5, 12508, "ボルダリング", "", "クライミングの一種に分類されるスポーツ。ロープなどを持たずほぼ身体一つで岩や壁面を登る", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1180, "グングニル", 0, 12523, 12531, 1, 12491, 12464, "これ、何と言う？", 5, 12464, "グングニル", "", "オーディンが所有する、必殺必中の槍。投げた後、自動的に手元に戻ってくると言われる", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1181, "エクスカリ", 0, 12522, 12463, 1, 12459, 12473, "これ、何と言う？", 5, 12456, "エクスカリバー", "", "「聖剣」と言えばこれ。と言っていいほど有名な架空の武器。選ばれた者のみ抜くことができる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 1182, "水泳自転車", 0, 36554, 27891, 1, 36578, 33258, "これ、何？", 6, 27700, "水泳⇒自転車⇒長距離走", "水泳⇒自転車⇒長距離走", "3種のスポーツを続けて行う「トライアスロン」、種目の順番の通りに並べると？（漢字で）", 6, 2, 6, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1183, "スプリット", 0, 12488, 12503, 1, 12483, 12522, "これ、何と言う？", 5, 12473, "スプリット", "", "ボウリング用語で、概略するとピンとピンの間が離れて残っている状態。倒すのが難しいとされる", 5, 2, 5, 2);
