INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1185, "タイブレク", 0, 12463, 12452, 1, 12524, 12502, "これ、何と言う？", 5, 12479, "タイブレーク", "タイブレーク", "テニスなどのスポーツで同点のままもつれ込んだ際、決着をつけるために適用される特別なルール", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 5, 1186, "いけポチャ", 0, 12515, 12369, 1, 12481, 12509, "これ、何と言う？", 5, 12356, "いけポチャ", "", "ゴルフでボールがコース内の池に落ちてしまうことの通称", 4, 2, 5, 1);
