INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1190, "サンドバグ", 0, 12464, 12531, 1, 12496, 12489, "これ、何と言う？", 5, 12469, "サンドバッグ", "サンドバッグ", "ボクシングなどでパンチのトレーニングに使う、砂の詰まった大きな袋", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1191, "フライング", 0, 12464, 12521, 1, 12531, 12452, "これ、何と言う？", 5, 12501, "フライングスタート", "", "陸上競技でスタートの際に、動いても良いとされるタイミングよりも先に動いてしまうルール違反", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1192, "トウループ", 0, 12503, 12454, 1, 12540, 12523, "これ、何と言う？", 5, 12488, "トウループ", "", "フィギュアスケートの技である「ループ」に関連して、これをつま先で踏み切った場合の呼び方", 5, 2, 5, 3);
