INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 1209, "けさがため", 0, 12417, 12373, 1, 12383, 12364, "これ、何と言う？", 4, 12369, "けさがため", "", "柔道の固め技の一つ。お坊さんが身につける、とある衣装から名付けられた", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 1210, "掛かり稽古", 0, 21476, 12363, 1, 31293, 12426, "これ、何と言う？", 4, 25499, "掛かり稽古", "", "剣道の練習法。ふた手に分かれて、一方がもう一方に対し、休憩をせずに繰り返しかかっていく", 4, 2, 6, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 1211, "クレー射撃", 0, 25731, 12524, 1, 23556, 12540, "これ、何と言う？", 5, 12463, "クレー射撃", "", "射撃競技の一つ。高速で空中に射出された皿を、ショットガンで撃ち壊す精度を競う", 5, 2, 6, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1212, "イエロカド", 0, 12489, 12456, 1, 12459, 12525, "これ、何と言う？", 5, 12452, "イエローカード", "イエローカード", "サッカーで悪質な反則に対して示される警告。これが2回出ると、さらに重いレッドカードとなる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1213, "フットサル", 0, 12523, 12483, 1, 12469, 12488, "これ、何と言う？", 5, 12501, "フットサル", "", "サッカーから派生したスポーツで、5人対5人形式のサッカー。室内でプレイされることも。", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 1214, "せおいなげ", 0, 12370, 12362, 1, 12394, 12356, "これ、何と言う？", 4, 12379, "せおいなげ", "", "柔道の花形の技としてよく知られる投げ技。相手を背負うような形で、肩越しに投げる", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 2, 1215, "ウルトラC", 0, 67, 12523, 1, 12521, 12488, "これ、何と言う？", 5, 12454, "ウルトラC", "", "最近では「秘密兵器」という意味でも使われるが、元々は体操競技で非常に難しい技を指す言葉", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1216, "カタパルト", 0, 12488, 12479, 1, 12523, 12497, "これ、何と言う？", 5, 12459, "カタパルト", "", "かつて使われた攻城兵器。てこの原理を利用して、遠くまで石を射出する", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 1217, "イージス艦", 0, 33382, 12540, 1, 12473, 12472, "これ、何と言う？", 5, 12452, "イージス艦", "", "高度な対空射撃設備を搭載した軍艦。神話の中でゼウスが娘に与えた盾の名前が由来", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1218, "ツマンセル", 0, 12523, 12510, 1, 12475, 12531, "これ、何と言う？", 5, 12484, "ツーマンセル", "ツーマンセル", "軍隊や警察において使われる言葉で、「二人一組」のこと", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1219, "ホロサイト", 0, 12488, 12525, 1, 12452, 12469, "これ、何と言う？", 5, 12507, "ホロサイト", "", "銃で狙いを定めるための「照準器」の種類の一つ。いわゆる「ホログラム」の技術を使っている", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1220, "サイレンサ", 0, 12469, 12452, 1, 12531, 12524, "これ、何と言う？", 5, 12469, "サイレンサー", "", "スパイ映画でもよく見る、銃の発砲音を抑えるために銃口につける装置。「サプレッサー」とも", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 5, 1221, "点バースト", 0, 12488, 12496, 1, 12473, 12540, "これ、何と言う？", 5, 28857, "3点バースト", "", "ライフルなどの仕組みで、一度引き金を引くたびに、銃弾が3発ずつ発射されるもの", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1222, "ホルスター", 0, 12540, 12523, 1, 12479, 12473, "これ、何と言う？", 5, 12507, "ホルスター", "", "拳銃を収めるために、主に腰に装備するケース。西部劇での決闘シーンなどでもよく目立つ", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 4, 1223, "ハロンぼう", 0, 12358, 12525, 1, 12412, 12531, "これ、何と言う？", 5, 12495, "ハロンぼう", "", "競馬で、ゴールから一定距離ごとに目印として置かれる棒。赤と白のシマシマ柄をしている", 5, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1224, "ステイヤー", 0, 12540, 12486, 1, 12516, 12452, "これ、何と言う？", 5, 12473, "ステイヤー", "", "競馬で、スタミナが多く長距離レースを得意とする馬のこと", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1225, "テンペスト", 1225, 12488, 12531, 1, 12473, 12506, "これ、何と言う？", 5, 12486, "テンペストブレード", "", "『ジータ』のユニオンバーストの名前", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1226, "フィジーク", 0, 12463, 12451, 1, 12540, 12472, "これ、何と言う？", 5, 12501, "フィジーク", "", "肉体美を競うコンテスト。よくボディビルと比較されるが、評価の観点や、対象の部位が異なる", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1227, "レスリング", 0, 12464, 12473, 1, 12531, 12522, "これ、何と言う？", 5, 12524, "レスリング", "", "「プロレス」の「レス」、何という言葉の略？", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1228, "ストレート", 0, 12488, 12488, 1, 12540, 12524, "これ、何と言う？", 5, 12473, "ストレート", "", "格闘技のパンチの種類の一つ。「ジャブ」と並んで重要となる、最も威力の出やすいもの", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1229, "ノクアウト", 0, 12488, 12463, 1, 12454, 12450, "これ、何と言う？", 5, 12494, "ノックアウト", "ノックアウト", "格闘技での勝敗判定の一つ。相手をダウンさせたまま、既定の時間が過ぎたとき", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1230, "テクニカル", 0, 12523, 12463, 1, 12459, 12491, "これ、何と言う？", 5, 12486, "テクニカル", "", "格闘技で、ただのノックアウトとは異なった特殊な判定である「TKO」。この「T」は何の略？", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1231, "ウェルター", 0, 12540, 12455, 1, 12479, 12523, "これ、何と言う？", 5, 12454, "ウェルター級", "", "ボクシングの階級で「ライト」などのつくものを省いて考えた場合、「ミドル級とライト級の間」は何？", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1232, "ブーメラン", 0, 12531, 12540, 1, 12521, 12513, "これ、何と言う？", 5, 12502, "ブーメラン", "", "狩猟のために生まれた武器。「くの字型」をしており、投げた後Uターンして手元に戻ってくる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1233, "ネクロマン", 0, 12531, 12463, 1, 12510, 12525, "これ、何と言う？", 5, 12493, "ネクロマンサー", "", "「死霊使い」とも呼ばれる、ファンタジーでもおなじみの存在。死者を蘇らせ操る術を持つ", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1234, "グラディウ", 0, 12454, 12521, 1, 12451, 12487, "これ、何と言う？", 5, 12464, "グラディウス", "", "かつての剣闘士（グラディエーター）が用いた、幅広の剣", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1235, "コロッセオ", 0, 12458, 12525, 1, 12475, 12483, "これ、何と言う？", 5, 12467, "コロッセオ", "", "「コロシアム」「コロセウム」とも表記される、非常によく知られた円形闘技場", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1236, "ノールック", 0, 12463, 12540, 1, 12483, 12523, "これ、何と言う？", 5, 12494, "ノールックパス", "", "球技スポーツで、そちらに目線を向けることなくパスをして、相手を出し抜くテクニックのこと", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1237, "スタンガン", 0, 12531, 12479, 1, 12460, 12531, "これ、何と言う？", 5, 12473, "スタンガン", "", "護身用や警官が持つ装備として知られた、手持ちサイズの武器。高圧の電撃で相手を無力化する", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 1238, "にょいぼう", 0, 12358, 12423, 1, 12412, 12356, "これ、何と言う？", 4, 12395, "にょいぼう", "", "西遊記の「孫悟空」が武器に使った、自由自在に拡大・縮小できる棒", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1239, "デスサイズ", 0, 12474, 12473, 1, 12452, 12469, "これ、何と言う？", 5, 12487, "デスサイズ", "", "「死神」が持つと言われる大きな鎌のこと", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 1240, "りゅうがん", 1240, 12435, 12421, 1, 12364, 12358, "これ、何と言う？", 4, 12426, "りゅうがん", "", "『ホマレ』が持つ能力で、発動中は遠く離れたものも見ることができる", 4, 1, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1241, "ドーピング", 0, 12464, 12540, 1, 12531, 12500, "これ、何と言う？", 5, 12489, "ドーピング", "", "スポーツにおいて、薬物などの禁止された手段を用いて、不当に能力を高めること", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1242, "スライダー", 0, 12540, 12521, 1, 12480, 12452, "これ、何と言う？", 5, 12473, "スライダー", "", "野球の投球における球種の一つ。文字通り、スルリと滑るような変化をすることから", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1243, "ツーシーム", 0, 12512, 12540, 1, 12540, 12471, "これ、何と言う？", 5, 12484, "ツーシーム", "", "野球の投球における球種の一つ。球が1回転する間に、縫い目が合計で2回通過することから", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1244, "アンパイア", 0, 12450, 12531, 1, 12452, 12497, "これ、何と言う？", 5, 12450, "アンパイア", "", "広義にはスポーツ全般の審判員を指すが、一般には特に野球の審判員を指すことが多い", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1245, "デイゲーム", 0, 12512, 12452, 1, 12540, 12466, "これ、何と言う？", 5, 12487, "ナイター⇔デイゲーム", "", "野球の試合の、開催形式の分類。時間帯によって、2つに分かれる", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1246, "ロスタイム", 0, 12512, 12473, 1, 12452, 12479, "これ、何と言う？", 5, 12525, "ロスタイム", "", "サッカーで「アディショナルタイム」と呼ばれるルールの、かつての呼び方", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1247, "センタリン", 0, 12531, 12531, 1, 12522, 12479, "これ、何と言う？", 5, 12475, "センタリング", "", "サッカーで、サイドから中央のゴール付近の味方へ大きなパスを出すこと", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1248, "オウンゴル", 0, 12523, 12454, 1, 12468, 12531, "これ、何と言う？", 5, 12458, "オウンゴール", "オウンゴール", "サッカーで、間違って自陣側のゴールにボールを入れてしまい、相手に得点が入ること", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1249, "フェイント", 0, 12488, 12455, 1, 12531, 12452, "これ、何と言う？", 5, 12501, "フェイント", "", "多くのスポーツで、相手の意表を突くためにわざと行う「ふり動作」のこと。「フェイク」とも", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 1250, "ごっつぁん", 0, 12435, 12387, 1, 12353, 12388, "これ、何と言う？", 4, 12372, "ごっつぁんです", "", "力士の間で、お礼を言うときに広く使われるあいさつ。昨今は相撲以外の一般層にも浸透している", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 5, 1251, "聖火リレー", 0, 12540, 28779, 1, 12524, 12522, "これ、何と言う？", 5, 32854, "聖火リレー", "", "オリンピックで、トーチに灯した火をランナーが次々と受け継いで、会場までリレーをする催し", 6, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1252, "ダイビング", 0, 12464, 12452, 1, 12531, 12499, "これ、何と言う？", 5, 12480, "ダイビングキャッチ", "", "野球の野手が、大きく頭から飛び込んで打球をキャッチするプレー。成功すると非常に盛り上がる", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1253, "フレミング", 0, 12464, 12524, 1, 12531, 12511, "これ、何と言う？", 5, 12501, "フレーミング", "フレーミング", "野球の捕手が、判定の際どいボールを、自分がうまく動くことで審判にストライクと判定させる技術", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1255, "ストライク", 1255, 12463, 12488, 1, 12452, 12521, "これ、何と言う？", 5, 12473, "プリンセスストライク", "", "『ペコリーヌ』のユニオンバースト", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 1256, "物理攻撃力", 0, 21147, 29702, 1, 25731, 25915, "これ、何と言う？", 6, 29289, "物理攻撃力", "", "プリコネRのバトルで、「物理攻撃で与えられるダメージ」に大きくかかわるステータス", 6, 1, 6, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 1257, "魔法攻撃力", 0, 21147, 27861, 1, 25731, 25915, "これ、何と言う？", 6, 39764, "魔法攻撃力", "", "プリコネRのバトルで、「魔法攻撃で与えられるダメージ」に大きくかかわるステータス", 6, 1, 6, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 1258, "物理防御力", 0, 21147, 29702, 1, 24481, 38450, "これ、何と言う？", 6, 29289, "物理防御力", "", "プリコネRのバトルで、「物理攻撃で受けるダメージ」に大きくかかわるステータス", 6, 1, 6, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 1259, "魔法防御力", 0, 21147, 27861, 1, 24481, 38450, "これ、何と言う？", 6, 39764, "魔法防御力", "", "プリコネRのバトルで、「魔法攻撃で受けるダメージ」に大きくかかわるステータス", 6, 1, 6, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1260, "メモリピス", 0, 12473, 12514, 1, 12500, 12522, "これ、何と言う？", 5, 12513, "メモリーピース", "メモリーピース", "プリコネRのバトルに関わる強化方法で、「才能開花」をするために必要なアイテム", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1261, "スラッシュ", 1261, 12517, 12521, 1, 12471, 12483, "これ、何と言う？", 5, 12473, "うさぎさんスラッシュ", "", "『ミミ』のユニオンバースト", 5, 1, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 1262, "ウルフェン", 1262, 12531, 12523, 1, 12455, 12501, "これ、何と言う？", 5, 12454, "ウルフェンバイト", "", "『マコト』のユニオンバースト", 5, 1, 5, 3);
