INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 2201, "デジャヴュ", 0, 12517, 12472, 2, 12532, 12515, "これ、何と言う？", 5, 12487, "デジャヴュ", "", "実際には体験したことがないのに、既に経験したことがあるように感じること。「既視感」とも", 5, 2, 5, 2);
