INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 4, 2217, "河童川流れ", 0, 12428, 31461, 2, 27969, 24029, "これ、何と言う？", 6, 27827, "河童の川流れ", "河童の川流れ", "どんなに上手な人でも、時には失敗すること。とある妖怪の名を使ったことわざ", 6, 2, 6, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 2224, "きんとうん", 0, 12435, 12435, 2, 12358, 12392, "これ、何と言う？", 4, 12365, "きんとうん", "", "「西遊記」に登場する孫悟空が乗る雲。仙人のみ乗ることが出来る", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 2227, "ゆりのはな", 0, 12394, 12426, 2, 12399, 12398, "これ、何と言う？", 4, 12422, "立てば芍薬、座れば牡丹、\n歩く姿はゆりのはな", "", "何をしていても美しく魅力的な振る舞いに見える人をあらわすフレーズ", 4, 2, 4, 3);
