INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 2229, "ぶっころす", 2229, 12377, 12387, 2, 12429, 12371, "これ、何と言う？", 4, 12406, "ぶっころすわよ！", "", "キャルの口癖の一つ。主に腹を立てたときに言うことが多いが、本気で言ってるわけではない", 4, 1, 4, 2);
