INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 2257, "うちはうち", 0, 12385, 12385, 2, 12358, 12399, "これ、何と言う？", 4, 12358, "よそはよそ、うちはうち", "", "「人と自分を比べて嫉妬するものではない」という言葉。だだをこねる子どもに対し言われがち", 4, 2, 4, 2);
