INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 2259, "はしわたる", 0, 12427, 12375, 2, 12383, 12431, "これ、何と言う？", 4, 12399, "このはし、わたるべからず", "このはし、わたるべからず", "「一休さん」のとんちで、意地悪な桔梗屋が店の前に立てた札に書かれていた言葉", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 4, 2260, "障子目あり", 0, 12426, 23376, 2, 12354, 30446, "これ、何と言う？", 6, 38556, "壁に耳あり 障子に目あり", "壁に耳あり 障子に目あり", "どこで誰が聞いているか分からないので、秘密というのは漏れるものだ。という意味のことわざ", 6, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 2261, "せいきまつ", 0, 12388, 12356, 2, 12414, 12365, "これ、何と言う？", 4, 12379, "せいきまつモノ", "", "フィクションで人気のあるジャンル。はるか未来、荒廃して無秩序になった世界が描かれる", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 2262, "異世界転生", 0, 29983, 19990, 2, 36578, 30028, "これ、何と言う？", 6, 30064, "異世界転生モノ", "", "近年フィクションで人気のジャンル。主人公が現代社会から異なる世界に飛ばされるのが始まり", 6, 2, 6, 2);
