INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3193, "えびすてん", 0, 12435, 12403, 3, 12390, 12377, "これ、何と言う？", 4, 12360, "えびすてん（ひらがなで）", "", "「七福神」のうち、漁業や商売を司るとされる神様。ニコニコ顔と、釣り竿と鯛を持った姿が有名", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (2, 5, 3194, "Eスポーツ", 0, 12484, 12473, 3, 12540, 12509, "これ、何と言う？", 5, 69, "Eスポーツ", "", "デジタルゲームをスポーツとして捉える、近年普及した呼称", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3195, "プリザーブ", 0, 12502, 12522, 3, 12540, 12470, "これ、何と言う？", 5, 12503, "プリザーブド・フラワー", "", "生花に特殊な加工を行い、観賞用に枯れないようにしたもの", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3196, "サバイバル", 0, 12523, 12496, 3, 12496, 12452, "これ、略さずに言うと？", 5, 12469, "サバゲー＝サバイバルゲーム", "", "", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3197, "ビギナーズ", 0, 12474, 12462, 3, 12540, 12490, "これ、何と言う？", 5, 12499, "ビギナーズラック", "", "賭けごとなどで、初心者がやけに運の良いヒキを持つこと", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3199, "うらせんけ", 0, 12369, 12425, 3, 12435, 12379, "これ、何と言う？", 4, 12358, "うらせんけ", "", "「三千家」と呼ばれる茶道の三流派、「表千家」と「武者小路千家」ともう一つは？", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3200, "りんしょう", 0, 12358, 12435, 3, 12423, 12375, "これ、何と言う？", 4, 12426, "りんしょう", "", "カエルのうたでおなじみの歌唱法。複数グループに分かれ、一定間隔で後を追いかけるように歌う", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3201, "アンチヒロ", 0, 12525, 12531, 3, 12498, 12481, "これ、何と言う？", 5, 12450, "アンチヒーロー", "アンチヒーロー", "ダークヒーローとも呼ばれるフィクションのキャラ像。非道徳的でありながら魅力的な要素も持つ", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 4, 3202, "読ま食べた", 0, 12383, 12414, 3, 12409, 39135, "この歌の歌詞は？", 6, 35501, "読まずに食べた", "読まずに食べた", "有名な童謡の歌い出し。♪白ヤギさんからお手紙着いた　黒ヤギさんたら…", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3204, "しおひがり", 0, 12426, 12362, 3, 12364, 12402, "これ、何と言う？", 4, 12375, "しおひがり", "", "干潟で、潮が引いているあいだに貝類や生き物をとって遊ぶこと。春の訪れとともに楽しまれる", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3205, "もみじがり", 0, 12426, 12415, 3, 12364, 12376, "これ、何と言う？", 4, 12418, "もみじがり", "", "紅葉の時期に野山に出かけ風景を楽しむこと。何かを採取するわけではなく、あくまで見て楽しむ", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3206, "ふくはうち", 0, 12385, 12367, 3, 12358, 12399, "これ、何と言う？", 4, 12405, "鬼は外、ふくはうち", "", "「節分」で鬼に豆を投げるときに言う、お決まりのかけ声", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3207, "こいのぼり", 0, 12426, 12356, 3, 12412, 12398, "これ、何と言う？", 4, 12371, "こいのぼり", "", "季節行事として5月5日前後に外に飾るもの。「♪屋根より高い～」という歌でもおなじみ", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3208, "ダーウンド", 0, 12489, 12540, 3, 12531, 12454, "これ、略さずに言うと？", 5, 12480, "アングラ＝アンダーグラウンド", "アングラ＝アンダーグラウンド", "", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3209, "ントマイム", 0, 12512, 12488, 3, 12452, 12510, "これ、何と言う？", 5, 12531, "パントマイム", "", "古くから親しまれる大道芸で、壁や階段などが、あたかも本当に存在するかのように振るまう", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 6, 3210, "ねずみ花火", 0, 28779, 12378, 3, 33457, 12415, "これ、何と言う？", 4, 12397, "ねずみ花火", "", "花火の一種。手には持たず、着火して地面に放つと火花を発しながら、ある動物のように動き回る", 4, 2, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3211, "アンダンテ", 0, 12486, 12531, 3, 12531, 12480, "これ、何と言う？", 5, 12450, "アンダンテ", "", "音楽用語で、「歩くような速さで」を意味する言葉", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3212, "オーシャン", 0, 12531, 12540, 3, 12515, 12471, "これ、何と言う？", 5, 12458, "レッドオーシャン", "", "ビジネス用語で、既に激しい競争状態にあるので新規参入には向いていない市場のこと", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3213, "ドンシャリ", 0, 12522, 12531, 3, 12515, 12471, "これ、何と言う？", 5, 12489, "ドンシャリ", "", "イヤホンなどについての俗語。低音がドンドン、高音がシャリシャリと強調されて聴こえるもの", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3214, "しゃみせん", 0, 12435, 12419, 3, 12379, 12415, "これ、何と言う？", 4, 12375, "しゃみせん", "", "非常によく知られた和楽器で、3本の弦を弾いて演奏する弦楽器。よく似た楽器に「三線」がある", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3215, "ストライプ", 0, 12503, 12488, 3, 12452, 12521, "これ、何と言う？", 5, 12473, "ストライプ柄", "", "しましま柄のこと。縦方向の縞のみを指すと誤解されがちだが、縦と横どちらも該当する", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3216, "サラウンド", 0, 12489, 12521, 3, 12531, 12454, "これ、何と言う？", 5, 12469, "サラウンド", "", "ホームシアターなどで自分を取り囲むようにスピーカーを置くことで音が立体的に聴こえる仕組み", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3217, "だんしょく", 0, 12367, 12435, 3, 12423, 12375, "これ、何と言う？", 4, 12384, "だんしょく系", "", "赤、オレンジ、黄色など、あたたかい印象を与える系統の色のこと", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3218, "マスキング", 0, 12464, 12473, 3, 12531, 12461, "これ、何と言う？", 5, 12510, "マスキングテープ", "", "模型やフィギュアを塗装する際などに、塗料をつけたくない箇所を覆うように使うテープ", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3219, "ピアニシモ", 0, 12514, 12450, 3, 12471, 12491, "これ、何と言う？", 5, 12500, "ピアニッシモ", "ピアニッシモ", "音楽用語で「弱く」は「ピアノ」、「やや弱く」は「メゾピアノ」、では「ごく弱く」は何と言う？", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3220, "ほんのむし", 0, 12375, 12435, 3, 12416, 12398, "これ、何と言う？", 4, 12411, "ほんのむし", "", "読書が好きでたまらない人を、まるで本を餌にして住みついている生き物かのように表した言葉", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3221, "クロッキー", 0, 12540, 12525, 3, 12461, 12483, "これ、何と言う？", 5, 12463, "クロッキー", "", "小中学校の美術教育でもよく用いられる、10分程度の短時間でモデルを素早く描くこと", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3222, "キュビズム", 0, 12512, 12517, 3, 12474, 12499, "これ、何と言う？", 5, 12461, "キュビズム", "", "「立体派」「立体主義」とも呼ばれる、美術の世界で起きた大きな運動", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3223, "フリプレイ", 0, 12452, 12522, 3, 12524, 12503, "これ、何と言う？", 5, 12501, "フリー・トゥ・プレイ", "フリー・トゥ・プレイ", "オンラインゲームで、「F2P」と略される言葉、正式には何と言う？", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3224, "ヨーソロー", 0, 12540, 12540, 3, 12525, 12477, "これ、何と言う？", 5, 12520, "ヨーソロー", "", "航海で、この舵取りのまま進行せよという合図。「宜しく候（よろしくそうろう）」から転じた", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3225, "ちくしょう", 0, 12358, 12367, 3, 12423, 12375, "これ、何と言う？", 4, 12385, "ちくしょう道", "", "「六道」と呼ばれる仏教用語。天道・人間道・修羅道・餓鬼道・地獄道、あと一つは？", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3226, "インフィニ", 0, 12491, 12531, 3, 12451, 12501, "これ、何と言う？", 5, 12452, "インフィニティ・プール", "", "プールのふちがそのまま海へ繋がって、境目が無く無限に続いて見えるよう設計された施設", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3227, "ロスカット", 0, 12488, 12473, 3, 12483, 12459, "これ、何と言う？", 5, 12525, "ロスカット", "", "一般には「損切り」と同じ意味で使われる経済用語。損失が拡大する前に投資対象を売却すること", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3228, "カウンター", 0, 12540, 12454, 3, 12479, 12531, "これ、略さずに言うと？", 5, 12459, "カンスト＝カウンターストップ", "", "", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3229, "ホールデム", 0, 12512, 12540, 3, 12487, 12523, "これ、何と言う？", 5, 12507, "テキサスホールデム", "", "ポーカーゲームの中で、最もポピュラーな種類のもの", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3230, "ゴーカート", 0, 12488, 12540, 3, 12540, 12459, "これ、何と言う？", 5, 12468, "ゴーカート", "", "遊園地で、特に子どもに大人気の遊具。免許が無くても乗れる、小型の一人乗り自動車", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3231, "グレージュ", 0, 12517, 12524, 3, 12472, 12540, "これ、何と言う？", 5, 12464, "グレージュ", "", "「グレー」と「ベージュ」の中間をとったような色のこと", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3232, "チルアウト", 0, 12488, 12523, 3, 12454, 12450, "これ、何と言う？", 5, 12481, "チルアウト", "", "元は音楽の一ジャンル。転じて「ゆったりする」の意味の言葉。縮めて「〇〇する」と言うことも", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3233, "じょやかね", 0, 12397, 12423, 3, 12363, 12420, "これ、何と言う？", 4, 12376, "じょやのかね", "じょやのかね", "年末年始の恒例行事。お寺の鐘を108回鳴らし、人間の煩悩をはらう", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 3234, "井戸端会議", 0, 35696, 25144, 3, 20250, 31471, "これ、何と言う？", 6, 20117, "井戸端会議", "", "水汲みに出かけたついでに、そこで集まった人たちと世間話や噂話をする習慣のこと", 6, 2, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3235, "ノリツコミ", 0, 12511, 12522, 3, 12467, 12484, "これ、何と言う？", 5, 12494, "ノリツッコミ", "ノリツッコミ", "お笑いのテクニック。ボケに即座につっこむのではなく、あえていったん同調してからつっこむ", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3237, "きんしょう", 0, 12358, 12435, 3, 12423, 12375, "これ、何と言う？", 4, 12365, "きんしょう", "", "将棋の駒の「銀将」が「成る」ことで、なんと言う駒に変わる？", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3238, "かんなづき", 0, 12365, 12435, 3, 12389, 12394, "これ、何と言う？", 4, 12363, "かんなづき", "", "6月は旧暦で「水無月」、では10月はなんと言う？", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3239, "チャルメラ", 0, 12521, 12515, 3, 12513, 12523, "これ、何と言う？", 5, 12481, "チャルメラ", "", "流し屋台のラーメン屋が吹いて歩く管楽器。この楽器といえばラーメンを思い浮かべる人も多い", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 3240, "前方後円墳", 0, 22707, 26041, 3, 20870, 24460, "これ、何と言う？", 6, 21069, "前方後円墳", "", "「古墳」の種類としてなじみの深い一つ。上から見ると、カギ穴のような形に見える", 6, 2, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3242, "リスポーン", 0, 12531, 12473, 3, 12540, 12509, "これ、何と言う？", 5, 12522, "リスポーン", "", "対戦ゲームなどで、倒されたプレイヤーが復活し、再度プレイできる状態になること", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 3243, "しちならべ", 0, 12409, 12385, 3, 12425, 12394, "これ、何と言う？", 4, 12375, "しちならべ", "", "トランプゲームの一種。はじめに「7」のカードを4枚並べ、隣り合う数字を交代で並べていく", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 3244, "ペンライト", 3244, 12488, 12531, 3, 12452, 12521, "これ、何と言う？", 5, 12506, "ペンライト", "", "カルミナサマーライブ会場のグッズエリアでゲットできるグッズの一つ", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 5, 3245, "会員カード", 3245, 12489, 21729, 3, 12540, 12459, "これ、何と言う？", 5, 20250, "会員カード", "", "カルミナサマーライブ会場のグッズエリアでゲットできるグッズの一つ", 6, 1, 5, 3);
