INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 4144, "エリクサー", 0, 12540, 12522, 4, 12469, 12463, "これ、何と言う？", 5, 12456, "エリクサー", "", "ファンタジー作品でもおなじみ、不老不死の霊薬。賢者の石から作られたとされる", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 4145, "ミステリー", 0, 12540, 12473, 4, 12522, 12486, "これ、何と言う？", 5, 12511, "ミステリーサークル", "", "畑などの穀物が円形に倒されて、模様が描かれたもの。宇宙人のしわざと言われることも", 5, 2, 5, 1);
