INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 4210, "ハニカム構", 0, 27083, 12491, 4, 12512, 12459, "これ、何と言う？", 5, 12495, "ハニカム構造", "", "ハチの巣のように六角形を敷き詰めた形のことで、非常に強度があるため工業でも広く応用される", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 4211, "ちゅうぞう", 0, 12358, 12421, 4, 12382, 12358, "これ、何と言う？", 4, 12385, "ちゅうぞう", "", "刀などの製法で、金属を直接叩いて作るのは「鍛造」。溶かした金属を鋳型に流し込んで作るのは？", 4, 2, 4, 3);
