INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5014, "オードブル", 0, 12523, 12540, 5, 12502, 12489, "これ、何と言う？", 5, 12458, "オードブル", "", "「作品外」という意味で、コース料理の最初に食欲をそそるための軽い前菜", 5, 2, 5, 2);
