INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 5025, "密林の果実", 5025, 23455, 26519, 5, 26524, 12398, "これ、何と言う？", 4, 23494, "密林の果実", "", "ダンジョン「密林の大樹」を制覇した証として美食殿が手にした料理、兼ギルドハウス家具", 6, 1, 6, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5026, "ひかりもの", 0, 12398, 12363, 5, 12418, 12426, "これ、何と言う？", 4, 12402, "ひかりもの", "", "寿司屋で使われる言葉。サバ・イワシ・サンマなど、背中が青く、腹が銀白色に光る魚のこと", 4, 2, 4, 2);
