INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5059, "ごりんがり", 0, 12426, 12426, 5, 12364, 12435, "これ、何と言う？", 4, 12372, "ごりんがり", "", "髪の毛を「２ミリメートル」ほどに短く丸刈りにした髪型", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5060, "ツブロック", 0, 12463, 12502, 5, 12483, 12525, "これ、何と言う？", 5, 12484, "ツーブロック", "ツーブロック", "髪型の一種。髪の毛を伸ばしてある部分と、短く刈り上げた部分の２つにきっぱり分かれている", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5061, "ピラティス", 0, 12473, 12521, 5, 12451, 12486, "これ、何と言う？", 5, 12500, "ピラティス", "", "マット上で体幹を鍛え、身体の機能を整えるエクササイズ。ヨガと近い要素もあるが別物である", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5062, "デグラッセ", 0, 12475, 12464, 5, 12483, 12521, "これ、何と言う？", 5, 12487, "デグラッセ", "", "洋食のテクニック。肉を焼いた鍋底に焦げ付いた旨味をワインでこそぎ取り、味付けに利用する", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5063, "グルタミン", 0, 12531, 12523, 5, 12511, 12479, "これ、何と言う？", 5, 12464, "グルタミン酸", "", "和食に欠かせない「旨味」のうち、三大旨味成分と呼ばれるのは、イノシン酸・グアニル酸と何？", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5064, "カフェイン", 0, 12531, 12501, 5, 12452, 12455, "これ、何と言う？", 5, 12459, "カフェイン", "", "一般にコーヒーを飲んで摂取することが多い、眠気覚ましの効果がある成分", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5065, "パルメザン", 0, 12531, 12523, 5, 12470, 12513, "これ、何と言う？", 5, 12497, "パルメザンチーズ", "", "「粉チーズ」と言うと、これを指すのが一般的。パスタなどの風味づけに使われることが多い", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5066, "しょっぱい", 0, 12356, 12423, 5, 12401, 12387, "これ、何と言う？", 4, 12375, "しょっぱい", "", "「甘味」なら「あまい」、「酸味」なら「すっぱい」。では「塩味」なら？", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5067, "ホアジャオ", 0, 12458, 12450, 5, 12515, 12472, "これ、何と読む？", 5, 12507, "花椒＝ホアジャオ", "", "スパイスの一つ。「かしょう」以外の読み方は？", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5068, "モンブラン", 0, 12531, 12531, 5, 12521, 12502, "これ、何と言う？", 5, 12514, "モンブラン", "", "ケーキの種類の一つ。「栗（クリ）」のクリームを、しぼるように飾り付ける", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5069, "セミダブル", 0, 12523, 12511, 5, 12502, 12480, "これ、何と言う？", 5, 12475, "セミダブル", "", "ベッドの大きさの種類。「シングル」と「ダブル」の間の大きさは？", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5070, "レモネード", 0, 12489, 12514, 5, 12540, 12493, "これ、何と言う？", 5, 12524, "レモネード", "", "レモン果汁に砂糖やシロップ、はちみつなどを加えて薄めた飲み物", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5071, "ユニトバス", 0, 12473, 12491, 5, 12496, 12488, "これ、何と言う？", 5, 12518, "ユニットバス", "ユニットバス", "一般には、浴槽とトイレと洗面台が一緒になったタイプのバスルームのこと", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5072, "バルコニー", 0, 12540, 12523, 5, 12491, 12467, "これ、何と言う？", 5, 12496, "バルコニー", "", "住宅の設備の一つ。ベランダと混同されるが、上に屋根が無く解放されているという点で異なる", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5073, "ようしょく", 0, 12367, 12358, 5, 12423, 12375, "これ、何と言う？", 4, 12424, "ようしょく", "", "マグロなどの食用の魚介で、「天然」の反対をなんと言う？", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5074, "デトックス", 0, 12473, 12488, 5, 12463, 12483, "これ、何と言う？", 5, 12487, "デトックス", "", "体内の老廃物や毒素を排出する健康法。食事やエクササイズ、マッサージなど、方法はさまざま", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5075, "てんぴぼし", 0, 12375, 12435, 5, 12412, 12404, "これ、何と言う？", 4, 12390, "てんぴぼし", "", "布団や洗濯物を直射日光の下で干すこと。紫外線の殺菌効果で雑菌の繁殖を抑えることができる", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5076, "こんにゃく", 0, 12367, 12435, 5, 12419, 12395, "これ、何と言う？", 4, 12371, "こんにゃく", "", "糸□□□□□、玉□□□□□、刺身□□□□□。共通して当てはめられる食材は？", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5077, "メタボリク", 0, 12463, 12479, 5, 12522, 12508, "これ、何と言う？", 5, 12513, "メタボリック・シンドローム", "メタボリック・シンドローム", "「メタボ」を略さずに言うと？", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5078, "ブーケトス", 0, 12473, 12540, 5, 12488, 12465, "これ、何と言う？", 5, 12502, "ブーケトス", "", "結婚式で花嫁が女性ゲストに向けて花束を投げること", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5079, "サーロイン", 0, 12531, 12540, 5, 12452, 12525, "これ、何と言う？", 5, 12469, "サーロイン", "", "牛肉の部位の呼び方の一種。背中から腰にかけてを指す。ステーキなどで好まれる", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5081, "カトラリー", 0, 12540, 12488, 5, 12522, 12521, "これ、何と言う？", 5, 12459, "カトラリー", "", "食卓用のナイフ、フォーク、スプーンなどの総称", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5082, "クオリティ", 0, 12451, 12458, 5, 12486, 12522, "これ、何と言う？", 5, 12463, "QOL＝クオリティオブライフ", "", "「生活の質」のことを表した略語", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5083, "デミグラス", 0, 12473, 12511, 5, 12521, 12464, "これ、何と言う？", 5, 12487, "デミグラスソース", "", "洋食のソースの一つ。ブラウンソースを煮詰めて出汁やワインなどで風味付けしたもの", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5084, "ベシャメル", 0, 12523, 12471, 5, 12513, 12515, "これ、何と言う？", 5, 12505, "ベシャメルソース", "", "洋食のソースの一つ。小麦粉とバターを熱しながら混ぜ、牛乳で溶かしてから味付けしたもの", 5, 2, 5, 3);
