INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5091, "かつらむき", 0, 12365, 12388, 5, 12416, 12425, "これ、何と言う？", 4, 12363, "かつらむき", "", "大根などの野菜を薄く長くむいていく事を何という？", 4, 2, 4, 2);
