INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5097, "カレライス", 0, 12473, 12524, 5, 12452, 12521, "これ、何？", 5, 12459, "カレーライス", "カレーライス", "戦隊モノのイエローの好物といえばどんな食べ物？", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5098, "キャンパス", 0, 12473, 12515, 5, 12497, 12531, "これ、何と言う？", 5, 12461, "キャンパス", "", "「○○○○○ライフ」などの言葉でもおなじみ。大学の敷地のことをなんと言う？", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5099, "ひゃっきん", 0, 12435, 12419, 5, 12365, 12387, "これ、何と言う？", 4, 12402, "ひゃっきん", "", "全ての商品が100円で統一されているタイプの生活用品店。一般に何と略す？", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5100, "かけふとん", 0, 12435, 12369, 5, 12392, 12405, "これ、何と言う？", 4, 12363, "敷き布団⇔かけふとん", "", "", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5101, "チャンプル", 2268, 12523, 12515, 5, 12503, 12531, "これ、何と言う？", 5, 12481, "ゴーヤーチャンプルー", "", "『カオリ』のキャラストーリーで作った料理。ゴーヤーの調理方法としても非常にポピュラー", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 5, 5102, "焼きそばパ", 5102, 12497, 12365, 5, 12400, 12381, "何を食べている場面？", 4, 28988, "焼きそばパン", "", "", 4, 1, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 5103, "マホマホ王", 2088, 29579, 12507, 5, 12507, 12510, "これ、何と言う？", 5, 12510, "マホマホ王国", "", "『マホ』いわく、彼女の故郷の土地。地図には載っていないが、実在している…らしい", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 6, 5104, "おせち料理", 5104, 29702, 12379, 5, 26009, 12385, "これ、何？", 4, 12362, "おせち料理", "", "シェフィが「どんな料理」\nの具材を集めに、おつ\nかいに出るストーリー？", 4, 1, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 5, 5106, "あさシャン", 0, 12531, 12373, 5, 12515, 12471, "これ、何と言う？", 5, 12354, "あさシャン", "", "毎日、朝の起きがけにシャンプーをする習慣、略してなんと言う？", 4, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5107, "ルドハウス", 5107, 12473, 12489, 5, 12454, 12495, "この場所の名前は？", 5, 12523, "美食殿のギルドハウス", "", "", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5108, "うらないし", 5108, 12375, 12425, 5, 12356, 12394, "これ、何？", 4, 12358, "うらないし", "", "霊能力で物事を見通すことができる『シノブ』は、普段どんな仕事を生業にしている？", 4, 1, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5109, "さんがにち", 0, 12385, 12435, 5, 12395, 12364, "これ、何と言う？", 4, 12373, "三が日＝さんがにち", "", "正月の1月1日～1月3日までの期間", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5110, "ダンジョン", 5110, 12531, 12531, 5, 12519, 12472, "これ、何と言う？", 5, 12480, "ダンジョンコイン", "", "ショップで様々なアイテムを購入するのに使うアイテム。ダンジョンをプレイすると手に入る", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5111, "ミクスベリ", 5111, 12522, 12463, 5, 12505, 12473, "これ、何と言う？", 5, 12511, "ミックスベリーケーキ", "ミックスベリーケーキ", "キャラとの絆Ptがアップするアイテム", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 5112, "クレープ屋", 5112, 23627, 12524, 5, 12503, 12540, "これは何？", 5, 12463, "クレープ屋", "", "『ラビリスタ』が店主を務めるのは、どんな飲食店？（とある洋菓子を販売している）", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5113, "おとしぶた", 0, 12383, 12392, 5, 12406, 12375, "これ、何と言う？", 4, 12362, "おとしぶた", "", "煮物料理で、鍋のフチに被せるフタではなく、鍋の中の食材に直接被せるようにするフタのこと", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5114, "キャッシュ", 0, 12517, 12515, 5, 12471, 12483, "これ、何と言う？", 5, 12461, "キャッシュレス決済", "", "買い物で、現金で直接決済するのではなく、電子マネー等を用いて決済する方法", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5115, "ウェルダン", 0, 12531, 12455, 5, 12480, 12523, "これ、何と言う？", 5, 12454, "ウェルダン", "", "ステーキの焼き加減。レア⇒ミディアム⇒□□□□□", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5116, "しちごさん", 0, 12435, 12385, 5, 12373, 12372, "これ、何と言う？", 4, 12375, "しちごさん", "", "子どもの成長を祈願し、3歳・5歳・7歳のときに着物を着飾って、寺社を参拝する習慣", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5117, "ソーセージ", 0, 12472, 12540, 5, 12540, 12475, "これ、何と言う？", 5, 12477, "ソーセージ", "", "肉を腸詰めにした加工食品のこと。「ウインナー」は、これのうちの一種と定義づけられている", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 5118, "一姫二太郎", 0, 37070, 23019, 5, 22826, 20108, "これ、何と言う？", 6, 19968, "一姫二太郎", "", "1人目の子どもは女の子、2人目は男の子が良い。という意味の言い伝え", 6, 2, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5119, "さんばいず", 0, 12378, 12435, 5, 12356, 12400, "これ、何と言う？", 4, 12373, "さんばいず", "", "酢・醤油・みりんを同じ比率で合わせた調味料", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5120, "レストラン", 0, 12531, 12473, 5, 12521, 12488, "これ、何と言う？", 5, 12524, "ゴーストレストラン", "", "実店舗を持たず、デリバリー専門として営業する飲食店のスタイル", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5121, "ローコスト", 0, 12488, 12540, 5, 12473, 12467, "これ、略さずに言うと？", 5, 12525, "LCC＝ローコストキャリア", "", "割安での航空券の提供をウリとしている、近年広く普及した形態の航空会社の通称", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 5122, "特定保健用", 0, 29992, 23450, 5, 20581, 20445, "これ、略さずに言うと？", 6, 29305, "トクホ＝特定保健用食品", "", "", 6, 2, 6, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5123, "マヌカハニ", 0, 12491, 12492, 5, 12495, 12459, "これ、何と言う？", 5, 12510, "マヌカハニー", "", "特定の種の花からのみ採れる希少なハチミツで、殺菌・消毒・抗炎症など多くの健康作用がある", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 5124, "アイガモ農", 0, 36786, 12452, 5, 12514, 12460, "これ、何と言う？", 5, 12450, "アイガモ農法", "", "米作りにおける、無農薬農法の一つ。とある鳥を田んぼに放ち、除草や害虫の駆除をさせる", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5125, "フォンデュ", 0, 12517, 12457, 5, 12487, 12531, "これ、何と言う？", 5, 12501, "フォンデュ", "", "串に刺したパン・野菜・フルーツなどの食材を、チーズやチョコなどにくぐらせて食べる料理", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5126, "バニラビン", 0, 12531, 12491, 5, 12499, 12521, "これ、何と言う？", 5, 12496, "バニラビーンズ", "バニラビーンズ", "バニラの風味の素となる植物。バニラアイスなどをよく見ると含まれている黒い粒の正体", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5127, "トゥンカロ", 0, 12525, 12453, 5, 12459, 12531, "これ、何と言う？", 5, 12488, "トゥンカロン", "", "「太ったマカロン」という意味のお菓子。普通のマカロンよりサイズがひと回り大きい", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5128, "フィッシュ", 0, 12517, 12451, 5, 12471, 12483, "これ、何と言う？", 5, 12501, "フィッシュ&チップス", "", "白身魚のフライにポテトフライを添えた非常にポピュラーな料理", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5129, "とうにゅう", 0, 12358, 12358, 5, 12421, 12395, "これ、何と言う？", 4, 12392, "とうにゅう", "", "大豆から作られる飲み物。大変体に良く、牛乳の代わりにする人も多い", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5130, "チョコレト", 0, 12488, 12519, 5, 12524, 12467, "これ、何と言う？", 5, 12481, "チョコレート", "チョコレート", "カカオ豆を原料とするポピュラーなお菓子。苦いものから甘いものまで種類はさまざま", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5131, "オブリジュ", 0, 12517, 12502, 5, 12472, 12522, "これ、何と言う？", 5, 12458, "ノブレス・オブリージュ", "ノブレス・オブリージュ", "「高貴な身分は社会的義務を伴う」の意味を持つ、かつて貴族階級から生まれた言葉", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 5132, "緑黄色野菜", 0, 33756, 40644, 5, 37326, 33394, "これ、何と言う？", 6, 32209, "緑黄色野菜", "", "ピーマンやカボチャなど、緑色や黄色の野菜の総称。定義上はカロテンを一定以上含む野菜を指す", 6, 2, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5133, "はんじゅく", 0, 12367, 12435, 5, 12421, 12376, "これ、何と言う？", 4, 12399, "はんじゅく卵", "", "ゆで卵の仲間で、黄身が完全に固まるまで火を通さず、あえてやわらかい状態を保った食べ物", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5134, "シュンギク", 0, 12463, 12517, 5, 12462, 12531, "これ、何と言う？", 5, 12471, "シュンギク", "", "鍋の具材としても人気の葉野菜。冬が旬の野菜だが、名前で「春の野菜」と誤解されることも", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5135, "バスタオル", 0, 12523, 12473, 5, 12458, 12479, "これ、何と言う？", 5, 12496, "バスタオル", "", "大きなサイズのタオルで、文字通り風呂上りに身体を乾かすのに使う", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5136, "ぼたんなべ", 0, 12409, 12383, 5, 12394, 12435, "これ、何と言う？", 4, 12412, "ぼたんなべ", "", "イノシシの肉を使った鍋料理の、「猪鍋」以外の呼び方。縮れた肉がとある花に似ていることから", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5137, "ンギスカン", 0, 12531, 12462, 5, 12459, 12473, "これ、何と言う？", 5, 12531, "ジンギスカン", "", "羊肉を使った料理で非常にポピュラーなもの。特殊な形の鉄板で、野菜と一緒に羊肉を焼く", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5138, "メゾネット", 0, 12488, 12478, 5, 12483, 12493, "これ、何と言う？", 5, 12513, "メゾネット式", "", "一般に、マンションやアパートでありながら、内部に階段があり2階構造になっている物件のこと", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5139, "リバシブル", 0, 12523, 12496, 5, 12502, 12471, "これ、何と言う？", 5, 12522, "リバーシブル", "リバーシブル", "裏返しでも着れるように作られている服のこと", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5140, "アイランド", 0, 12489, 12452, 5, 12531, 12521, "これ、何と言う？", 5, 12450, "アイランドキッチン", "", "普通の壁に向いた形のキッチンと異なり、島のように独立した形のキッチン", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 5, 5141, "海のミルク", 0, 12463, 12398, 5, 12523, 12511, "これ、何と言う？", 5, 28023, "海のミルク", "", "「牡蠣」の別名。乳白色をしており、また栄養バランスの良さがとある飲み物を思わせることから", 4, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5142, "しんきゅう", 0, 12358, 12435, 5, 12421, 12365, "これ、何と言う？", 4, 12375, "しんきゅう師", "", "「はり師」と「きゅう師」二つの資格を有する人の職業、何という？", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5143, "タコライス", 0, 12473, 12467, 5, 12452, 12521, "これ、何と言う？", 5, 12479, "タコライス", "", "「タコス」の具材を、生地で挟むのではなくご飯の上に乗せる形でアレンジした料理", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5144, "ワーキング", 0, 12464, 12540, 5, 12531, 12461, "これ、何と言う？", 5, 12527, "ワーキングホリデー", "", "若者が、他国で働いて資金を稼ぎながら、そこの文化に触れ長期休暇を過ごすという、公の制度", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5145, "チンジャオ", 0, 12458, 12531, 5, 12515, 12472, "これ、何と読む？", 5, 12481, "青椒肉絲＝チンジャオロース", "", "", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5146, "シズニング", 0, 12464, 12474, 5, 12531, 12491, "これ、何と言う？", 5, 12471, "シーズニング", "シーズニング", "鉄のフライパンが錆びないように行う「空焼き」と「油ならし」の2工程を合わせてこう呼ぶ", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5147, "ホイコーロ", 0, 12525, 12452, 5, 12540, 12467, "これ、何と読む？", 5, 12507, "回鍋肉＝ホイコーロ", "", "", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 5, 5148, "しらがネギ", 0, 12462, 12425, 5, 12493, 12364, "これ、何と言う？", 4, 12375, "しらがネギ", "", "ネギの白い部分を、まるで髪の毛のようにごく細くせん切りにしたもの。和食の飾りに使われる", 4, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5149, "モロヘイヤ", 0, 12516, 12525, 5, 12452, 12504, "これ、何と言う？", 5, 12514, "モロヘイヤ", "", "特別に栄養価が高いことから「野菜の王様」とも呼ばれる緑色の葉野菜", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5150, "スムージー", 0, 12540, 12512, 5, 12472, 12540, "これ、何と言う？", 5, 12473, "スムージー", "", "凍らせた果物や野菜などをミキサーにかけて作る飲み物で、朝食に好まれる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5151, "ルーティン", 0, 12531, 12540, 5, 12451, 12486, "これ、何と言う？", 5, 12523, "ナイトルーティン", "", "毎日の夜に行う、自分ならではの習慣のこと。近年ではタレントが動画に撮って発信するのが人気", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5152, "バンバンジ", 0, 12472, 12531, 5, 12531, 12496, "これ、何と読む？", 5, 12496, "棒棒鶏＝バンバンジー", "", "蒸した鶏肉をごまだれで味付けた中華料理。キュウリを添えることが多い", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5153, "いそべあげ", 0, 12370, 12381, 5, 12354, 12409, "これ、何と言う？", 4, 12356, "いそべあげ", "", "天ぷらの一種。「青のり」を混ぜた衣で、ちくわなどの練り物を揚げた食べ物", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5154, "おにぎりざ", 5154, 12374, 12395, 5, 12426, 12366, "このパネルの「プリコネ読み」は？", 4, 12362, "おにぎりざ", "", "", 4, 1, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5155, "ダージリン", 0, 12531, 12540, 5, 12522, 12472, "これ、何と言う？", 5, 12480, "ダージリン", "", "非常にポピュラーな紅茶の種類。「ウバ」「キーマン」に並び、「世界三大銘茶」と評される", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5156, "アルグレイ", 0, 12452, 12523, 5, 12524, 12464, "これ、何と言う？", 5, 12450, "アールグレイ", "アールグレイ", "ポピュラーな紅茶の一種で、お菓子作りの材料にも好まれる。伯爵という意味の言葉が含まれる", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5157, "ジンジャー", 0, 12540, 12531, 5, 12515, 12472, "これ、何と言う？", 5, 12472, "ジンジャーエール", "", "非常にポピュラーな、炭酸飲料の一種。ショウガの風味が特徴的", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 5158, "エンゲル係", 0, 20418, 12531, 5, 12523, 12466, "これ、何と言う？", 5, 12456, "エンゲル係数", "", "家庭の支出額のうち、「食費」が占める割合のこと", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 4, 5159, "プリンたい", 0, 12356, 12522, 5, 12383, 12531, "これ、何と言う？", 5, 12503, "プリンたい", "", "一般にビールやレバー、エビ、牡蠣などに多く含まれる成分。あまりとり過ぎると痛風の原因に", 5, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5160, "きぬどうふ", 0, 12405, 12396, 5, 12358, 12393, "これ、何と言う？", 4, 12365, "きぬどうふ", "", "豆腐の種類として特によく食べられる2種類。1つは「木綿豆腐」で、もう一つは？", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5161, "インフォム", 0, 12512, 12531, 5, 12457, 12501, "これ、何と言う？", 5, 12452, "インフォームド・コンセント", "インフォームド・コンセント", "患者が医師から十分な説明を受け、合意のうえで医療行為を受けること", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5162, "だんしゃり", 0, 12426, 12435, 5, 12419, 12375, "これ、何と言う？", 4, 12384, "だんしゃり", "", "元は「ヨガ」から生まれた習慣で、不要なものを捨て、物に執着せずに生きること", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 4, 5163, "スギかふん", 0, 12435, 12462, 5, 12405, 12363, "これ、何と言う？", 4, 12473, "スギかふん", "", "花粉症の原因として最も多いのは、どんな種類の花粉？", 5, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5164, "フレックス", 0, 12473, 12524, 5, 12463, 12483, "これ、何と言う？", 5, 12501, "フレックスタイム制", "", "固定の時間帯で働く従来の働き方とは違い、始業と終業の時間を自由に決められる労働制度", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5165, "きゅうかん", 0, 12435, 12421, 5, 12363, 12358, "これ、何と言う？", 4, 12365, "きゅうかん日", "", "アルコールをあまり毎日飲み過ぎず、肝臓を休める日を適度に設けることを推奨する言葉", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5166, "マヨネーズ", 0, 12474, 12520, 5, 12540, 12493, "これ、何と言う？", 5, 12510, "マヨネーズ", "", "卵・酢・油などから作られ、サラダなどにかけることが多い、定番の調味料", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5167, "ケチャップ", 0, 12503, 12481, 5, 12483, 12515, "これ、何と言う？", 5, 12465, "ケチャップ", "", "主にトマトなどから作られる、定番の赤い調味料。オムライスやフライドポテトなどによく合う", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5168, "マーガリン", 0, 12531, 12540, 5, 12522, 12460, "これ、何と言う？", 5, 12510, "マーガリン", "", "トーストなどに塗るのが定番の加工食品。バターの代替品として使われることも", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 5169, "砂糖塩酢醤", 0, 37284, 31958, 5, 37218, 22633, "これ、何と言う？", 6, 30722, "砂糖・塩・酢・醤油・味噌", "砂糖・塩・酢・醤油・味噌", "料理の「さしすせそ」、順番に何と言う？", 6, 2, 6, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5170, "ごこくまい", 0, 12356, 12371, 5, 12414, 12367, "これ、何と言う？", 4, 12372, "ごこくまい", "", "健康食品として知られるお米。米のほかに麦や粟など、合計5種類の穀物が合わさっている", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5171, "ビュッフェ", 0, 12455, 12517, 5, 12501, 12483, "これ、何と言う？", 5, 12499, "ビュッフェ", "", "ホテルなどで多い、セルフサービス形式での食事。「バイキング」と混同されることが少なくない", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5172, "たかのつめ", 0, 12417, 12363, 5, 12388, 12398, "これ、何と言う？", 4, 12383, "たかのつめ", "", "トウガラシの代表的な品種。名前は、とある鳥のかぎづめに似ていることから", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5173, "タラバガニ", 0, 12491, 12521, 5, 12460, 12496, "これ、何と言う？", 5, 12479, "タラバガニ", "", "高級食材としてよく知られるカニ。魚のタラが獲れる場所に生息していることが由来", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5174, "サクラエビ", 0, 12499, 12463, 5, 12456, 12521, "これ、何と言う？", 5, 12469, "サクラエビ", "", "食用として、かき揚げなどで好まれる小さなエビ。透き通ったピンク色の身体をしている", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5175, "さんきゅぱ", 0, 12401, 12435, 5, 12421, 12365, "これ、何と言う？", 4, 12373, "さんきゅっぱ", "さんきゅっぱ", "売り物によく設定される「398円」という値段を読みやすくした読み方", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5176, "かいばしら", 0, 12425, 12356, 5, 12375, 12400, "これ、何と言う？", 4, 12363, "かいばしら", "", "ホタテなどの二枚貝で、円柱の形をした大きな可食部の通称", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5177, "ファースト", 0, 12488, 12449, 5, 12473, 12540, "これ、何と言う？", 5, 12501, "ファーストバイト", "", "結婚式で、ケーキ入刀のあとに新郎新婦がお互いにケーキを食べさせあう演出", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5178, "いろなおし", 0, 12375, 12429, 5, 12362, 12394, "これ、何と言う？", 4, 12356, "おいろなおし", "", "結婚披露宴の途中で新郎新婦が離席し、衣装を変えること", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 4, 5179, "剪定ばさみ", 0, 12415, 23450, 5, 12373, 12400, "これ、何と言う？", 4, 21098, "剪定ばさみ", "", "庭木を切って整えるためのはさみで、普通のはさみなら切れない太い枝なども切ることができる", 6, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5180, "クーリング", 0, 12464, 12540, 5, 12531, 12522, "これ、何と言う？", 5, 12463, "クーリング・オフ", "", "訪問販売などで考える暇なく購入してしまったものを、一定期間中ならキャンセルできる制度", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5181, "ねこまんま", 0, 12414, 12371, 5, 12435, 12414, "これ、何と言う？", 4, 12397, "ねこまんま", "", "「猫にあげるような飯」のこと。削り節をかけるか、味噌汁をかけるか、定義は地域で分かれる", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 5, 5182, "おくション", 0, 12531, 12367, 5, 12519, 12471, "これ、何と言う？", 5, 12362, "おくション", "", "分譲マンションで、価格が1億円を超えるような特に高級なものを指す俗語", 4, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5183, "パーキング", 0, 12464, 12540, 5, 12531, 12461, "これ、何と言う？", 5, 12497, "パーキングエリア", "", "高速道路で、「SA」は「サービスエリア」。では「PA」は？", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 5, 5184, "細マッチョ", 0, 12519, 12510, 5, 12481, 12483, "これ、何と言う？", 5, 32048, "細マッチョ", "", "「ほどよく筋肉があり、脂肪は少なく引き締まった体型」を指す俗語。対義語は「ゴリマッチョ」", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5185, "ハンバーグ", 0, 12464, 12531, 5, 12540, 12496, "これ、何と言う？", 5, 12495, "ハンバーグ", "", "定番の肉料理。ひき肉をメインに、卵やパン粉、野菜などと一緒にこねて焼いたもの", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5186, "ウェルカム", 0, 12512, 12455, 5, 12459, 12523, "これ、何と言う？", 5, 12454, "ウェルカムボード", "", "結婚式やパーティなどで、ゲストを迎え入れるために入り口に置かれる案内板のこと", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5187, "てっかまき", 0, 12365, 12387, 5, 12414, 12363, "これ、何と言う？", 4, 12390, "てっかまき", "", "のり巻き寿司の一つで、マグロの赤身とわさびが巻かれたもの", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5188, "かっぱまき", 0, 12365, 12387, 5, 12414, 12401, "これ、何と言う？", 4, 12363, "かっぱまき", "", "のり巻き寿司の一つで、きゅうりが巻かれたもの", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5189, "おやこどん", 0, 12435, 12420, 5, 12393, 12371, "これ、何と言う？", 4, 12362, "おやこどん", "", "どんぶり物の料理の一つ。割下で甘く煮た鶏肉を、たまごでとじてご飯に乗せたもの", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5190, "ちらしずし", 0, 12375, 12425, 5, 12378, 12375, "これ、何と言う？", 4, 12385, "ちらしずし", "", "寿司の一種で、握ったり巻いたりしたものではなく、具材をご飯の上に散らすように盛るのが特徴", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5191, "にはちそば", 0, 12400, 12399, 5, 12381, 12385, "これ、何と言う？", 4, 12395, "にはちそば", "", "「そば」の分類の一つで、そば粉とうどん粉を２：８の配分で打ったそばのこと", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5192, "ストレミン", 0, 12531, 12488, 5, 12511, 12524, "これ、何と言う？", 5, 12473, "シュールストレミング", "", "「世界一臭い缶詰め」として、良くも悪くも有名な食べ物。塩漬けのニシンを発酵させて作る", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 5193, "子丑寅卯辰", 0, 36784, 19985, 5, 21359, 23493, "これ、何と言う？", 6, 23376, "子・丑・寅・卯・辰・巳・午・未・申……", "子・丑・寅・卯・辰・巳・午・未・申……", "「干支」を順番に並べると、最初にくる5つは何？", 6, 2, 6, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5194, "ふねんごみ", 0, 12415, 12397, 5, 12372, 12435, "これ、何と言う？", 4, 12405, "可燃ごみ⇔ふねんごみ", "", "", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5195, "ベドタウン", 0, 12531, 12489, 5, 12454, 12479, "これ、何と言う？", 5, 12505, "ベッドタウン", "ベッドタウン", "日中は大都市へ通勤し、夜だけ帰ってくるための場所として、その近郊に発展した街のこと", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5196, "カーシェア", 0, 12450, 12540, 5, 12455, 12471, "これ、何と言う？", 5, 12459, "カーシェア", "", "レンタカーに並び、近年急速に普及した貸し車サービス。短時間でも気軽に使えるのが長所", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5197, "シュウマイ", 0, 12452, 12517, 5, 12510, 12454, "これ、何と読む？", 5, 12471, "焼売＝シュウマイ", "", "豚のひき肉を皮で包んで蒸した、中華料理の一つ", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5198, "チャーハン", 0, 12531, 12515, 5, 12495, 12540, "この料理、何と読む？", 5, 12481, "炒飯＝チャーハン", "", "", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5199, "うるうどし", 0, 12375, 12427, 5, 12393, 12358, "これ、何と言う？", 4, 12358, "うるうどし", "", "太陽暦では実際の季節と暦とがわずかにズレるので、4年に一度、「366日」の年を設けること", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5200, "バスソルト", 0, 12488, 12473, 5, 12523, 12477, "これ、何と言う？", 5, 12496, "バスソルト", "", "入浴剤の一種で、ミネラルを多く含んだ塩系の成分を主として作られるものを特にこう呼ぶ", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 5201, "ばれいしょ", 0, 12423, 12428, 5, 12375, 12356, "これ、何と言う？", 4, 12400, "ばれいしょ", "", "「じゃがいも」の別の呼び名。一般には馴染みが薄いが行政などではこちらが好まれることも", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5202, "オクション", 0, 12531, 12463, 5, 12519, 12471, "これ、何と言う？", 5, 12458, "オークション", "オークション", "「競売」とも呼ばれる、物品の売買の方法の一つ。「入札」「落札」などの用語が使われる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5203, "シェーバー", 0, 12540, 12455, 5, 12496, 12540, "これ、何と言う？", 5, 12471, "シェーバー", "", "本来はカミソリ全般を指す言葉だが、一般には特に電動のひげ剃り機を指して使うことが多い", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 5204, "マネクリプ", 0, 12503, 12493, 5, 12522, 12463, "これ、何と言う？", 5, 12510, "マネークリップ", "マネークリップ", "紙幣やカードなどを挟んで持ち歩く道具で、財布と比べ気軽に携帯できるため使う人が増えている", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 5, 5205, "ちゅうトロ", 0, 12525, 12421, 5, 12488, 12358, "これ、何と言う？", 4, 12385, "ちゅうトロ", "", "お寿司のネタとしてよく知られるマグロの部位。脂のノリがほどよく、テッパンの人気を誇る", 4, 2, 5, 1);
