INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6004, "やべーやつ", 6004, 12388, 12409, 6, 12420, 12540, "これ、何と言う？", 4, 12420, "テレ女のやべーやつ", "", "ルーセント学院の男子生徒が、ユニの「ヤバさ」を指して言ったのではという疑惑のある言葉", 4, 1, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6005, "やべーやつ", 6005, 12388, 12409, 6, 12420, 12540, "これ、何と言う？", 4, 12420, "テレ女のやべーやつ", "", "ルーセント学院の男子生徒が、チエルの「ヤバさ」を指して言ったのではという疑惑のある言葉", 4, 1, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6006, "やべーやつ", 6006, 12388, 12409, 6, 12420, 12540, "これ、何と言う？", 4, 12420, "テレ女のやべーやつ", "", "ルーセント学院の男子生徒が、クロエの「ヤバさ」を指して言ったのではという疑惑のある言葉", 4, 1, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6007, "カモノハシ", 0, 12471, 12514, 6, 12495, 12494, "これ、何と言う？", 5, 12459, "カモノハシ", "", "ゴムのような柔らかいくちばし、水かき、哺乳類でありながら卵を産むなどの特徴をもつ動物", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6008, "フラミンゴ", 0, 12468, 12521, 6, 12531, 12511, "これ、何と言う？", 5, 12501, "フラミンゴ", "", "淡いピンク色の身体に、長い首と脚をもつ、片足立ちが特徴の鳥", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6009, "アルマジロ", 0, 12525, 12523, 6, 12472, 12510, "これ、何と言う？", 5, 12450, "アルマジロ", "", "「武装した小さなもの」という意味の哺乳類。硬い甲羅を持ち、ピンチのときは球状に丸まる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6010, "はくちょう", 0, 12358, 12367, 6, 12423, 12385, "これ、何と言う？", 4, 12399, "はくちょう", "", "童話「みにくいアヒルの子」で、アヒルの子は本当はどんな鳥だった？", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6012, "テナガザル", 0, 12523, 12490, 6, 12470, 12460, "これ、何と言う？", 5, 12486, "テナガザル", "", "猿の種類の一つ。その名の通り長い手を使って、樹上を軽やかに動く", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6013, "アライグマ", 0, 12510, 12521, 6, 12464, 12452, "これ、何と言う？", 5, 12450, "アライグマ", "", "哺乳類の一種。水に直接手を突っ込んで獲物を取る様子が「洗っている」ように見える", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6014, "ミドリガメ", 0, 12513, 12489, 6, 12460, 12522, "これ、何と言う？", 5, 12511, "ミドリガメ", "", "お祭りや縁日でも親しまれてきたカメの通称。幼い頃の甲羅の色に由来している", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6015, "カンガルー", 0, 12540, 12531, 6, 12523, 12460, "これ、何と言う？", 5, 12459, "カンガルー", "", "ジャンプが得意な、哺乳類の一種。お腹の袋で子育てをすることでよく知られる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6020, "キャッシュ", 6020, 12517, 12515, 6, 12471, 12483, "これ、何と言う？", 5, 12461, "クレジッタ・キャッシュ", "", "【リッチモンド商工会】のギルドマスターの『クレジッタ』。フルネームはなんと言う？", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 6021, "ドクロ親父", 6021, 29238, 12463, 6, 35242, 12525, "この人は誰？", 5, 12489, "ドクロ親父", "", "『シノブ』の父親の通称。過去の霊媒の仕事で、ドクロ姿になってしまったことから", 5, 1, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 4, 6022, "ドラちゃん", 6022, 12435, 12521, 6, 12419, 12385, "これ、何と言う？", 4, 12489, "ドラちゃん", "", "エルピス島で発見された生物。正しくは『ドラゴンもどき』だが、『イノリ』からの呼ばれ方は？", 5, 1, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 6, 6024, "すこすこ侍", 6024, 20365, 12371, 6, 12371, 12377, "この人は誰？", 4, 12377, "カルミナすこすこ侍", "", "チエルの、「カルミナファン」としての二つ名", 4, 1, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 6025, "疾風の冥姫", 3002, 23019, 39080, 6, 20901, 12398, "これ、何と言う？", 4, 30142, "疾風の冥姫", "", "『アンナ』の二つ名。後ろの2文字はそのままでは読まず、「ヘカーテ」と読むらしい", 6, 1, 6, 2);
