INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6044, "うわきぼし", 6044, 12375, 12431, 6, 12412, 12365, "これ、何と言う？", 4, 12358, "覚醒・うわきぼし", "", "イベント「七夕剣客旅情譚　天に流れる夏の恋」のボス", 4, 1, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6045, "トゥンヌス", 6045, 12473, 12453, 6, 12492, 12531, "これ、何と言う？", 5, 12488, "グレートトゥンヌス", "", "イベント「新春グルメプリンセス！　一投にかけた乙女たち」のボス", 5, 1, 5, 2);
