INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6047, "トリニティ", 6047, 12451, 12522, 6, 12486, 12491, "これ、何と言う？", 5, 12488, "アンチビースト・トリニティ", "", "イベント「魔法少女外伝　マジカル★ノワールズ！」のボス", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 5, 6048, "大人レディ", 6048, 12451, 20154, 6, 12487, 12524, "これ、何と言う？", 5, 22823, "大人のレディ", "大人のレディ", "『ミサキ』がどのような女性であるかを表した通称。背伸び感が可愛らしい", 6, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (2, 5, 6049, "Tレックス", 0, 12473, 12524, 6, 12463, 12483, "これ、何と言う？", 5, 84, "Tレックス", "", "『ティラノサウルス』の別の言い方", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6050, "ケラトプス", 0, 12473, 12521, 6, 12503, 12488, "これ、何と言う？", 5, 12465, "トリケラトプス", "", "3本の立派なツノが特徴の、有名な恐竜。名前の読み方は、「トリ」で区切るのが実は正しい", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6051, "ロケション", 0, 12531, 12465, 6, 12519, 12471, "これ、何と言う？", 5, 12525, "エコーロケーション", "エコーロケーション", "イルカやコウモリなどが持つ能力。超音波を発し、その反響によって周囲の様子を探る", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6052, "マングロブ", 0, 12502, 12531, 6, 12525, 12464, "これ、何と言う？", 5, 12510, "マングローブ", "マングローブ", "熱帯や亜熱帯の一部の水辺に生育する植物の総称で、非常にユニークな形の根っこを形成する", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 6053, "天然記念物", 0, 29289, 28982, 6, 24565, 35352, "これ、何と言う？", 6, 22825, "天然記念物", "", "学術的に貴重なものとして保護される、天然の動物・植物・地質・鉱物。アホウドリやトキなど", 6, 2, 6, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6054, "つちふまず", 0, 12378, 12385, 6, 12414, 12405, "これ、何と言う？", 4, 12388, "つちふまず", "", "人間の足の裏の、地面と接しないくぼんだ部分。人によっては無い場合もある", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6055, "ファントム", 5036, 12512, 12449, 6, 12488, 12531, "これは何？", 5, 12501, "ファントムキャッツ", "", "『タマキ』の裏の名前にして、恵まれない人たちを助ける、謎に包まれた義賊", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6056, "ワンアイド", 6056, 12489, 12531, 6, 12452, 12450, "これ、何と読む？", 5, 12527, "隻眼の赤い悪魔\n＝ワンアイドデビル", "", "裏社会で恐れられる『ミツキ』の二つ名。彼女の隻眼からは逃れられない", 5, 1, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6057, "コレクター", 6057, 12540, 12524, 6, 12479, 12463, "これ、何と読む？", 5, 12467, "蒐集家（＝コレクター）", "", "『ナナカ』の二つ名。さまざまな魔法を探し集めていることに由来する", 5, 1, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6058, "レグナント", 6058, 12488, 12464, 6, 12531, 12490, "これ、何と読む？", 5, 12524, "変貌大妃（＝メタモルレグナント）", "", "『ネネカ』の、『七冠』としての二つ名。あらゆるものに姿を変化させられることから", 5, 1, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6059, "ケンカバカ", 6059, 12459, 12531, 6, 12496, 12459, "これ、何と言う？", 5, 12465, "脳筋ケンカバカ", "", "『イノリ』が『カヤ』に対して、ムキになったり、おちょくったりするときの呼び方", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6060, "サラブレド", 0, 12489, 12521, 6, 12524, 12502, "これ、何と言う？", 5, 12469, "サラブレッド", "サラブレッド", "レースのために、長い年月をかけて改良された類の馬。家柄の良い人のたとえ表現でもおなじみ", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6061, "スナギツネ", 0, 12493, 12490, 6, 12484, 12462, "これ、何と言う？", 5, 12473, "チベットスナギツネ", "", "キツネの一種。冷ややかで悟りを開いたような表情に見えることで近年人気を博している", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6062, "シラカンス", 0, 12473, 12521, 6, 12531, 12459, "これ、何と言う？", 5, 12471, "シーラカンス", "シーラカンス", "数億年も前から変わらない姿のまま現存しているため、「生きた化石」と呼ばれる深海魚", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6063, "ヘラクレス", 0, 12473, 12521, 6, 12524, 12463, "これ、何と言う？", 5, 12504, "ヘラクレスオオカブト", "", "カブトムシの中で最大のサイズを誇る種として、子どもたちからも絶大な人気を誇る昆虫", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6064, "アブラムシ", 0, 12471, 12502, 6, 12512, 12521, "これ、何と言う？", 5, 12450, "アブラムシ", "", "昔の子どもたちがこの虫の体液を髪に塗り、テカテカさせて遊んでいたことに由来する虫の名前", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 4, 6065, "泣きぼくろ", 0, 12429, 12365, 6, 12367, 12412, "これ、何と言う？", 4, 27875, "泣きぼくろ", "", "目のすぐ下のあたりにあるほくろの通称", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6066, "シルババク", 0, 12463, 12523, 6, 12496, 12496, "これ、何と言う？", 5, 12471, "シルバーバック", "シルバーバック", "年を重ねたオスのゴリラの背中の毛が白くなる現象。群れの中のボス個体によく見られる", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6067, "カメレオン", 0, 12531, 12513, 6, 12458, 12524, "これ、何と言う？", 5, 12459, "カメレオン", "", "身体の色を自在に変化させることでおなじみの爬虫類。演技の幅が広い俳優や声優のたとえにも", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6068, "オニヤンマ", 0, 12510, 12491, 6, 12531, 12516, "これ、何と言う？", 5, 12458, "オニヤンマ", "", "非常に大きなトンボの一種。「鬼」の服装とよく似た、黒と黄色のシマシマ模様が特徴的", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6069, "グルミング", 0, 12464, 12523, 6, 12531, 12511, "これ、何と言う？", 5, 12464, "グルーミング", "グルーミング", "猫などの動物の習性として知られる「毛づくろい」。英語でなんと言う？", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6070, "マングース", 0, 12473, 12531, 6, 12540, 12464, "これ、何と言う？", 5, 12510, "マングース", "", "一般に「ハブ」の天敵というイメージを持たれている、体長数十センチほどの哺乳類", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6071, "ギンヤンマ", 0, 12510, 12531, 6, 12531, 12516, "これ、何と言う？", 5, 12462, "ギンヤンマ", "", "トンボの一種。身体は黄緑色だが、よく見るとキレイな銀色の模様があることからこう呼ばれる", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6072, "カモミール", 0, 12523, 12514, 6, 12540, 12511, "これ、何と言う？", 5, 12459, "カモミール", "", "「カミツレ」とも呼ばれるハーブの一種。りんごのような香りがあり、お茶としてよく好まれる", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6073, "ホタルイカ", 0, 12459, 12479, 6, 12452, 12523, "これ、何と言う？", 5, 12507, "ホタルイカ", "", "指先ほどのサイズの小さなイカ。青白く発光する性質があり、また食用としても好まれる", 5, 2, 5, 2);
