INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6161, "かまいたち", 0, 12385, 12414, 6, 12383, 12356, "これ、何と言う？", 4, 12363, "かまいたち", "", "つむじ風に乗って現れ、人を切りつける妖怪。痛みは無く、血も出ず、傷だけが残ると言われる", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6162, "フェンリル", 0, 12523, 12455, 6, 12522, 12531, "これ、何と言う？", 5, 12501, "フェンリル", "", "神話に登場する巨大な狼。ラグナロクではオーディンを丸呑みにしたとされる", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6164, "やばいです", 1255, 12377, 12400, 2, 12391, 12356, "これ、何と言う？", 4, 12420, "やばいですね☆", "", "ペコリーヌの口癖の一つ。良い意味でも悪い意味でもヤバイことが起きたときに言うことが多い", 4, 1, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6165, "あるじさま", 6165, 12414, 12427, 6, 12373, 12376, "左のキャラは右のキャラを何と呼ぶ？", 4, 12354, "あるじさま", "", "", 4, 1, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6166, "クソメガネ", 6166, 12493, 12477, 6, 12460, 12513, "左のキャラは右のキャラを何と呼ぶ？", 5, 12463, "クソメガネ", "", "", 5, 1, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6167, "フレーメン", 0, 12531, 12524, 6, 12513, 12540, "これ、何と言う？", 5, 12501, "フレーメン反応", "", "猫などの動物が強い匂いを嗅いだときに、目や口を開いて独特な表情をする現象", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6168, "メガロドン", 0, 12531, 12460, 6, 12489, 12525, "これ、何と言う？", 5, 12513, "メガロドン", "", "太古の海に存在していた、史上最大のサメ", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6169, "いときりば", 0, 12400, 12392, 6, 12426, 12365, "これ、何と言う？", 4, 12356, "いときりば", "", "「犬歯」の通称として、「八重歯」以外になじみ深いもの。糸が切れるほど尖っているのが由来", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6170, "カネション", 0, 12531, 12493, 6, 12519, 12471, "これ、何と言う？", 5, 12459, "カーネーション", "カーネーション", "「母の日」の贈り物としても親しまれる花。赤やピンクの鮮やかな花びらを持つ", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6171, "メイヨシノ", 0, 12494, 12452, 6, 12471, 12520, "これ、何と言う？", 5, 12513, "ソメイヨシノ", "", "桜の木の種類として非常にポピュラーな一つ。一般に桜のイメージとして最も浸透している", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6172, "デライオン", 0, 12531, 12521, 6, 12458, 12452, "これ、何と言う？", 5, 12487, "ダンデライオン", "", "「タンポポ」の英名。とある哺乳類の動物に由来している", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6173, "せいりゅう", 0, 12358, 12356, 6, 12421, 12426, "これ、何と言う？", 4, 12379, "せいりゅう", "", "「四聖獣」と呼ばれる生き物、朱雀・白虎・玄武、あとは何？（ひらがなで）", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 4, 6174, "座敷わらし", 0, 12375, 25975, 6, 12425, 12431, "これ、何と言う？", 4, 24231, "座敷わらし", "", "小さな子どもの姿で、座敷や土間に現れる妖怪。遭遇した者には幸運が舞い込むとされる", 6, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6175, "マンチカン", 0, 12531, 12531, 6, 12459, 12481, "これ、何と言う？", 5, 12510, "マンチカン", "", "猫の種類の一つ。「小さい」「子ども」という意味を持ち、小柄で脚が短いのが特徴", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6176, "デュラハン", 0, 12531, 12517, 6, 12495, 12521, "これ、何と言う？", 5, 12487, "デュラハン", "", "伝承に登場する、首の無い騎士", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6177, "オッドアイ", 0, 12452, 12483, 6, 12450, 12489, "これ、何と言う？", 5, 12458, "オッドアイ", "", "両目でそれぞれ瞳の色が異なること。いろいろな動物に見られるが、特に猫に多い", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 4, 6178, "猫じゃらし", 0, 12375, 12376, 6, 12425, 12419, "これ、何と言う？", 4, 29483, "猫じゃらし", "", "「エノコログサ」という草の通称。猫の目の前で振ると興味を示し、よくじゃれつくことから", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6179, "ドラキュラ", 0, 12521, 12521, 6, 12517, 12461, "これ、何と言う？", 5, 12489, "ドラキュラ", "", "非常によく知られた怪物。獲物の血を吸うのが特徴で、十字架やニンニクが弱点とされる", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6180, "ンコロガシ", 0, 12471, 12467, 6, 12460, 12525, "これ、何と言う？", 5, 12531, "フンコロガシ", "", "動物のフンをボール状にして、コロコロ転がすという変わった習性を持つ昆虫", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6181, "ラグドール", 0, 12523, 12464, 6, 12540, 12489, "これ、何と言う？", 5, 12521, "ラグドール", "", "「ぬいぐるみ」という意味を持つ、猫の種類。抱っこが好きで、毛並みがフサフサなことから", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6182, "エキゾチク", 0, 12463, 12461, 6, 12481, 12478, "これ、何と言う？", 5, 12456, "エキゾチック・ショートヘア", "エキゾチック・ショートヘア", "猫の種類の一つ。鼻が低く、ムスッとしたような愛嬌のある顔立ちが特徴的", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6183, "んしょばと", 0, 12392, 12375, 6, 12400, 12423, "これ、何と言う？", 4, 12435, "でんしょばと", "", "ハトの帰巣本能を使って遠くに手紙を届けること。通信技術が発達する前は、広く活用された", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6184, "ホホケキョ", 0, 12519, 12507, 6, 12461, 12465, "これ、何と言う？", 5, 12507, "ホーホケキョ", "ホーホケキョ", "「ウグイス」の鳴き声を文字に起こした表現。春の訪れの象徴としてもなじみ深い", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6185, "ホトトギス", 0, 12473, 12488, 6, 12462, 12488, "これ、何と言う？", 5, 12507, "ホトトギス", "", "武将の性格を比喩した「鳴かぬなら～～」の俳句に使われている鳥の名前は？", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6186, "ヘビイチゴ", 0, 12468, 12499, 6, 12481, 12452, "これ、何と言う？", 5, 12504, "ヘビイチゴ", "", "「ヘビがいそうな場所に生える」ことに由来する、イチゴに似た植物。食用には向かない", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 4, 6187, "松ぼっくり", 0, 12426, 12412, 6, 12367, 12387, "これ、何と言う？", 4, 26494, "松ぼっくり", "", "松かさとも呼ばれる、概略すると松の木の果実の部分。クリスマスツリーの飾りにもよく使われる", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6188, "アゾールド", 6188, 12489, 12478, 6, 12523, 12540, "このキャラの名前は？", 5, 12450, "アゾールド", "", "", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 4, 6189, "ズナちょむ", 6189, 12416, 12490, 6, 12423, 12385, "左のキャラは右のキャラを何と呼ぶ？", 4, 12474, "ズナちょむ", "", "", 5, 1, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6190, "フォールド", 0, 12489, 12457, 6, 12523, 12540, "これ、何と言う？", 5, 12501, "スコティッシュフォールド", "", "猫の種類の一つ。垂れた耳が特徴的", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6191, "ゴールデン", 0, 12531, 12540, 6, 12487, 12523, "これ、何と言う？", 5, 12468, "ゴールデンレトリーバー", "", "非常にポピュラーな種の犬で、フサフサとした黄色系の体毛と、大きな体が特徴", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6192, "ドッグラン", 0, 12531, 12483, 6, 12521, 12464, "これ、何と言う？", 5, 12489, "ドッグラン", "", "家庭犬がリードをつけずに自由に走り回れるように設けられた、フェンスで囲われた広いスペース", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6193, "ラブラドル", 0, 12523, 12502, 6, 12489, 12521, "これ、何と言う？", 5, 12521, "ラブラドールレトリーバー", "ラブラドールレトリーバー", "レトリーバー種としてゴールデンレトリーバーに並び有名な犬。盲導犬・警察犬としても活躍する", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6194, "マーキング", 0, 12464, 12540, 6, 12531, 12461, "これ、何と言う？", 5, 12510, "マーキング", "", "動物が自分の体臭や尿などを残すことで、そこが自分のなわばりであるとアピールする習性", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6195, "たいこ持ち", 0, 12385, 12356, 6, 25345, 12371, "これ、何と言う？", 4, 12383, "たいこ持ち", "", "しきりに偉い人のご機嫌をとるような人間を、ある楽器の名前を使ってなんと呼ぶ？", 4, 2, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6196, "キリギリス", 0, 12473, 12522, 6, 12522, 12462, "これ、何と言う？", 5, 12461, "キリギリス", "", "有名な童話の中で、「アリ」と対比して、なまけ者として描かれている昆虫", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6197, "クルマエビ", 0, 12499, 12523, 6, 12456, 12510, "これ、何と言う？", 5, 12463, "クルマエビ", "", "エビの一種。身体にクッキリとシマシマがあり、丸まった姿が車のホイールに見える", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6198, "ダイショウ", 0, 12454, 12452, 6, 12519, 12471, "これ、何と言う？", 5, 12480, "アオダイショウ", "", "ヘビの一種で、身体は青緑色。由来は諸説あるが「ヘビの中でも特に大物の風格がある」ことなど", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6199, "しそちょう", 0, 12358, 12381, 6, 12423, 12385, "これ、何と言う？", 4, 12375, "しそちょう", "", "太古の時代に存在した鳥で、その名の通り、鳥類の始まりとなった鳥とされている", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6200, "アウストラ", 0, 12521, 12454, 6, 12488, 12473, "これ、何と言う？", 5, 12450, "アウストラロピテクス", "", "「最古の人類」とされている種類の猿人", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6201, "キュウカン", 0, 12531, 12517, 6, 12459, 12454, "これ、何と言う？", 5, 12461, "キュウカンチョウ", "", "ペットとしても親しまれる手乗りサイズの鳥で、人の声をマネして鳴くのが上手い", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6202, "オットセイ", 0, 12452, 12483, 6, 12475, 12488, "これ、何と言う？", 5, 12458, "オットセイ", "", "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6203, "アジアゾウ", 0, 12454, 12472, 6, 12478, 12450, "これ、何と言う？", 5, 12450, "アジアゾウ", "", "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6204, "ベニシジミ", 0, 12511, 12491, 6, 12472, 12471, "これ、何と言う？", 5, 12505, "ベニシジミ", "", "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6205, "ジュウサン", 0, 12531, 12517, 6, 12469, 12454, "これ、何と言う？", 5, 12472, "ジュウサンホシテントウ", "", "テントウムシの一種。身体に13個の点々模様がある", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6206, "ニジュウヤ", 0, 12516, 12472, 6, 12454, 12517, "これ、何と言う？", 5, 12491, "ニジュウヤホシテントウ", "", "テントウムシの一種。身体に28個の点々模様がある", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6207, "アニサキス", 0, 12473, 12491, 6, 12461, 12469, "これ、何と言う？", 5, 12450, "アニサキス", "", "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6208, "わたりどり", 0, 12426, 12383, 6, 12393, 12426, "これ、何と言う？", 4, 12431, "わたりどり", "", "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", 4, 2, 4, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6209, "フンボルト", 0, 12488, 12531, 6, 12523, 12508, "これ、何と言う？", 5, 12501, "フンボルトペンギン", "", "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6210, "エボシドリ", 0, 12522, 12508, 6, 12489, 12471, "これ、何と言う？", 5, 12456, "エボシドリ", "", "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6211, "ネザランド", 0, 12489, 12470, 6, 12531, 12521, "これ、何と言う？", 5, 12493, "ネザーランドドワーフ", "ネザーランドドワーフ", "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6212, "ホーランド", 0, 12489, 12540, 6, 12531, 12521, "これ、何と言う？", 5, 12507, "ホーランドロップ", "", "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6213, "フェレット", 0, 12488, 12455, 6, 12483, 12524, "これ、何と言う？", 5, 12501, "フェレット", "", "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6214, "マントヒヒ", 0, 12498, 12531, 6, 12498, 12488, "これ、何と言う？", 5, 12510, "マントヒヒ", "", "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6215, "シロナガス", 0, 12473, 12525, 6, 12460, 12490, "これ、何と言う？", 5, 12471, "シロナガスクジラ", "", "最も大きい動物としてよく知られる種類のクジラ", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6216, "シロイルカ", 0, 12459, 12525, 6, 12523, 12452, "これ、何と言う？", 5, 12471, "シロイルカ", "", "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6217, "しろうさぎ", 0, 12366, 12429, 6, 12373, 12358, "これ、何と言う？", 4, 12375, "いなばのしろうさぎ", "", "神話に登場する、有名な動物", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6218, "スコピオン", 0, 12531, 12467, 6, 12458, 12500, "これ、何と言う？", 5, 12473, "さそり＝スコーピオン", "さそり＝スコーピオン", "カタカナでの呼び方は？", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6219, "ちからこぶ", 0, 12406, 12363, 6, 12371, 12425, "これ、何と言う？", 4, 12385, "ちからこぶ", "", "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 6, 6220, "さるかに合", 0, 21512, 12427, 6, 12395, 12363, "これ、何と言う？", 4, 12373, "さるかに合戦", "", "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6221, "チンパンジ", 0, 12472, 12531, 6, 12531, 12497, "これ、何と言う？", 5, 12481, "♪サル、ゴリラ、チンパンジー", "", "よく親しまれた替え歌の歌詞。ある動物の名前が入る", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6222, "アオイソメ", 0, 12513, 12458, 6, 12477, 12452, "これ、何と言う？", 5, 12450, "アオイソメ", "", "釣り人には釣り餌として非常におなじみの、ミミズのような虫", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 5, 6223, "鋼メンタル", 0, 12523, 12513, 6, 12479, 12531, "これ、何と言う？", 5, 37628, "鋼メンタル", "", "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6224, "マゾヒスト", 6224, 12488, 12478, 6, 12473, 12498, "これ、何と言う？", 5, 12510, "マゾヒスト", "", "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", 5, 1, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6225, "コアトルス", 0, 12473, 12450, 6, 12523, 12488, "これ、何と言う？", 5, 12467, "ケツァルコアトルス", "", "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6226, "アナツバメ", 0, 12513, 12490, 6, 12496, 12484, "これ、何と言う？", 5, 12450, "アナツバメ", "", "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6227, "タンチョウ", 0, 12454, 12531, 6, 12519, 12481, "これ、何と言う？", 5, 12479, "タンチョウヅル", "", "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6228, "ルリタテハ", 0, 12495, 12522, 6, 12486, 12479, "これ、何と言う？", 5, 12523, "ルリタテハ", "", "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6229, "プリンセス", 6229, 12473, 12522, 6, 12475, 12531, "これ、何と言う？", 5, 12503, "プリンセスナイト", "", "特定の相手の能力\nを高める権能を持\nった存在", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 4, 6230, "ネコむすめ", 6230, 12417, 12467, 6, 12377, 12416, "このパネルの「プリコネ読み」は？", 4, 12493, "ネコむすめ", "", "", 5, 1, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (6, 6, 6231, "絶滅危惧種", 0, 31278, 28357, 6, 24807, 21361, "これ、何と言う？", 6, 32118, "絶滅危惧種", "", "数が減少し、絶滅の危機に晒されている生物", 6, 2, 6, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6232, "アブラゼミ", 0, 12511, 12502, 6, 12476, 12521, "これ、何と言う？", 5, 12450, "アブラゼミ", "", "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 6, 6233, "アキレス腱", 0, 33137, 12461, 6, 12473, 12524, "これ、何と言う？", 5, 12450, "アキレス腱", "", "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6234, "ナルシスト", 6234, 12488, 12523, 6, 12473, 12471, "これ、何と言う？", 5, 12490, "ナルシスト", "", "自己愛が非常に強く、自分に陶酔した人、英語にすると？", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6235, "だんちょう", 6235, 12358, 12435, 6, 12423, 12385, "左のキャラは右のキャラを何と呼ぶ？", 4, 12384, "だんちょう", "", "", 4, 1, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6236, "いいなずけ", 0, 12369, 12356, 6, 12378, 12394, "これ、何と読む？", 4, 12356, "許嫁＝いいなずけ", "", "若いうちから結婚が約束された相手のこと", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6237, "やたがらす", 0, 12377, 12383, 6, 12425, 12364, "これ、何と言う？", 4, 12420, "やたがらす", "", "「古事記」などに登場する、三本足の神聖なカラス", 4, 2, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6238, "さごじょう", 0, 12358, 12372, 6, 12423, 12376, "これ、何と言う？", 4, 12373, "さごじょう", "", "「西遊記」で孫悟空と行動を共にする、河童の姿のキャラクター", 4, 2, 4, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6239, "ディーラー", 0, 12540, 12451, 6, 12521, 12540, "これ、何と言う？", 5, 12487, "ディーラー", "", "カジノで、ルーレットを回したりカードを配ったりとゲームの進行を行う役目", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6240, "エンプレス", 0, 12473, 12531, 6, 12524, 12503, "これ、何と言う？", 5, 12456, "エンプレス", "", "「皇帝」は「エンペラー」。では「女帝」は？", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6241, "ガンスミス", 0, 12473, 12531, 6, 12511, 12473, "これ、何と言う？", 5, 12460, "ガンスミス", "", "銃のメンテナンスを職業として行う人のこと", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6242, "ハイビスカ", 0, 12459, 12452, 6, 12473, 12499, "これ、何と言う？", 5, 12495, "ハイビスカス", "", "熱帯の地域を象徴する花で、カオリの故郷でも親しまれる。赤や黄色の鮮やかな花びらをつける", 5, 1, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6243, "オウィスプ", 0, 12503, 12454, 6, 12473, 12451, "これ、何と言う？", 5, 12458, "ウィル・オ・ウィスプ", "ウィル・オ・ウィスプ", "「ひとだま」と呼ばれる超常現象、英語で言うと？", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6244, "ニシキゴイ", 0, 12452, 12471, 6, 12468, 12461, "これ、何と言う？", 5, 12491, "ニシキゴイ", "", "魚の「鯉」の中でも観賞用として好まれる種で、高級な織物の柄によく似ていることが由来", 5, 2, 5, 2);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6245, "レサパンダ", 0, 12480, 12469, 6, 12531, 12497, "これ、何と言う？", 5, 12524, "レッサーパンダ", "レッサーパンダ", "「小さなパンダ」が由来の、動物園の人気者", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6246, "アナコンダ", 0, 12480, 12490, 6, 12531, 12467, "これ、何と言う？", 5, 12450, "アナコンダ", "", "ヘビの一種で、大きなものでは数メートルもある。映画で恐ろしいヘビとして描かれることが多い", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6247, "タランチュ", 0, 12517, 12521, 6, 12481, 12531, "これ、何と言う？", 5, 12479, "タランチュラ", "", "「毒グモの代名詞」としてよく知られる大型のクモ。しかし、ペットとして好む人も多い", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6248, "スイギュウ", 0, 12454, 12452, 6, 12517, 12462, "これ、何と言う？", 5, 12473, "スイギュウ", "", "「牛」の一種で、地域によって家畜としてもなじみ深い動物。その名の通り、水辺を好む", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6249, "ハムスター", 0, 12540, 12512, 6, 12479, 12473, "これ、何と言う？", 5, 12495, "ハムスター", "", "ネズミの仲間にあたる動物で、ヒマワリの種などを口いっぱいに頬張る姿が象徴的", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6250, "ハリネズミ", 0, 12511, 12522, 6, 12474, 12493, "これ、何と言う？", 5, 12495, "ハリネズミ", "", "身を守るためのトゲトゲの体毛が非常に特徴的な動物。愛好家も多く、英名は「ヘッジホッグ」", 5, 2, 5, 1);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6251, "ヒョウモン", 0, 12531, 12519, 6, 12514, 12454, "これ、何と言う？", 5, 12498, "ヒョウモンダコ", "", "猛毒を持つタコ。蛍光色のヒョウ柄の模様が、恐ろしさを助長している", 5, 2, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 5, 6252, "カメレオン", 6252, 12531, 12513, 6, 12458, 12524, "これ、何と言う？", 5, 12459, "カメレオン", "", "ネネカといつも一緒にいる動物", 5, 1, 5, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (5, 4, 6253, "アイちゃん", 6048, 12435, 12452, 6, 12419, 12385, "これ、何と言う？", 4, 12450, "アイちゃん", "", "『ミサキ』がいつも一緒にいる、生きた杖の名前", 5, 1, 4, 3);
INSERT INTO `v1_49981dd7c4366db722ffd2384446c12a29452c17646a13d5303a932ca952f03d` VALUES (4, 4, 6254, "にくきゅう", 0, 12358, 12367, 6, 12421, 12365, "これ、何と言う？", 4, 12395, "にくきゅう", "", "ネコ目の動物の足にある、ぷにぷにとやわらかい部分の通称", 4, 2, 4, 1);
