INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1001, 2, 0, 0, 1, 1, 1.4, 0, "ヒヨリでっす♪\n元気いっぱいがんばるよ♪", 0, "お！\nあそこに困ってそうな人発見！", "春咲 ひより", "ねぇねぇ、騎士クン\n手伝ってあげようよ～");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1002, 2, 0, 0, 1, 1, 1.4, 0, "みんなみたいに\nわたしも強くならなきゃ", 0, "大切な人を\n守れるようになりたい…って", "草野 優衣", "わ、わたし、\nなに言ってるんだろ\nあはは…");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1003, 2, 0, 0, 1, 1, 1.4, 0, "フェンシングを始めてみては？", 0, "キミに必要な集中力と感性が", "士条 怜", "少しは磨かれると思いますよ。");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1004, 2, 0, 0, 1, 1, 1.5, 0, "ねぇ、にいちゃん！", 0, "今日はなにして遊ぶ？", "穂高 みそぎ", "みそぎはねぇ、\nかくれんぼやりたい！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1006, 2, 0, 0, 1, 1, 1.4, 0, "風宮あかりです", 0, "ねぇ…", "風宮 あかり", "私といっしょに…\nいかない？");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1007, 2, 0, 0, 1, 1, 1.5, 0, "出雲宮子なのー", 0, "プリンたべたいのー", "出雲 宮子", "食べ物の恨みは怖いのー");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1008, 2, 0, 0, 1, 1, 1.4, 0, "ボクは虹村雪。", 0, "キミもボクの美貌に\n吸い寄せられたんだね。", "虹村 雪", "いいよ。\n見られることは運命さ…");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1009, 2, 0, 0, 1, 1, 1.4, 0, "フッ…聞いて後悔するがいい！", 0, "我が真名は\nアンネローゼ・フォン・シュテッヒパルム！", "柊杏奈", "人呼んで「疾風の冥姫」!!");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1010, 2, 0, 0, 1, 1, 1.5, 0, "マホマホ王国のプリンセス、\nまほ姫どす", 0, "うさぎさん、\n運命の王子はんに巡りあわせてくれて", "姫宮 真歩", "ほんまおおきにやわ～♪");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1011, 2, 0, 0, 1, 1, 1.4, 0, "衣之咲璃乃です！", 0, "生き別れたお兄ちゃんを探して\n三千世界！", "衣之咲 璃乃", "ここで会ったが\n100年目～！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1012, 2, 0, 0, 1, 1, 1.4, 0, "私はハツネ、\n結構強いんだよ。\nきらーん☆", 0, "ち、超能力って…\n何のことかな～？", "柏崎 初音", "…って、お願い！\n誰にも言わないでおいて～！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1016, 2, 0, 0, 1, 1, 1.4, 0, "ちょす！\n美波鈴奈だよ～♪", 0, "一応カリスマ読モJKやってまっす！", "美波 鈴奈", "ヒデサイまぢ\nGF（グッドフィーリング）～♪");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1017, 2, 0, 0, 1, 1, 1.4, 0, "はいたーい。\n喜屋武香織さー。", 0, "東京は遊園地みたいなところさー", "喜屋武 香織", "でも、沖縄もとってもいいとこさー");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1018, 2, 0, 0, 1, 1, 1.4, 0, "支倉伊緒です。", 0, "先生って呼ばれるのは\nくすぐったいから", "支倉 伊緒", "イオちゃんって呼んでね。");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1020, 2, 0, 0, 1, 1, 1.5, 0, "ふえ…？\nミミ、むずかしいこと\nよくわかんない…", 0, "あ！/\お兄ちゃ～ん\nまってよ～", "茜 ミミ", "ミミをおいてかないでぇ～");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1021, 2, 0, 0, 1, 1, 1.5, 0, "あ…あの…えっと…\n栗…林…くるみ…です……", 0, "あの……\nふぇ……", "栗林 くるみ", "ふぇぇぇぇぇん……");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1022, 2, 0, 0, 1, 1, 1.4, 0, "風宮よりです。\nあああああ！", 0, "こんな私に貴重な時間を\n割いていただき、", "風宮 より", "なんてありがとうございます！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1023, 2, 0, 0, 1, 1, 1.5, 0, "私、アヤネ！\nぷうきちと一緒についてってあげるね！", 0, "おにいちゃんどこいくの？\nえ？", "北条 綾音", "私こっち行きたいー\nね、早く早くー");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1025, 2, 0, 0, 1, 1, 1.4, 0, "わっ…わっ…私、\n天野すずめといいます！", 0, "ふ、不束者ですが\nどうぞ末永く…って、", "天野 すずめ", "これて何か違う…\nあああすみません！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1027, 2, 0, 0, 1, 1, 1.4, 0, "……クスクス…私は\n…倉石恵理子……", 0, "あなたは…運命の……\n伴侶……", "倉石 恵理子", "離しませんわ………\n絶対に…!!");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1028, 2, 0, 0, 1, 1, 1.4, 0, "佐々木咲恋よ。\nねえ、世の中に無駄なことが\n多すぎると思わない？", 0, "その無駄を省くことができれば、\nもっと余裕のある暮らしができるっ！", "佐々木 咲恋", "…そうでしょ？？");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1029, 2, 0, 0, 1, 1, 1.4, 0, "桜井望だよ！\nよろしくねっ", 0, "ぇっと…\nキミ、私のこと知らないの?!", "桜井 望", "あはは、\n私もまだまだだなぁ～");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1030, 2, 0, 0, 1, 1, 1.4, 0, "デケデケデンっ！", 0, "初めまして、\n私はニノン・ジュベール申すデス！", "ニノン・ジュベール", "ショーグン、\nワタシと一緒に天下統一デース！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1031, 2, 0, 0, 1, 1, 1.4, 0, "上喜しのぶです。\n手元のドクロが父です。", 0, "でもって私の隣にいるのが……\nえ、見えない？", "上喜 しのぶ", "おかしいですね……");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1033, 2, 0, 0, 1, 1, 1.5, 0, "目指すはビッグな\nお笑い芸人だっぺ！", 0, "けんども\nそれには足りないモンが…", "野戸まひる", "そう、\nオラのボケに突っ込める相方が…！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1034, 2, 0, 0, 1, 1, 1.4, 0, "あの…私…綾瀬ゆかりです…\nあの…ごめんなさい！", 0, "ちょっと緊張しちゃって…", "綾瀬 ゆかり", "ちょ\nちょっと一杯飲んできます…！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1036, 2, 0, 0, 1, 1, 1.5, 0, "氷川鏡華…です", 0, "知らない人とは話しちゃいけないので", "氷川 鏡華", "これ以上お話しすることは…\nないです！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1038, 2, 0, 0, 1, 1, 1.4, 0, "柏崎…栞です。", 0, "……あ、アラーム。", "柏崎 栞", "私体が弱いから、\n30分に一度休憩しないと\nいけないんです。");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1040, 2, 0, 0, 1, 1, 1.4, 0, "ふ、双葉碧です。", 0, "私、一人も友達がいなくて……", "双葉 碧", "野に咲く花が、\n唯一の話相手です。");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1042, 2, 0, 0, 1, 1, 1.4, 0, "三角千歌です。", 0, "私の歌声で、\n皆さんが幸せになってくれたらいいなって…", "三角 千歌", "そう思っています。");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1043, 2, 0, 0, 1, 1, 1.4, 0, "安芸真琴だ。", 0, "あぁ？\nお前のそいつの仲間か？", "安芸 真琴", "う、疑ってすまなかったな…");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1044, 2, 0, 0, 1, 1, 1.4, 0, "わらわこそは夜を総べる者！", 0, "生けとし生ける全ての者よ！", "イリヤ・オーンスタイン", "わらわの偉大な力に\nひれ伏すがいい！\nはっはっは！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1045, 2, 0, 0, 1, 1, 1.4, 0, "そこで騎士は妖精を乱暴に……", 0, "ぐふふふふ……はっ！", "遠見 空花", "ｌク、クウカに何かご用ですか!?");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1046, 2, 0, 0, 1, 1, 1.4, 0, "宮坂たまきにゃ♪\n猫はいいにゃよ？", 0, "自由気ままで♪\n寝て遊んで、食べてケンカして…", "宮坂たまき", "う～ん…\n猫ってホントに最高にゃ～♪");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1048, 2, 0, 0, 1, 1, 1.4, 0, "大神美冬よ！", 0, "それでは早速クエストに…", "大神 美冬", "…と思ったら\nバイトの時間だ！\nまたあとでね！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1049, 2, 0, 0, 1, 1, 1.4, 0, "星野静流だよ！", 0, "クエストもバトルも\n掃除も洗濯も", "星野静流", "お姉ちゃんがぜ～んぶ\nやってあげるからね！");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1050, 2, 0, 0, 1, 1, 1.5, 0, "アタシは玉泉美咲。", 0, "見ての通りの\nセクシー美少女で", "玉泉美咲", "学校では誰もが憧れる\nアイドル的な存在ってやつよ♪");
INSERT INTO `v1_4afa4c7e01488c9dbd6be00e20fbb8d45154ab706de84e7cc60ae73663eb5e53` VALUES (0, 1, 1052, 2, 0, 0, 1, 1, 1.4, 0, "？？？", 0, "？？？？", "リマ", "？？？？");
