INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 100, 10.85, 1, 7, 100, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 101, 11.15, 1, 2, 101, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 102, 6.4, 7, 5, 102, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 103, 0.0, 3, 3, 103, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 104, 2.43, 2, 8, 104, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 105, 9.08, 4, 3, 105, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 106, 0.0, 6, 6, 106, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 107, 10.6, 3, 8, 107, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 108, 7.54, 6, 4, 108, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 109, 11.5, 2, 7, 109, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 110, 8.83, 2, 1, 110, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 111, 6.68, 1, 2, 111, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 112, 8.8, 6, 2, 112, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 113, 8.9, 2, 1, 113, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 114, 3.7, 2, 5, 114, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "2030/12/17 14:59:59", "エリコ", 0.0, 0.0, 129901);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 200, 7.6, 1, 2, 200, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 201, 2.25, 2, 1, 201, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 202, 3.28, 2, 1, 202, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 203, 2.98, 4, 1, 203, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 204, 3.0, 4, 1, 204, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 205, 10.3, 1, 2, 205, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 206, 2.93, 6, 1, 206, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 207, 9.3, 3, 1, 207, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 208, 6.9, 4, 1, 208, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 209, 0.0, 2, 2, 209, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 210, 5.95, 1, 2, 210, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 211, 6.73, 2, 1, 211, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 212, 0.0, 2, 2, 212, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 213, 6.65, 1, 2, 213, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (10142, 24, "2024/03/31 12:00:00", 214, 3.05, 6, 1, 214, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "2030/12/17 14:59:59", "マコト", 0.0, 0.0, 129801);
