INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (100, 10.85, 0.0, 24, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 7, "2024/03/31 12:00:00", 10142, 100, 1);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (101, 11.15, 0.0, 24, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 2, "2024/03/31 12:00:00", 10142, 101, 1);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (102, 6.4, 0.0, 24, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 5, "2024/03/31 12:00:00", 10142, 102, 7);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (103, 0.0, 0.0, 24, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 3, "2024/03/31 12:00:00", 10142, 103, 3);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (104, 2.43, 0.0, 24, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 8, "2024/03/31 12:00:00", 10142, 104, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (105, 9.08, 0.0, 24, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 3, "2024/03/31 12:00:00", 10142, 105, 4);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (106, 0.0, 0.0, 24, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 6, "2024/03/31 12:00:00", 10142, 106, 6);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (107, 10.6, 0.0, 24, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 8, "2024/03/31 12:00:00", 10142, 107, 3);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (108, 7.54, 0.0, 24, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 4, "2024/03/31 12:00:00", 10142, 108, 6);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (109, 11.5, 0.0, 24, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 7, "2024/03/31 12:00:00", 10142, 109, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (110, 8.83, 0.0, 24, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 1, "2024/03/31 12:00:00", 10142, 110, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (111, 6.68, 0.0, 24, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 2, "2024/03/31 12:00:00", 10142, 111, 1);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (112, 8.8, 0.0, 24, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 2, "2024/03/31 12:00:00", 10142, 112, 6);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (113, 8.9, 0.0, 24, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 1, "2024/03/31 12:00:00", 10142, 113, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (114, 3.7, 0.0, 24, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "エリコ", "2030/12/17 14:59:59", 129901, 0.0, 5, "2024/03/31 12:00:00", 10142, 114, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (200, 7.6, 0.0, 24, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 2, "2024/03/31 12:00:00", 10142, 200, 1);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (201, 2.25, 0.0, 24, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 201, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (202, 3.28, 0.0, 24, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 202, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (203, 2.98, 0.0, 24, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 203, 4);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (204, 3.0, 0.0, 24, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 204, 4);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (205, 10.3, 0.0, 24, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 2, "2024/03/31 12:00:00", 10142, 205, 1);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (206, 2.93, 0.0, 24, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 206, 6);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (207, 9.3, 0.0, 24, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 207, 3);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (208, 6.9, 0.0, 24, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 208, 4);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (209, 0.0, 0.0, 24, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 2, "2024/03/31 12:00:00", 10142, 209, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (210, 5.95, 0.0, 24, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 2, "2024/03/31 12:00:00", 10142, 210, 1);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (211, 6.73, 0.0, 24, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 211, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (212, 0.0, 0.0, 24, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 2, "2024/03/31 12:00:00", 10142, 212, 2);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (213, 6.65, 0.0, 24, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 2, "2024/03/31 12:00:00", 10142, 213, 1);
INSERT INTO `v1_4b3ee6df46443df31f545e871d056256a02d93e57a36db2d3d654f90560601df` VALUES (214, 3.05, 0.0, 24, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "マコト", "2030/12/17 14:59:59", 129801, 0.0, 1, "2024/03/31 12:00:00", 10142, 214, 6);
