INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (4.0, 1, 250400, 0, "トドドン", "【物理】【防御】\n身体も態度も巨漢な北海のドン。\n纏う厚い脂肪が敵からの攻撃を押止める。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 0, 0, 0, 270, 1, 55, 250400);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.3, 11, 250500, 0, "サンドワーム", "【物理】【遠距離】\n砂地の地中で獲物を待ち、\n一気に大顎で仕留めるムカデの魔物\n\n・後ろから１番目に移動して近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 0, 0, 0, 270, 1, 410, 250500);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (0.5, 0, 250600, 0, "ワッパダヌキ", "【魔法】【中距離】【防御】\n幼いながらも巧みな変化で\n攻守を必死にこなす豆狸の魔物。\n\n・後ろから３番目攻撃\n・一定時間無敵\n・与えるダメージによるＴＰ回復量80％ダウン", 0, 0, 0, 100, 2, 120, 250600);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (5.0, 16, 250700, 0, "ゴーレム", "【物理】【物理防御】\n頑丈な身体で、攻守を備えた岩の魔物。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 0, 0, 0, 270, 1, 20, 250700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (5.0, 16, 250800, 0, "ブルーゴーレム", "【物理】【魔法防御】\n魔力を秘めた岩から生まれた、\n魔法耐性のあるゴーレム。\n\n・近接攻撃\n・与えるダメージによるＴＰ回復量80％ダウン", 0, 0, 0, 270, 1, 25, 250800);
