INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.6, 11, 302003, 0, "暴風の鷲獅子", "【物理】苛烈な環境で生き抜いた、\n獰猛な鷲獅子。剛翼から放つ強風\nは、冒険者を容易に吹き飛ばす。\n\n・ユニオンバーストは、前から\n　３キャラに魔法中ダメージを\n　与え、さらにノックバック\n　させる。キャラのＨＰが70％\n　未満の場合、スタン状態を\n　付与する。\n　このスキルの状態異常は\n　ダメージを無効化しても\n　付与される。\n\n・前から３キャラに魔法中ダメージ\n　を与え、魔法防御力を小ダウン\n　させる。このスキルの状態異常は\n　ダメージを無効化しても\n　付与される。", 0, 0, 0, 270, 1, 1000, 302003);
