INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.6, 11, 302100, 0, "ワイバーン", "【物理】大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ワイバーンから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を中ダウン\n　させる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした広範囲内の敵最大３キャラに物理大ダ\n　メージを与える。", 0, 0, 0, 270, 1, 3000, 302100);
