INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.6, 16, 302200, 0, "イクト・ガラディオン", "【魔法】ルナの魔力で目覚めた、古代の機械兵器。\n無慈悲に放たれる魔力が、侵入者を阻む。\n\n・敵全体に魔法特大ダメージを与え、魔法防御力\n　を大ダウン。\n・前から３キャラに物理大ダメージを与える。\n・前から２キャラに魔法大ダメージを\n　与え、麻痺状態にする。", 0, 0, 0, 270, 2, 1000, 302200);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.8, 16, 302300, 0, "ワイズオウル", "【魔法】大樹の頂に棲まう、聡明なる大梟。\n森に生ける者の英知を束ね、巧みに魔術を操る。\n\n・ユニオンバーストは敵全体に魔法ダメージを\n　与える。\n・ランダムな1キャラに物理ダメージを与え\n　さらに麻痺状態にする。", 0, 0, 0, 270, 2, 1000, 302300);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.6, 16, 302400, 0, "アークガーディアン", "【物理】太古より遺跡を護り続ける人工生命体。\n創造主の秘術で、無限に動力を得て動き続ける。\n\n・ユニオンバーストは敵全体に魔法大ダメージを\n　与える。\n・前方範囲内に物理ダメージを与える。\n・敵全体に魔法ダメージを与え、さらに沈黙させ\n　る。", 0, 0, 0, 270, 1, 1000, 302400);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.4, 16, 302500, 0, "キマイラ", "【物理】孤塔の頂に座する、暴虐なる混成魔獣。\n千変万化の猛攻は、挑む者数多を屠ってきた。\n\n・ユニオンバーストは前方範囲内に物理大\n　ダメージを与え、さらにノックバックさせる。\n・前方3キャラに物理ダメージを与え、さらに\n　火傷状態にする。\n・敵全体に魔法ダメージを与え、さらに行動速度\n　物理防御力、魔法防御力をダウン。\n・残り時間が10秒を切ると、敵全体に魔法極大\n　ダメージを与え、自身のTPを特大ダウン。", 0, 0, 0, 270, 1, 1000, 302500);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.6, 16, 302600, 0, "ツインピッグス", "【物理】さらなる力を得た双子座の魔星獣。\n前衛で防御しつつ、熟達した技で攻撃を行う。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ツインピッグスから受けるすべてのダメージで\n　発生する、パーティキャラのＴＰ回復量を中ダ\n　ウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与え、物理攻\n　撃力と魔法攻撃力を大ダウンさせる。このスキ\n　ルによるダメージでは、ＴＰが回復しない。こ\n　のスキルの状態異常はダメージを無効化しても\n　付与される。\n・後方小範囲内の敵最大３キャラに物理中ダメー\n　ジを与え、スタン状態を付与する。このスキル\n　の状態異常はダメージを無効化しても付与され\n　る。\n・後方小範囲内の敵最大３キャラに物理中ダメー\n　ジを与え、ＴＰを中ダウンさせる。その後、前\n　にいるツインピッグスは前方へ移動する。この\n　スキルによるダメージでは、ＴＰが回復しない。\n　このスキルはダメージを無効化してもＴＰをダ\n　ウンさせる。", 0, 0, 0, 270, 1, 1000, 302600);
