INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.7, 13, 303600, 0, "バーンサウルス", "【物理】強靭な脚と鋭利なツメで\n獲物を一瞬で狩る、獰猛な恐竜。\n滅びた大地でマグマを喰らい、\n生存の道を見つけた孤高の存在。\n\n・敵全体に物理中ダメージを与え、\n　自分の行動速度を上げる。\n\n・前方3キャラに物理中ダメージ\n　を与え、行動速度を下げる。", 0, 0, 0, 270, 1, 1000, 303600);
