INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.5, 13, 304100, 0, "ティタノタートル", "甲羅に大樹が根ざすほどの、老大な陸ガメ。\n敵の攻撃を耐え続け、憤怒の反撃を見舞う。\n\n・敵全体に物理大ダメージを与える。\n・甲羅にこもっている間に受けたダメージ量に\n　応じた物理ダメージを与える。", 0, 0, 0, 270, 1, 1000, 304100);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (0.1, 13, 304101, 0, "ティタノタートル", "【物理】甲羅に大樹が根ざすほどの老大な陸ガメ。\n敵の攻撃を耐え続け、憤怒の反撃を見舞う。\n\n・物理攻撃力ダウン効果の影響を受けやすい。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・ティタノタートルから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、敵全体に物理大ダメージ\n　を与える。このスキルによるダメージでは、\n　ＴＰが回復しない。\n・甲羅にこもっている間に自身の物理防御力と魔\n　法防御力を特大アップする。その後、敵全体に\n　自身の物理攻撃力に応じた物理ダメージを与え\n　る。このスキルによるダメージでは、ＴＰが回\n　復しない。\n・全ターゲットがブレイクされると、一定時間行\n　動不能になる。行動不能の間、自身の物理防御\n　力と魔法防御力を中ダウンさせ、ブレイクされ\n　なくなる。", 0, 0, 0, 270, 1, 1000, 304101);
