INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.8, 16, 307600, 0, "マッドクラウン", "【物理】ルナの塔を己の舞台にしようと画策する、\n魔界の道化。陽気な笑顔と狂気で観客の命を弄ぶ。\n\n・敵全体に魔法大ダメージを与え、\n　暗闇状態にする。\n・6回、ランダムで1キャラに物理小ダメージを\n　与え、敵全体に物理大ダメージを与える。\n　この攻撃は前列にいるキャラほど\n　当たりやすくなる。\n・時限式の爆弾を前方中距離に召喚する。\n　爆弾は召喚されてから8秒後に大爆発し、\n　範囲内の敵全体に物理中ダメージを与える。\n　ただし、爆発するまでに爆弾のＨＰが\n　1％を切ると、マッドクラウンに\n　最大ＨＰの15％の物理ダメージを与える。", 0, 0, 0, 270, 1, 1000, 307600);
