INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.8, 16, 309002, 0, "レイジーママル", "【物理】広大な密林に棲む巨大な\n獣。身の危険が迫ると防衛反応が\n働き、大いなる野生を解き放つ。\n\n・ユニオンバーストは、敵全体に\n　キャラの最大ＨＰに応じた魔法\n　割合ダメージを与え、さらに物\n　理攻撃力と魔法攻撃力を大ダウ\n　ン。このスキルの状態異常はダ\n　メージを無効化しても付与され\n　る。\n\n・敵全体に魔法小ダメージを与え\n　スタンさせる。このスキルの状\n　態異常はダメージを無効化して\n　も付与される。", 0, 0, 0, 270, 1, 1000, 309002);
