INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (4.0, 16, 311606, 0, "叫怖のゴーストファーザー", "【ＭＯＤＥ３】蓄えた力をすべて吐き出し、真の\n力を解放する。この世界を支配すべく、残虐非道\nの限りを尽くし、地獄へと染め上げる。\n\n・ユニオンバーストは、敵全体にキャラの元々の\n　最大ＨＰに応じた魔法割合ダメージを与える。\n　さらに、敵全体のキャラの最大ＨＰを10％減\n　少させる。減少したＨＰは、バトル終了まで解\n　除できない。\n・自身に物理/魔法無効バリアを展開する。\n・ゴーストファーザー自身のＨＰが40％を\n　切ると、さらに攻撃が激しく凶暴になる。", 0, 0, 0, 270, 2, 3000, 311600);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (999.0, 16, 311700, 0, "ゴーストブラザーズ", "ゴーストファーザーの側近の手下。\n自身の体を盾にして、一切の攻撃から首領を守る。", 0, 0, 0, 270, 2, 3000, 311700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (999.0, 16, 311701, 0, "ゴーストブラザーズ", "ゴーストファーザーの側近の手下。\n鍛え上げた鉄砲玉で、街に特攻をしかける。血気\n盛んに突撃し、逃げ惑う人々を恐怖の底に陥れる。\n\n・ゴーストブラザーズは攻撃の対象にならない。", 0, 0, 0, 270, 2, 3000, 311700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (999.0, 16, 311702, 0, "ゴーストブラザーズ", "ゴーストファーザーの側近の手下。\n鉄砲玉たちと集めた上質な精気を首領へ献上する。\n\n・ゴーストブラザーズは攻撃の対象にならない。\n・ゴーストファーザーのＴＰを回復させる。\n　ゴーストファーザーが行動不能状態のときは、\n　ＴＰを回復しない。", 0, 0, 0, 270, 2, 3000, 311700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (999.0, 16, 311703, 0, "ゴーストブラザーズ", "いよいよ訪れた出番に、鼻息荒く推参する。\n気合十分の最強の盾が、歯向かう相手の心を折る。", 0, 0, 0, 270, 2, 3000, 311700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (999.0, 16, 311800, 0, "ゴーストブラザーズ", "ゴーストファーザーの側近の手下。\n取り立てた精気を首領へ渡し、補佐を務めあげる。\n\n・ゴーストファーザーのＴＰを小回復させる。", 0, 0, 0, 270, 2, 3000, 311800);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (999.0, 16, 311801, 0, "ゴーストブラザーズ", "ゴーストファーザーの側近の手下。\n首領の近辺を警護し、一分の隙も見せない。裏で\nは密かに街の人々を絶望へ追い込もうと暗躍する。\n\n・ゴーストブラザーズは攻撃の対象にならない。", 0, 0, 0, 270, 2, 3000, 311800);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (999.0, 16, 311802, 0, "ゴーストブラザーズ", "ゴーストファーザーの側近の手下。\n部下と取り立てた極上の精気を首領へ差し出す。\n\n・ゴーストブラザーズは攻撃の対象にならない。\n・ゴーストファーザーのＴＰを回復させる。\n　ゴーストファーザーが行動不能状態のときは、\n　ＴＰを回復しない。", 0, 0, 0, 270, 2, 3000, 311800);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (999.0, 16, 311803, 0, "ゴーストブラザーズ", "ファミリーの危機を守るべく、冷静に戦況を見極\nめる。命尽きる直前まで、首領の忠実なる右腕と\nして補佐に徹する。\n\n・ゴーストファーザーのＴＰを回復させる。", 0, 0, 0, 270, 2, 3000, 311800);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.0, 13, 311900, 0, "ブライトホーン", "【物理】霞を食み、自身の姿も霞の\n如く掴みどころがない。人を嫌い、\n近づく者は容赦なく踏み潰す。\n\n・敵全体に魔法大ダメージを与え\n　る。さらに、火傷状態の敵はダ\n　メージがアップする。\n\n・最も物理攻撃力が高い敵を中心に\n　した範囲内に魔法中ダメージを与\n　え、火傷状態にする。", 0, 0, 0, 270, 1, 1000, 311900);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.0, 16, 312000, 0, "オブシダンワイバーン", "【物理】魔力を宿せし黒曜の結晶竜。美しい体躯\nの輝きは冥府を彷徨いし魂の嘆きといわれている。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・オブシダンワイバーンから受けるすべてのダ\n　メージで発生する、パーティキャラのＴＰ回\n　復量を中ダウンさせる。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、さらにＴ\n　Ｐを大ダウンさせる。このスキルはダメージを\n　無効化してもＴＰをダウンさせる。\n・敵全体のＨＰ吸収を特大ダウンし、さらに物理\n　防御力を大ダウンさせる。\n・物理攻撃力、魔法攻撃力いずれかで、最も攻撃\n　力が高い１キャラに物理大ダメージを与える。\n　さらにそのキャラを中心にした小範囲に物理中\n　ダメージを与え、ＴＰを中ダウンさせる。この\n　スキルはダメージを無効化してもＴＰをダウン\n　させる。", 0, 0, 0, 270, 1, 3000, 312000);
