INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312604, 0, "ジェネラルホーン", "【ＭＯＤＥ１】首領の雄たけびで、手先の牡鹿が\n群れを成し押し寄せる。捨て身の突進を食い止め\nられるか？\n\n・バトル開始時、ワイルドホーンを３体召喚し、\n　さらにノックバックさせる。\n・ワイルドホーンを１体倒すたびに、残りワイル\n　ドホーン数が１減少する。\n　その後、ワイルドホーンが再度召喚され、ノッ\n　クバックさせる。\n・通常攻撃は、前方１キャラを中心とした範囲内\n　に物理小ダメージを与える。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。\n　さらに、自身の物理・魔法防御力を中ダウンさ\n　せる。", 0, 0, 0, 270, 1, 3000, 312600);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312605, 0, "ジェネラルホーン", "【ＭＯＤＥ２】朽木の魔物を従え、冒険者を追い\n詰める。共生の朽木を失うとその身もともに破滅\nへと向かう。朽木の撃滅が攻略のカギとなる。\n\n・バトル開始時、トレントを２体召喚し、さらに\n　ノックバックさせる。\n・前方のトレントのＨＰが０になった時、ジェネ\n　ラルホーンの物理・魔法防御力を小ダウンさせ\n　る。\n・後方のトレントのＨＰが０になった時、ジェネ\n　ラルホーンの物理・魔法防御力を極大ダウンさ\n　せる。\n・通常攻撃は、前方１キャラを中心とした範囲内\n　に物理小ダメージを与える。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。\n　さらに、自身の物理・魔法防御力を中ダウンさ\n　せる。\n・前方範囲内に物理中ダメージを与え、麻痺さ\n　せる。さらに、自身の物理・魔法防御力を大ダ\n　ウンさせる。", 0, 0, 0, 270, 1, 3000, 312600);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312606, 0, "ジェネラルホーン", "【ＭＯＤＥ３】手負いの角獣が死力を振り絞る。\n命を賭した突進は体躯を紫電に変え、光の如き\n一閃は歴戦の冒険者一行を瓦解させる。\n\n・通常攻撃は、前方１キャラを中心とした範囲内\n　に物理小ダメージを与える。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与える。\n　さらに、自身の物理・魔法防御力を中ダウンさ\n　せる。\n・前方範囲内に物理中ダメージを与え、麻痺さ\n　せる。さらに、自身の物理・魔法防御力を大ダ\n　ウンさせる。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 0, 0, 0, 270, 1, 3000, 312600);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312700, 0, "黒風の精霊", "", 0, 0, 0, 270, 2, 3000, 312700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312701, 0, "黒風の精霊", "【魔法】使役者の真の力を試す黒き風の精霊。\nそこには悪意はなく、純粋に己の内に秘めた想い\nと絆が試される。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n　黒風の精霊がユニオンバーストを使用してから\n　一定時間が経過するまでは再度ユニオン\n　バーストを使用しない。", 0, 0, 0, 270, 2, 3000, 312700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312702, 0, "黒風の精霊", "【魔法】使役者の真の力を試す黒き風の精霊。\nそこには悪意はなく、純粋に己の内に秘めた想い\nと絆が試される。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n　黒風の精霊がユニオンバーストを使用してから\n　一定時間が経過するまでは再度ユニオン\n　バーストを使用しない。\n・敵全体を猛毒状態にし、さらに物理・魔法攻撃\n　力を大ダウンさせる。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 0, 0, 0, 270, 2, 3000, 312700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312703, 0, "黒風の精霊", "【魔法】使役者の真の力を試す黒き風の精霊。\nそこには悪意はなく、純粋に己の内に秘めた想い\nと絆が試される。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n　黒風の精霊がユニオンバーストを使用してから\n　一定時間が経過するまでは再度ユニオン\n　バーストを使用しない。\n・敵全体を猛毒状態にし、さらに物理・魔法攻撃\n　力を大ダウンさせる。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 0, 0, 0, 270, 2, 3000, 312700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312704, 0, "黒風の精霊", "【ＭＯＤＥ１】第一の試練。黒き風の精霊の暴風\nの継ぎ目を見切れば、自ずと己の進むべき道が\n切り拓かれん。\n\n・敵全体に物理ダメージとスタンを与え、ノック\n　バックさせる。\n　黒風の精霊が眠っている間、自身の物理防御力\n　が大幅にダウンし、その間に100000以上\n　のダメージを与えると、防御力ダウンが延長さ\n　れ行動速度もダウンする。", 0, 0, 0, 270, 2, 3000, 312700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312705, 0, "黒風の精霊", "【ＭＯＤＥ２】第二の試練。己の弱き心と対峙\nせよ。いつも答えは己の内にある。今一度、仲間\nの力を信じ立ち向かえ。\n\n・前から２キャラに物理小ダメージを与え、さら\n　にノックバックさせ、スタンを与える。", 0, 0, 0, 270, 2, 3000, 312705);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (2.7, 16, 312706, 0, "黒風の精霊", "【ＭＯＤＥ３】第三の試練。終の試練は、己の\nすべてを懸け黒き風の精霊に本当の想いと絆を\n顕現し、己の想いを確かめよ。\n\n・ユニオンバーストは、敵全体に黒風の精霊\n　の物理攻撃力とキャラの最大ＨＰに応じた\n　魔法割合ダメージを与える。さらに、\n　残りＨＰが最も低い１キャラにカウントダウン\n　を付与し、５秒後に最大ＨＰの80%の魔法\n　割合ダメージを与える。\n・敵全体を猛毒状態にし、さらに物理・魔法攻撃\n　力を大ダウンさせる。\n・残り時間が25秒を切ると、さらに攻撃が激し\n　く凶暴になる。", 0, 0, 0, 270, 2, 3000, 312700);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.8, 16, 312800, 0, "魔物の姫", "", 0, 0, 0, 270, 1, 3000, 312800);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.8, 16, 312801, 0, "魔物の姫", "【物理】辺境の城に座する、孤高な魔物の姫君。\n悲哀なる恋慕を胸に、自らに挑む者を打ち砕く。\n\n・ユニオンバーストは、前から２キャラに\n　最大ＨＰに応じた物理割合ダメージを与える。\n　力を溜めることに成功した場合、ダメージが\n　アップし、敵全体を畏怖状態にする。\n　畏怖状態のキャラは、一定時間攻撃およびＨＰ\n　回復のユニオンバーストとスキル、強化通常攻\n　撃の効果がダウン。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・物理防御力と魔法防御力が大幅にダウンした後、\n　一定時間、力を溜める。\n　力が溜まると、ＴＰが特大回復する。\n　力を溜めている間に魔物の姫が7000以上の\n　ダメージを受けると、力を溜めることに失敗し、\n　ＴＰが大ダウン。 \n・目の前の敵１キャラに物理ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。\n・前から３キャラに最大ＨＰに応じた物理割合\n　ダメージを与える。\n　この攻撃は物理攻撃力アップ・ダウン効果の\n　影響を受けやすい。", 0, 0, 0, 270, 1, 3000, 312800);
