INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.0, 16, 314001, 0, "得意な宿題", "まずは手始めに得意な宿題を終わらせよう。\nここでつまづいてはいられない。夏休みの宿題は\nほかにもまだまだあるぞ！！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 0, 0, 0, 270, 1, 3000, 314001);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.0, 16, 314002, 0, "ちょっと苦手な宿題", "誰にでもある苦手科目の宿題。ついつい後回しに\nしがちで、溜まってしまうとなかなか大変。\nここで立ち止まるわけにはいかないぞ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 0, 0, 0, 270, 1, 3000, 314002);
INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.0, 16, 314003, 0, "すごく苦手な宿題", "眺めているだけで眩暈のするいや～な宿題。\nペンを置いて外に飛び出したくなる衝動を耐え、\nコツコツ進めていくことが攻略のカギ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 0, 0, 0, 270, 1, 3000, 314003);
