INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.6, 16, 316201, 0, "アストライアタケモドキ", "【物理】偽キノコで獲物を誘う、恐ろしき化け茸。\nキノコを口にした者は幻覚に囚われ、何も知らぬ\n間に大自然の餌食となってしまう。\n\n・ユニオンバーストは、敵全体に自身の物理攻撃\n　力とキャラの最大ＨＰに応じた物理割合ダメー\n　ジを与え、ＨＰが40％未満のキャラを誘惑状\n　態にする。このスキルの状態異常はダメージを\n　無効化しても付与される。\n・自身を中心とした範囲内の敵を誘惑状態にし、\n　物理攻撃力と魔法攻撃力をバトル終了まで\n　小アップさせる。\n・前から１番目のキャラに物理大ダメージ。", 0, 0, 0, 270, 1, 3000, 316200);
