INSERT INTO `v1_4b987dfc76b42db243c1770414859b4b2e78208e6b58fdc9b4514be8a071f087` VALUES (1.4, 16, 318501, 0, "楽園の守護者", "【魔法】思い出の中へ囚われゆくランドソルに\n突如として現れた異質な魔物。楽園を護らんと\n冷たく硬い両槍で解放者たちを拒絶する。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　を与え、さらに行動速度を中ダウン、魔法防御\n　力を小ダウンさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・自身の魔法攻撃力を小アップ、行動速度を大\n　アップさせる。\n・前から１番目のキャラに魔法中ダメージ。\n・向かって左側のターゲットのブレイク時、\n　物理/魔法防御力が中ダウン。\n・中央下部のターゲットのブレイク時、\n　物理/魔法防御力が大ダウン。\n・中央上部のターゲットのブレイク時、\n　物理/魔法防御力が大ダウン。\n・向かって右側のターゲットのブレイク時、\n　物理/魔法防御力が特大ダウン。", 0, 0, 0, 270, 2, 3000, 318500);
